css:
@-moz-document domain("transifex.com") {
#appspace, .u-backgroundColor-white, #filter-area, .check-mass span, .o-select__container, .o-textinput, .c-select-dropdown__controller {
	background-color: #3a3a3a !important;
}
.o-icon {
	fill: #fff;
}
.suggest-steady, #suggestions-area .o-tabs, .u-borderTop-divider, .u-borderRight-divider, .u-borderBottom-divider, #string-list > div, .u-borderHorizontal-divider, .u-borderVertical-divider, .u-border-divider, .o-select__container, .u-borderLeft-divider, .o-textinput, .c-select-dropdown__controller {
	border-color: #222 !important;
}
#source-string-area, #string-list > div .check-mass.checked, #string-list > div.shovered .check-mass.checked, #string-list > div.string-selected, body.editor, .u-backgroundColor-muted, .u-backgroundColor-main, .u-backgroundColor-stateFirst {
	background-color: #4a4a4a !important;
}
.c-infoarea {
	background-color: #444;
}
.o-link--brand, #filter-menu li.is-active > span, .o-tabs__item.is-active, .o-button--toggle, .o-tabs__item:hover, .o-link--brand-muted:hover, .o-link:hover, .o-link--brand-muted.is-active:hover, .o-button:hover {
	color: #73ba25;
}
.u-fill-brandPrimary {
	fill: #73ba25 !important;
}
.o-tabs__item::before, #string-list > div::before, .o-pill--notification, .o-button--fill:hover, .o-interactive-list--highlight .o-interactive-list__item::before {
	background-color: #73ba25;
}
.o-button, .check-mass span, .o-button, .o-button--fill:hover, .o-button:hover {
	border-color: #73ba25;
}
#filter-menu li:hover > span, .o-link--muted:hover, .o-link, .o-link--brand-muted.is-active {
	color: #6da741;
}
.o-button--fill {
	background-color: #6da741;
}
#string-list > div.string-selected, #source-string, #translated-string, #select-resource-arrow, .c-select-dropdown__controller, .c-infoarea__action.o-button.o-button--fill.u-float-right.u-marginLeft-2x, .o-button {
	color: #fff !important;
}
.o-pill--notification {
	color: #000;
}
img.o-avatar__object {
	display: none;
}
span.o-avatar {
	background-image: url(https://ipfs.io/ipfs/QmUfC2xXhWqu9zig8w1hJpGL5sQ2ioXkdH1nYgKho85yDt/login-img-brand.png);
	background-size: cover;
	border-color: transparent;
}
.o-button--fill.is-disabled, .o-button--fill.is-disabled:hover, .o-badge {
	background-color: #636363 !important;
	border-color: #636363 !important;
}
body, .o-select__option.is-active, .u-color-secondary, .u-color-secondary, .u-color-primary {
	color: #aaa !important;
}
#filter-menu li, .u-color-important, .o-interactive-focus-text:hover .o-interactive-focus-text__item--primary, .o-textinput {
	color: #ccc !important;
}
.check-mass:not(.check-no-bg):hover {
	background-color: #4d4d4d;
}
#select-resource-arrow {
	background-image: url(https://ipfs.io/ipfs/QmPGkRVXWTakwvH6pT4Pt4cnCGDhEMJuK3d2rLHRfvs8ak/select-res-arrow.png);
}
#resources-area {
	background-color: rgba(58, 58, 58, .8);;
}
.c-sidebar {
	background-color: #173f4f;
}
.c-sidebar__section--subsection {
	background-color: hsl(197, 55%, 17%);
}
.o-button:hover {
	background-color: rgba(23, 63, 79, 0.2);
}
}
