css:
    .theme-light header {
         border-color: #324977;
         background: linear-gradient(0.25turn, #112244, #86457f);
         color: #ffffff;
    }
     .theme-light header .profile .middle .username {
         color: #ffffff;
    }
     .theme-light header .profile .middle .address {
         color: #ffffff;
    }
    .theme-light header .right {
         color: #ffffff;
    }
    .theme-light header .dropdown div {
         background: #29447a;
         border: 1px solid #7D75E4;
    }
     .page-content {
         background: linear-gradient(0.25turn, #112244, #86457f);
    }
     .theme-light #topic .sidebar > .invite .details input {
         background: #223355;
         border: 1px solid #223355;
    }
     .theme-light #topic .sidebar .box, .theme-light #topic .feed .feed-header .left, .theme-light #topic .feed .feed-content .post {
         background: #29447a;
         border: 3px solid #7D75E4;
         color: #ffffff;
    }
     .theme-light .stats-bar .votes .value, .theme-light .stats-bar .rewards .value, .theme-light .stats-bar .comments .value {
         color: #ffffff;
    }
     .theme-light .ReactModal__Content {
         background: #143255;
         color: #ffffff;
    }
     .theme-light #faq-modal .section .question {
         color: #ffffff;
    }
     .theme-light #faq-modal .section .answer, .theme-light #post-creator-modal .input-file, .theme-light #welcome-modal .section>.left .step .sub-header, .theme-light #topic .sidebar>.global-stats .details .column .title, .theme-light #topic .feed .feed-content .post .right>.info .details, .theme-light .stats-bar .rewards .symbol, .theme-light #topic .sidebar>.invite .details .subtitle, .theme-light #topic .sidebar>.invite .details .description, .theme-light footer .notification, .theme-light footer .theme-switch, .theme-light footer .links .link, .theme-light footer .copyright, .theme-light .modal-slim .modal-subheader {
         color: #aaaaaa;
    }
     .theme-light .vote-icon.up.clicked {
         color: #4e8afb;
         background: #2c4473;
    }
     .theme-light .vote-icon.down.clicked {
         color: #ff8579;
         background: #c03c30;
    }
     .theme-light .vote-icon.up:hover, .theme-light .vote-icon.up.clicked:hover {
         color: #92b8ff;
         background: #4e6ead;
    }
     .theme-light .vote-icon.down:hover, .theme-light .vote-icon.down.clicked:hover {
         color: #ff988f;
         background: #fd5c4e;
    }
     .theme-light .vote-icon.disabled, .theme-light .vote-icon.disabled:hover {
         color: #5e5e5e;
         background: transparent;
    }
    /* Fixes */
     .modal-slim .modal-subheader {
         padding-right: 25px;
    }

    .theme-light #post-modal .link {
        color: #e5e5e5;
    }

    .modal-description .modal-content {
        background: #46408b;
        border: 3px solid #7D75E4;
    }

    nav .dropdown div {
        background: #46408b;
    }

    /* i really want this to be global */
