css:
@-moz-document domain(nonolive.com)
{
/*------------------------------------------------------------------------------------全局-*/
html,
body {
    color:#fff;
    background: #000;
}
.header-content-icon {
    /*logo*/
    -webkit-filter: grayscale(1) brightness(2);
    opacity: .6;
}
.header-content-panel {
    /*header 背景*/
    background: #24252c;
}
.common-background-color {
    /*全局 背景*/
    background-color: #000;
}
.footer {
    /*footer*/
    background: #111115;
    opacity: 0.4;
    transition: all .6s ease;
}
.footer:hover {
    /*footer hover*/
    opacity: 1;
    transition: all .3s ease;
}
/*---------------------------------------------------------------------------------header-*/
.header-content-panel,
.header-content-classify-content,
.header-content-connector-content,
.header-content-download-text{
    /*header 文字*/
    color: #888888;
}
.header-content-classify-btn {
    /*header 分類 展開 文字*/
    color: #94969e;
    border: solid 1px #5f616d;
}

.header-content-classify-panel {
    /*header 分類 展開 背景*/
    background-color: #24252c;
}
.header-content-connector-panel-txt {
    /*header 連接 展開 文字*/
    color: #94969e;
}
.header-content-connector-panel {
    /*header 連接 展開 背景*/
    background-color: #24252c;
}
.header-content-connector-panel-txt {
    /*header 連接 展開 列表 文字*/
    color: #94969e;
}
.header-content-connector-panel-txt:hover {
    /*header 連接 展開 列表 文字 hover*/
    color: #e33d3d;
    background: #151617;
}
.header-content-download-panel-item {
    /*header 下載 展開 文字*/
    color: #94969e;
}
.header-content-download-panel {
    /*header 下載 展開 背景*/
    background-color: #24252c;
}
.header-content-download-panel-item {
    /*header 下載 展開 列表 文字*/
    color: #94969e;
}
.header-content-download-panel-item:hover {
    /*header 下載 展開 列表 文字 hover*/
    color: #e33d3d;
    background: #151617;
}
.header-content-search-input {
    /*header 搜尋 欄位*/
    color:#fff;
    border: 0px;
    background: #151617;
}
.header-content-search-input::placeholder {
    /*header 搜尋 欄位 預設文字*/
    color:#555;
}
.search-wrap {
    /*header 搜尋 展開*/
    background: #24252c;
}
.search-wrap .search-popular .main .popular-item:hover {
    /*header 搜尋 展開 列表 hover*/
    color: #e33d3d;
    background: #151617;
}
.search-wrap .search-popular .main .popular-item {
    /*header 搜尋 展開 列表*/
    color: #94969e;
}
.header-content-follow-panel {
    /*header 關注 展開 列表*/
    background: #24252c;
    border-top: 2px solid #e33d3d;
}
.header-avatar-content-panel {
    /*header 大頭貼 展開 列表*/
    background: #24252c;
    border-top: 2px solid #e33d3d;
}
.header-avatar-content-panel-username {
    /*header 大頭貼 展開 列表 用戶名稱 文字*/
    color:#fff;
}
/*------------------------------------------------------------------------------------登入-*/
.login-box {
    /*登入 展開 背景*/
    background: #24252c;
}
.login-box .title {
    /*掃描二維碼 標題 文字*/
    color: #e33d3d;
}
.phone-bind-box .header .title {
    /*登入 展開 綁定電話 標題*/
    color:#e33d3d;
}
.region-wrapper .current {
    /*登入 展開 國家/地區 輸入欄位*/
    color:#fff;
    border:0px;
    background: #151617;
}
.region-wrapper .region-list {
    /*登入 展開 國家/地區 輸入欄位 下拉*/
    border:0px;
    background: #151617;
}
.phone-bind-box .phone-bind-wrapper .row .phone-wrap {
    /*登入 展開 電話號碼 輸入欄位*/
    border:0px;
    background: #151617;
}
.phone-bind-box .phone-bind-wrapper .row .phone-wrap .region-code,
.phone-bind-box .phone-bind-wrapper .row .phone-wrap input.phone{
    /*登入 展開 電話號碼 輸入欄位 展開*/
    color:#fff;
    border:0px;
    background: #151617;
}
.phone-bind-box .phone-bind-wrapper .row .verification-wrap input.code {
    /*登入 展開 驗證碼 輸入欄位*/
    color:#fff;
    border:0px;
    background: #151617;
}
.phone-bind-box .phone-bind-wrapper .row .verification-wrap .send-wrap.disabled {
    /*登入 展開 驗證碼 送出 按鈕*/
    border:0px ;
}
.phone-bind-box {
    /*登入 展開 綁定電話 背景*/
    background: #24252c;
}
/*------------------------------------------------------------------------------------首頁-*/
.channel-hot__top-channel {
    /*首頁熱推 背景*/
    background-image:url(https://);
    background-color:rgba(0,0,0,0);
}
.top-channel__current-border::after {
    /*首頁熱推 列表 選取框*/
    box-shadow: 0 0 0 3px #f62333 inset;
}
.top-channel__current-border::before {
    /*首頁熱推 列表 小箭頭*/
    border-right: 7px solid #f62333;
}
.channel-hot-live-list-title-text {
    /*首頁 分類標題*/
    color: #fff;
}
/*------------------------------------------------------------------------------------分類-*/
.specific-channel-list-content-title > p {
    /*分類 標題*/
    color: #ffffff;
}
/*--------------------------------------------------------------------------------各直播間-*/
.room {
    /*直播間上下間距調整*/
    padding: 20px 100px;
}
.room-anchor-panel {
    /*主播標題 背景*/
    background-color: #24252d;
}
.room-anchor-panel__anchor-intro {
    /*主播標題 標題文字*/
    color: #fff;
}
/*---------------------------------------------------------------------------------聊天室-*/
.chat-panel {
    /*聊天室*/
    color: #fff;
    background-color: #24252d;
}
.chat-panel .chat-item {
    /*聊天室 訊息間距*/
    padding: 4px 10px;
}
.chat-panel-input-wrap .chat-panel-input {
    /*聊天室 輸入欄位*/
    line-height: 22px;
    color:#fff;
    border: 0px;
    border-radius:5px;
    background: #151617;
}
.chat-panel-input-wrap .chat-panel-input::placeholder {
    /*聊天室 輸入欄位 預設文字*/
    line-height: 22px;
    color:#555;
}
.chat-panel .chat-item .sender {
    /*聊天室 傳送訊息者名稱 文字*/
    color: #fff;
}
.chat-panel .chat-item .text {
    /*聊天室 文字訊息 文字*/
    color: #888;
}
}
