css:
@-moz-document domain("u-cursos.cl") {
:root {
    --color-primario: #9e2626;
    --color-fondo-1: #2b2b2b;
    --color-fondo-2: #1f1f1f;
    
    --color-resaltado: #262626;
    --color-bordes: #111;
    
    --color-hover-1: #3b3b3b;
    --color-hover-2: #4b4b4b;
    
    --color-links: #56938f;
    --color-links-hover: #fff;
    
    --color-texto: #c3c3c3;
    --color-texto-2: #848484;
}



/* ------------------------------ Header ------------------------------ */
/* logo */
h1#header {
    background: var(--color-fondo-2) url(https://imgur.com/l6DpNuV.png); /* logo azul */
    /* background: var(--color-fondo-2) url(https://imgur.com/Pxi8WmJ.png); /* logo gris */
    
    background-repeat: no-repeat;
    background-position: center;
    background-size: auto 40px;
    
    border-bottom: 4px solid var(--color-primario);
    
    position: fixed;
}
/* header */
h2#titulo {
    color: var(--color-texto-2);
    border-bottom: 4px solid var(--color-primario);
    margin-left: 217px;
}
/* imagen perfil */
#widget_perfil a.perfil img {
    border: 1px solid var(--color-fondo-2);
}
/* fondo opciones perfil */
#contenedor_opciones {
    background-color: var(--color-fondo-1);
}


/* ------------------------------ Barra Lateral ------------------------------ */
/* fondo barra lateral */
#modulos {
    background-color: var(--color-fondo-2);
    
    position: fixed;
    top: 54px;
    bottom: 0;

    overflow-y: auto;
    overflow-x: hidden;    
    margin-bottom: -1.8em;
}
/* titulos secciones barra lateral */
#modulos>div>h1 {
    background-color: var(--color-primario);
    border-radius: 3px 3px 3px 3px;
    color: var(--color-texto);
    padding: .5em .8em .5em 2em;
}
/* texto barra lateral */
#modulos>div ul li a, #modulos>div ul li a h1, #modulos div ul li.sel a span, #modulos div ul li.sel a small {
    color: var(--color-texto-2);
}
#modulos .nuevos li a {
    color: var(--color-texto);
}
#modulos .nuevos li a:hover {
    color: var(--color-links-hover);
}
/* seleccionado barra lateral */
#modulos div ul li.sel {
    background-color: var(--color-hover-1);
}
#modulos div ul li.sel a span {
    color: var(--color-texto);
}
/* hover barra lateral */
#modulos div ul li:hover, #modulos div ul li a:hover {
    background-color: var(--color-hover-2);
}
/* iconos cargos */
div.sprite_cargos {
    background-image: url(https://imgur.com/r80PnAB.png);
    width: 19px;
    height: 19px;
}



/* ------------------------------ Links ------------------------------ */
/* links */
a {
    color: var(--color-links);
}
a:hover {
    color: var(--color-links-hover);
}



/* ------------------------------ Modulos ------------------------------ */
/* modulos */
ul.modulos {
    padding: 10px 0;
}
ul.modulos li span {
    color: var(--color-texto-2);
}
/* modulo seleccionado */
ul.modulos li.sel span {
    color: var(--color-primario);
}
ul.modulos li.sel img {
    border: 2px solid var(--color-primario);
    border-radius: 3px;
}
ul.modulo li.sel a, ul.modulo li.sel {
    background: var(--color-primario);
    border-radius: 0px 0px 3px 3px;
}
ul.modulo li.sel a {
    border-top: 0 solid var(--color-primario);
}
/* hover modulo */
ul.modulos li a:hover span {
    color: var(--color-primario);
}
/* titulo modulo */
h1.modulo {
    color: var(--color-primario) !important;
    border-bottom: solid 1px var(--color-primario);
}


/* ------------------------------ Tablas (contenido default) ------------------------------ */
/* fondo tablas y default */
body, table {
    background: var(--color-fondo-1);
    color: var(--color-texto);
}
/* secciones tablas */
tr.separador {
    background: var(--color-primario);
    color: var(--color-texto);
}
/* resaltado tablas */
table td.opciones, table th {
    background: var(--color-resaltado);
    color: var(--color-texto-2);
}
/* borde inferior tabla */
table td, tbody th {
    border-bottom: 1px solid var(--color-fondo-2);
}
/* entrada nueva */
table tr.nuevo {
    background: var(--color-hover-1);
}
/* hover tabla notas */
#body table#notas tr:hover {
    background: var(--color-hover-1);
}
/* orden destacado */
table.sortable th.desc {
    background: var(--color-fondo-2) url(https://www.u-cursos.cl/d/images/desc.gif);
    background-position: 4px 4px;
    background-repeat: no-repeat;
}
table.sortable th.asc {
    background: var(--color-fondo-2) url(https://www.u-cursos.cl/d/images/asc.gif);
    background-position: 4px 4px;
    background-repeat: no-repeat;
}
/* footers */
tfoot td, tr.foot td, tfoot td h1, tr.foot td h1, td.foot {
    background-color: var(--color-resaltado);
}


/* ------------------------------ Imagenes ------------------------------ */
/* borde imagenes */
img.avatar, img.foto {
    background-color: var(--color-bordes);
    border: 1px solid var(--color-fondo-2);
    border-radius: 3px;
}
/* borde gráficos caracterización */
body div.c {
    border: 1px solid var(--color-fondo-2) !important;
    border-radius: 3px;
}
/* albumes galería */
#body div.album {
    background-color: var(--color-resaltado);
    border: 1px solid var(--color-fondo-2);
}
/* fotos galería */
#body div.foto {
    border: 1px solid var(--color-fondo-2);
}



/* ------------------------------ Foro ------------------------------ */
/* mensajes foro */
#body div.msg .texto {
    background-color: var(--color-fondo-2);
    border: 1px solid var(--color-bordes);
    border-radius: 3px;
}
/* hover autor mensaje foro */
#body span.autor a:hover {
    color: var(--color-links-hover);
}
/* fondo nuevo mensaje */
#body div.msg .texto, #body div.msg .new_texto {
    background-color: var(--color-fondo-2);
    border: 1px solid var(--color-bordes);
}
/* textarea foro */
.textarea ul.modos {
    background-color: var(--color-fondo-2);
    color: var(--color-texto);
}
.textarea ul.modos li {
    border-color: var(--color-bordes);
}
/* preview */
.textarea div.preview {
    border: 1px dashed var(--color-bordes);
}
/* mensaje nuevo resaltado */
#body div.nuevo .texto, #body div.responder .texto {
    font-weight: normal;
    border-color: var(--color-fondo-2);
    background-color: var(--color-hover-1);
}
/* código fuente */
a.audio, pre.source, pre.latex, pre.graphviz {
    background-color: var(--color-hover-1);
    color: var(--color-texto);
    padding: 5px;
    margin: 5px;
}
/* latex */
img.latex, img.sola {
    background-color: #fff;
}
/* página seleccionada foro */
ul.paginas li.sel a {
    border: 1px solid var(--color-primario);
    color: var(--color-links);
}
ul.paginas li.sel a:hover {
    background-color: var(--color-hover-2);
    color: var(--color-links-hover);
}



/* ------------------------------ Otros ------------------------------ */
/* texto titulos modulos */
h1, h2 {
    color: var(--color-texto);
}
/* texto novedades */
div.revista p, div.revista ul li, div.revista dl, div.revista table td {
    color: var(--color-texto);
}
/* titulos novedades */
#body.novedades .objeto h1 a, #body.novedades_institucion .objeto h1 a, #body.blog .objeto h1 a, #body.mi_blog .objeto h1 a {
    color: var(--color-primario);
}
/* comentarios en blog/novedades */
ul#comentarios li {
    border-top: 1px solid var(--color-fondo-2);
}
ul#comentarios li:nth-child(even) {
    background: var(--color-resaltado);
}
/* warning */
.warn {
    background-color: var(--color-resaltado);
    border-color: var(--color-bordes);
    color: var(--color-texto) !important;
}


/* ------------------------------ Horario ------------------------------ */
/* borde botones horario */
ul.paginas li a {
    border: 1px solid var(--color-bordes);
    border-radius: 3px;
}
/* hover botones horario */
ul.paginas li a:hover {
    background-color: var(--color-hover-2);
    color: var(--color-links-hover);
}
/* separador horizontal horario  */
#body .horario .hora {
    border-top: 1px solid var(--color-fondo-2);
}
/* borde y texto bloques horario */
#body .horario .bloque {
    border: 1px solid var(--color-bordes);
    color: var(--color-bordes);
    border-radius: 3px;
}
/* filas coordinador */
.dhorario .hora {
    border-top: 1px solid var(--color-fondo-2);
    background: var(--color-fondo-1);
    color: var(--color-texto-2);
}
/* bloques coordinador */
.dhorario .bloque {
    border: 1px solid var(--color-bordes);
    color: var(--color-bordes);
}



/* ------------------------------ Calendario ------------------------------ */
/* cosas resaltadas calendario */
td.gris, tr.gris, col.gris {
    background-color: var(--color-resaltado);
}
/* bordes header calendario */
.calendario tr {
    border-bottom: 1px solid var(--color-fondo-2);
    border-right: 1px solid var(--color-fondo-2);
}
/* número día calendario */
#body #calendario .dia, #body #calendario .opciones .dia, #body #calendario .gris .dia {
    background: var(--color-fondo-2);
}
/* eventos calendario */
#body #calendario li {
    border: 1px solid var(--color-bordes) !important;
    border-top-width: 5px !important;
    background-color: var(--color-fondo-2);
}



/* ------------------------------ Forms ------------------------------ */
/* borde tabla form */
table.form tbody th {
    border-bottom-color: var(--color-bordes);
}
/* inputs */
.boton, input, input[type="submit"] {
    background: var(--color-fondo-1);
    border: 1px solid var(--color-bordes) !important;
    border-radius: 3px;
    color: var(--color-texto);
}
/* barra login inicio */
#upasaporte {
    border-bottom: 1px solid var(--color-fondo-2);
    background-color: var(--color-resaltado);
}
/* select */
.chosen-container-single .chosen-single {
    background-color: var(--color-fondo-1);
    color: var(--color-texto);
    border: 1px solid var(--color-bordes) !important;
    border-radius: 3px;
}
.chosen-container-single .chosen-search, .chosen-container .chosen-results {
    background-color: var(--color-fondo-1);
    color: var(--color-texto);
}
.chosen-container.chosen-with-drop .chosen-drop {
    background: var(--color-fondo-1);
    border: 1px solid var(--color-bordes);
    border-radius: 3px;
}
/* textarea */
textarea {
    background-color: var(--color-resaltado);
    color: var(--color-texto);
    border: 1px solid var(--color-bordes);
    border-radius: 3px;
}

/* select fecha */
span.calendario th {
    background: var(--color-fondo-1);
}
/* destacar fin de semana */
span.calendario table th.finde {
    background-color: var(--color-resaltado) !important;
}
.calendario .finde {
    background-color: var(--color-resaltado) !important;
}
/* remarcar parte superior */
span.calendario th {
    border-top: 1px solid var(--color-primario);
}
/* separador horizontal */
.calendario tr {
    border-bottom: 1px solid var(--color-bordes);
    border-right: 1px solid var(--color-bordes);
    border-left: 1px solid var(--color-bordes);
}
/* separador vertical */
.calendario td {
    border-right: 1px solid var(--color-bordes) !important;
}
/* destacar dia actual */
.calendario .hoy {
    background-color: var(--color-primario);
    border-right: 1px solid var(--color-bordes);
    border: 0px;
}
/* hover */
.calendario tr:hover, .calendario tr:hover .finde {
    background: var(--color-hover-1) !important;
}
.calendario td:hover, .calendario td:hover .finde {
    background: var(--color-hover-2) !important;
}

/* otros botones (subsección edición nota) */
ul.submodulo {
    border-bottom: 1px solid var(--color-bordes);
}
ul.submodulo li.sel {
    background-color: var(--color-fondo-1);
    border: 1px solid var(--color-bordes);
    border-radius: 3px;
    border-bottom: 1px solid var(--color-fondo-1);
}
ul.submodulo li {
    background-color: var(--color-fondo-2);
    border: 1px solid var(--color-bordes);
    border-radius: 3px;
    border-bottom: 1px solid var(--color-fondo-1);
}
ul.submodulo li a {
    color: var(--color-texto);
}
ul.submodulo li:hover a {
    color: var(--color-primario);
}

/* importar notas */
.importar {
    border: 1px dashed var(--color-bordes);
    background-color: var(--color-fondo-2);
}
/* input notas */
.rojo {
    color: #000;
}
.reprueba, .reprueba input {
    color: #e10c12;
}

/* checkbox */
.status_on, .status_off {
    background: transparent;
    border: 1px solid var(--color-bordes);
    width: 12px;
    height: 12px;
}
.status_on {
    background: url(https://imgur.com/Z9UOp1P.png);
    background-size: 12px 12px;
}
}
