css:
@-moz-document url-prefix("https://mail.google.com/mail/") {
/* turns off google hangouts in collapsed sidebar mode */
.bhZ .Jc {
    display: none;
}

/* more spacing to reduce noise density for easier readability */
.zA {
    padding-top: 20px;
    padding-bottom: 20px;
    background-color: #fcfcfc;
}

.zA:nth-child(odd) {
    background-color: #fff;
}

/* better email row hover shadow, and soften the hover sidebar shadow */
.zA:hover, .bhZ.bym {
    box-shadow: 0 0 25px rgba(40, 47, 60, 0.05), 
                                            0 20px 25px rgba(40, 47, 60, 0.05), 
                                            0 3px 4px rgba(40, 47, 60, 0.05);
}

/* email titles that stand out */
.y6 {
    font-weight: 500;
}

/* hidden the crappy border around gsuite logo */
.gb_Ra {
	border: 1px solid #fff;
}
}
