css:
@-moz-document domain("rbxtrading.com")
{
html,
  body {
    border: 5px solid red;
    background: #5c4726;
    /* set bg image */
    background-image: url(https://thumbs.gfycat.com/WastefulSparklingGrayling-size_restricted.gif);
    background-attachment: scroll;
    /* just place the bg image, no repeat, it takes up the space it can */
    background-size: contain; 
    /* where are we putting the bg image */
    background-position: center;
    /* main text color */
    font-family: "Comic Sans MS";
    color: #0dff00;
    text-transform: uppercase;
    word-spacing: 30px;
    letter-spacing: 2px;
    text-shadow: 10px 5px 1px #000;
}
  
.topic-body .cooked {
    font-family: "Comic Sans MS";
}
    
header.d-header.clearfix {
    /* set a basic color for the header */
    background-color: rgba(30, 255, 0, .79);
    border: 5px solid #4d2302;
}
 
h2#site-text-logo.text-logo {
    letter-spacing:5px;
    color: rgba(255, 0, 0, 0);
    background-image: url(http://i.imgur.com/Gz0Is5X.png);
    background-size: 750px;
    padding-right: 7000px;
    background-repeat: no-repeat;
    border: 5px solid #530909;
}
  
i.fa.fa-search {
    /* set the color for the magnifying glass/search button */
    color: #ff00b8;
    border: 5px solid #335835;
}
  
i.fa.fa-bars {
    /* set the color for the hamburger menu icon */
    color: #ff6d00;
    border: 5px solid #6d314b;
}
  
div#main-outlet.wrap {
    /* set a background color behind the "main middle area", 4th number is transparency */
    background-color: rgba(255, 0, 0, .53);
    border: 5px solid #374012;
}
  
img#site-logo.logo-small {
    /* when in a thread, brighten up the butterfly logo to be about the main text color #AAA */
    -webkit-filter: brightness(1.9);
}
  
a.widget-link.topic-link {
    /* set the color for the thread title when in a thread */
    color: #1acffc;
    border: 5px solid #5c2a19;
}
  
.extra-info-wrapper .badge-wrapper.bullet span.badge-category {
    /* in a thread, set the color for the category you are in at the top */
    color: #1ad6fc !important;
    border: 5px solid #5c4b13;
}
  
p {
    /* set post text color */
    color: #00fff5;
    border: 5px solid #5c4b13;
    text-shadow: 10px 10px 1px #000;
    word-spacing: 30px;
    letter-spacing: 2px;
  }
}
