css:
@-moz-document url-prefix("https://login.jupitered.com/student"), url-prefix("https://login.jupitered.com/login"), url-prefix("https://login.jupitered.com"), url-prefix("https://login.jupitered.com/0/student"), url-prefix("https://juno.jupitered.com") {
body {
    background-color: #262626 !important;
}
.pad20 {
    color: #fff;
}
.hilite {
    color: #fff;
    background-color: #fff !important;
}
.print {
    color: #ffffff;
}
.studcap {
    background-color: #262626 !important;
}
.bgcolor {
    background-color: #262626 !important;
}
.smtab {
    color: #262626;
}
.menu {
    background-color: #262626;
    color: #fff;
}
.menui {
    background-color: #262626;
    color: #fff;
}
#alert {
    color: #fff;
}
#showcalendar {
    color: #fff;
}
.shade {
    background-color: #717171 !important;
}
.noprint {
    color: #484848;
}
.colite {
    background-color: #262626;
    color: #fff;
    border: 1px solid #fff;
}
.btni {
    background-color: #202226 !important;
    background-image: none !important;
    color: #fff;
    border-top: 1px solid white;
    border-bottom: 1px solid white;
}
.btnir {
    background-color: #202226 !important;
    background-image: none !important;
    border-top-right-radius: 50%;
    border-bottom-right-radius: 50%;
    border-right: 1px solid white;
    border-top: 1px solid white;
    border-bottom: 1px solid white;
}
.btnil {
    background-color: #202226 !important;
    background-image: none !important;
    border-bottom-left-radius: 50%;
    border-top-left-radius: 50%;
    border-left: 1px solid white;
    border-top: 1px solid white;
    border-bottom: 1px solid white;
}
.tab_login_off {
    background-color: #333030 !important;
    color: white;
}
.tab_login_off_r {
    background-color: #333030 !important;
    color: white;
    background-image: none;
}
.tab_login_off_l {
    background-color: #333030 !important;
    color: white;
    background-image: none;
}
.tab_login_lit {
    background-color: #555759 !important;
    color: white;
    border-top: 1px solid white;
}
.tab_login_lit_r {
    background-color: #555759 !important;
    color: white;
    background-image: none;
    border-top: 1px solid white;
    border-right: 1px solid white;
    border-top-right-radius: 50%;
}
.tab_login_lit_l {
    background-color: #555759 !important;
    color: white;
    background-image: none;
    border-top: 1px solid white;
    border-left: 1px solid white;
    border-top-left-radius: 50%;
}

/*To-Do*/
td {
    color: #cecece !important;
}
.cell {
    color: #980000 !important;
}
#revframe {
    background-color: #181918 !important;
}
#writetext {
    background-color: #252825 !important;
    color: #b2b2b2;
    border: 1px solid white;
}
.ahover {
    color: #af2a1d !important;
}
.content {
    color: #fff;
}
table .inverse tbody tr:nth-child(17) td img {
    display: none;
    /*Cell Colors*/
}
.whitebox,
.whitecell,
.page {
    background-color: #313131bf !important;
    /*Green*/
}
#bar2,
#leftnav,
.tabcurve,
.navlit,
.classlit,
.rowlit,
.menulit,
.lit,
.btn:hover,
.darkbox .btn,
.darkbox .dimbtn,
.dark .btn,
.dark .dimbtn,
.btnl:hover,
.topnav2list,
.progbar,
.podbtn,
.podbtnbig,
.podopt0,
.podopt1,
.podhandlel,
.podhandler,
.jupbtnlit,
.jupbtn:hover,
.callit,
.sellit,
#busydot,
.seatrollhi,
.colorbg,
.findbg .rowlit,
.blockhi:hover {
    background-color: hsla(0, 0%, 13%, .3) !important;

}
.podchoicelit {
    color: rgb(0, 0, 0);
}
.podchoice:hover {
    color: rgb(0,0,0);
}   
.podchoicer {
    display: table-cell;
    text-align: left;
    vertical-align: middle;
    padding: 8px 12px 8px 8px;
    border-radius: 0px 9px 9px 0px;
    background-color:#3e3e3e
        
}.podtext {
    color: rgb(175, 175, 175);
}
.rowhi:hover,
.menurow:hover,
.hi:hover,
.hilite {
    background-color: hsl(0, 0%, 37%) !important;
}
.alert,
.alertcolor {
    background-color: hsla(0, 0%, 39%, .5) !important;
    color: #ddd !important;
}
bbody,
.password,
.normaltext {
    color: hsl(0, 0%, 21%) !important;
}
*/*What Makes the progress bar dark*/
.normaltext {
        background-color: #2d2d2d !important;
}

/*Blue*/

/* aqua 25  (logo,btn) */
.btn,
.dimbtn,
.btncolor,
#logobg1,
#logobg2,
#loginlogobg,
.toptab1:hover,
.toptab1hi,
.navrow:hover,
.classtab:hover,
.classsemi:hover,
.darkhi:hover,
.btntran:hover,
.darker,
#podprogspace,
.jupbtnlit,
.jupbtn:hover,
.caldate:hover,
.caldim:hover,
.caltoday,
.btnllit,
.findbg .rowhi:hover,
.xhover {
    background-color: hsla(0, 0%, 15%, .51) !important;
}
.logotext {
    color: hsl(0, 0%, 23%) !important;
}
@keyframes glow {
    0%,
    40% {
        background-color: hsl(0, 80%, 40%) !important;
    }
    60%,
    100% {
        background-color: hsl(0, 80%, 25%) !important;
    }
}

/* aqua 32  (bar1,darkbox) */
#bar1,
#sidebar,
.navlist,
.topnav1list,
.navtab,
.navtabdim,
.darkbox,
.dark,
.darkhi,
#tipbox,
.topnav2list .navlit,
#loginbar1,
.minibar,
#podbar1,
#podbar2,
.podbtnrim,
.jupbtns,
.calbox,
#busybar,
.findbg {
    background-color: hsl(0, 0%, 22%) !important;
}

/*Normal CSS*/
.tab:hover,
.toptab2:hover,
.toptab2hi,
.topnav2list .navrow:hover,
.darkbox .btn:hover,
.dark .btn:hover,
.podbtn:hover,
.podbtnbig:hover,
.podoptlit,
.podopthi:hover,
.selhi,
.touchnavlit {
    background-color: hsl(0, 0%, 32%) !important;
}
.textbox,
.textboxp1,
.menuclosed,
.menuopenbox,
.menuabtn,
.menulist,
.menualist,
.border,
.textarea,
.darkbox .calbox {
    border-color: hsl(0, 0%, 32%) !important;
}
.placeholder {
    color: hsl(0, 0%, 32%) !important;
}

/* Menu Items */
#bar1,
#sidebar,
.btn,
.dimbtn,
.btncolor,
.navlist,
.topnav1list,
.navtab,
.navtabdim,
.darkbox,
.dark,
.darkhi,
.graph,
.graphbar,
#tipbox,
.topnav2list .navlit,
#loginbar1,
.minibar,
#podbar1,
#podbar2,
.podbtnrim,
.jupbtns,
.calbox,
#busybar,
.seat,
.blockhi,
.blockun,
.btnllit,
.findbg,
.xhover {
    background-color: hsl(0, 0%, 28%) !important;
}
.widgetstroke,
.iconstroke,
.iconstrokeh {
    stroke: hsl(0, 0%, 40%) !important;
}
.widgetfill,
.iconfill,
.iconfillh,
.menudot {
    fill: hsl(0, 0%, 40%) !important;
}
.btnl,
.hyperlink,
.clink:hover,
a,
.colortext {
    color: hsl(0, 60%, 50%)
}
}
