css:
@-moz-document domain('somtoday.nl') {
/* The yellow ribbon tag that contains icons such as messages or the back/forward buttons in the rooster tab*/
.yellow.ribbon {
    background: url('https://wouter.pw/stylish/som_pink.png') no-repeat left 0;
    position: relative;
    z-index: 2
}
.yellow.ribbon p {
    background: url('https://wouter.pw/stylish/som_pink.png') no-repeat right 0
}
#main-menu-bottom {
    background-color: #ff84e6;
}
#main-menu a.active,
#main-menu a.active:hover {
    background: #ff84e6;
}
#main-menu a:hover {
    background: #ff84e6;
}
#dialog {
    background: #ff84e6;
}
.IconFont.b-alert {
    color: #ff84e6;
}
.today {
    border-bottom: solid 3px #ff84e6;
}
.stpanel .form--input i {
    color: #ff84e6
}
.stpanel .form--select i:after {
    border-top: .3rem solid #ff84e6
}
.stpanel .form--select i:before {
    border-bottom: .3rem solid #ff84e6
}
.stpanel .form--input.state-disabled:hover i {
    color: #ff84e6 !important
}
.icon-toets,
.icon-huiswerk,
.icon-studiewijzer,
.icon-leermiddelen,
.icon-geen-van-beide,
.icon-maatregel {
    background: #ff84e6;
}
.stpanel section form {
    background: #ff84e6;
}
.stpanel--meldingen {
    background: #ff84e6;
}
.stpanel--meldingenpage {
    background-color: #ff84e6
}
.stpanel--screensaver {
    background: #ff84e6 !important
}
.stpanel--screensaver .form--input i {
    color: #ff84e6
}
.stpanel--screensaver .form--select i:after {
    border-top: .3rem solid #ff84e6
}
.stpanel--screensaver .form--select i:before {
    border-bottom: .3rem solid #ff84e6
}
.stpanel--screensaver .form--input.state-disabled:hover i {
    color: #ff84e6 !important
}
.stpanel--screensaver section form {
    background: #ff84e6;
}
}
