css:
@-moz-document domain("www.tvtime.com") {
body.white {
    background: #0f0f0f;
}

body {
    background: #0a0a0a;
    font-family: ProximaNova,"Helvetica Neue",Helvetica,Arial,sans-serif;
    color: #CCC;
}

.page-center {
    display: block;
    background-color: #0a0a0a;
}

#calendar .day .day-header .relative-date {
    font-size: 18px;
    border-top: 5px solid #008860;
    padding-top: 2px;
    width: 150px;
    margin: 2px auto 0;
    color: #777;
}

.fanarts-list>li .fanart-container {
    height: 235px;
    background-color: #0f0f0f;
    border: 1px solid #0f0f0f;
    padding: 0;
    overflow: hidden;
    position: relative;
}

.fanarts-list>li .fanart-details p {
    width: 193px;
    margin: 5px auto 0;
    border-top: 0px solid #e0245e;
    color: #ccc;
    font-size: 13px;
    font-family: ProximaNova;
    padding: 5px 0;
}

.fanarts-list>li .fanart-details .secondary-link {
        font-size: 16px;
    color: #777;
    font-family: Calibri;
    font-weight: bold;
}

.page-right {
    right: 0;
    border-left: 1px solid #0F0F0F;
}
.page-sidebar {
    position: fixed;
    overflow: hidden;
    top: 0;
    bottom: 0;
    width: 240px;
    vertical-align: top;
    background-color: #0F0F0F;
    z-index: 99;
}

#calendar-filters .mode {
    width: 100%;
    margin: 5px 0 15px;
    background: #101010;
    color: #ccc;
    border-radius: 2px;
    border: 1px solid #101010;
    outline: 0;
}

#calendar-picker {
    padding-top: 5px;
    font-size: 14px;
    font-weight: 400;
    font-family: ProximaNovaSB;
    color: #666;
    border-top: 3px solid #E0245E;
}

#global-search input {
    font-size: 14px;
    font-family: ProximaNova;
    font-weight: 400;
    border: 1px solid #151515;
    color: #CCC;
    padding: 4px 6px 4px 26px;
    background-color: #151515;
    width: 196px;
}

.page-left section {
    padding: 12px 0;
    margin: 0 20px;
    border-top: none;
}

#calendar .day {
    border-bottom: 0px solid #008860;
    padding-bottom: 8px;
    margin-bottom: 12px;
}

#calendar .day.today .day-header .relative-date, #calendar .day.tomorrow .day-header .relative-date, #calendar .day.yesterday .day-header .relative-date {
    color: #A9894F;
}

.fanarts-list>li .fanart-details h2 a {
    color: #1888BC;
}

#settings .tab-content {
    max-width: 800px;
    margin: 10px auto 40px;
    padding-bottom: 20px;
    border: none;
    background: #0f0f0f;
}

#settings .nav-pills>li.active>a, #settings .nav-pills>li.active>a:focus, #settings .nav-pills>li.active>a:hover {
    background-color: #0f0f0f;
    color: #CCC;
    font-family: ProximaNovaSb;
}

.nav>li>a:focus, .nav>li>a:hover {
    text-decoration: none;
    background-color: #0f0f0f;
}

#settings section .title {
    background: none;
    font-size: 16px;
    color: #008860;
    border-bottom: 1px solid #151515;
    padding: 8px 10px;
}

#settings section h2 {
    margin: 0 0 10px;
    font-size: 14px;
    background: none;
    padding: 3px 5px;
    color: #888;
    position: relative;
}

.btn-tvst:hover {
    background: none;
    color: #fff;
    text-decoration: none;
    box-shadow: none;
}

.btn-tvst-light-grey {
    background-color: #151515;
    color: #CCC;
    border: none;
}

#settings section .form-control {
    border-radius: 4px;
    background-color: #101010;
    border: none;
    box-shadow: none;
    outline: 0;
}

#settings .delete-account:hover {
    background-color: #E0245E;
}

#settings .delete-account {
    position: absolute;
    right: 15px;
    bottom: -30px;
    width: 160px;
    background-color: #E0245E;
    height: 25px;
    line-height: 25px;
    color: #fff;
    font-family: ProximaNovaSB;
    font-size: 14px;
    border-radius: 5px;
}

#settings section h3 {
    font-size: 14px;
    color: #CCC;
    border-bottom: none;
    padding-bottom: 3px;
    position: relative;
}

#settings .social-infos .twitter-message .twitter-message-info {
    margin-top: 10px;
    padding: 10px;
    background-color: #151515;
    border: none;
    border-radius: 4px;
}

#profile {
    background-color: #0A0A0A;
}

#profile .profile-nav {
    background-color: #008860;
    border-bottom: none;
}

#profile .profile-nav .nav-tabs>li .label {
    background: none;
    color: #CCC;
    text-transform: uppercase;
    padding: 0;
    font-size: 14px;
}

.nav>li>a:focus, .nav>li>a:hover {
    text-decoration: none;
    background: none;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #FFF;
    cursor: default;
    background-color: #008860;
    border: none;
}

#profile .profile-nav .nav-tabs>li.active, #profile .profile-nav .nav-tabs>li:focus, #profile .profile-nav .nav-tabs>li:hover {
    border: none;
    margin: 0;
    padding-bottom: 0;
    text-decoration: none;
    border-bottom: 0px solid #008860;
    background: none;
}
}
