css:
/*@-moz-document domain(elementy.ru) */
/* @-moz-document regexp("http://\\.elementy\\.ru/.*") */
@-moz-document domain(elementy.ru) {
  
  body {
    background-color: #242430;
    color: #FFF;
  }
  .ill_block.ins_block.center_ins_block.ilarge.cmt {
    opacity: 0.9; 
    font-size: 16px;
	font-family: Helvetica;
  }
  
  /* smth around main text too */ 
  .memo .itemblock {
    color: #d1d1d1; 
    font-size: 16px;
	font-family: Helvetica;
  }
  
  /* the main text block */
  .memo {
    color: #d1d1d1;
    font-size: 16px;
	font-family: Helvetica;
    text-align: justify;
  }
  
  .title {
    color: #af9c85;
  }
  .chapter  {
    color: #af9c85;
  }
  .chapter small {
    color: #af9c85;
  }
  .goto {
    color: #af9c85;
  }
  .sm_item {
    color: #af9c85;
  }
  .edesc {
     color: #af9c85;
  }
  
   .edate {
      color: #af9c85;
   }
  
  .xh .mb4 {
    background-color: #001034; 
  }

  .mb itemhead newslists {
    color: #FFF;
  }
  
  .mbshading {
    background-color: #415481;
  }
  
  .img {
    opacity: .92;
  }
  
  a {
    color: #FFF;
  } 
  
  a:hover {
    color: #fb7820;
  }
  
/*  insertion block - ligth blue */
  .subins_block {
    background-color: #26365b; 
  }
  
  .itemblock {
    color: #FFF;
  }
  
  /* comments counter at the left top */
  .counter {
    background-color: #1c1c1c;
  }
  
  .mbshading {
    background-color: #5f7097;
  }
  .author_caption {
    background-color: #26365b; 
  }
  .comment {
    color: #b4b6c1;
  }
  
  /* the text of comment */
  .brief {
    color: #d1d1d1;
  }
  .forum .fmhead .fmauthor {
    color: #af9c85;
  }
  .mb3 rub1 {
    color: #af9c85;
  }
  .ill_block {
    background-color: #001034;
  }
  div.mb:nth-child(1) {
    color: #FFF;
  }
  .chead {
    color: #FFF;
  }
  div.clblock:nth-child(2) > h2:nth-child(1) > a:nth-child(1) {
    color: #FFF;
  }
  .crblock > h2:nth-child(2) > a:nth-child(1) {
    color: #af9c85;
  }
  
  /* was beige - became badge :P boxes around imgs with links to article */
  .beige {
    background-color: #001034;
  } 
  .novosti_nauki > hr:nth-child(1) {
    background-color: #fff;
    color: #fff;
  }
  .crblock > h2:nth-child(1) > a:nth-child(1) {
    color: wheat;
  }
  .hfooter a {
    color: #fff;
  }
  .memo .pr_statement {
    background-color: #534C56;
  }
  
  /* the horizontal line on top */
  hr.top {
    background-color: #fff;
  }
  /* links to books and authors */
  ul.statmenu li.sm_item a {
    color: #8fb0ff;
  }
  ul.statmenu li.sm_item a:hover {
    color: white;
  }
  h1 a, h2 a {
    color: #af9c85;
  }
  
}
