css:
@-moz-document domain("ngb.to") {
html {
    background-color: #111;
}

html.cke_skin_kama_container {
    background-color: #fff;
}

html.cke_skin_kama_container body,
body.yui-layout {
    width: 100% !important;
}

body {
    margin: 0px auto !important;
    width: 80% !important;
}

@media (max-width: 700px) {
    .restrain {
        width: 100%;
    }
}

@media (max-width: 1300px) {
    body {
        width: 100% !important;
    }
}

/* Logo and navigation block */
.above_body {
    margin-top: 45px !important;
    background: none;
}

.doc_header {
    margin-bottom: 20px !important;
}

.navbar {
    height: 6.4em !important;
    border-radius: 6px;
}

.navtabs.floatcontainer {
    padding-top: 8px;
    border-radius: 6px 6px 0px 0px;
    border-top: 1px solid #333;
}

.navtabs ul.floatcontainer {
    border-top: 1px solid #555;
}

ul.floatcontainer {
    top: 35px !important;
    padding-top: 4px;
}

.navtabs li {
    border: none !important;
}

.navtabs li .navtab {
    border: 1px solid #333 !important;
    border-bottom: none !important;
}

.navtabs li .navtab:hover {
    border: 1px solid #444 !important;
    border-bottom: none !important;
}

.navtabs li.selected .navtab {
    border: 1px solid #444 !important;
    border-bottom: none !important;
}

.navtab {
    border-radius: 6px 6px 0px 0px;
    margin-left: 6px;
}

.globalsearch {
    position: absolute;
    margin: 0px;
    padding: 0px;
    margin-top: 4px;
    min-width: 33%;
    right: 26px;
}

.globalsearch .searchbutton {
    position: relative;
    right: 4px;
}

.globalsearch .buttoncontainer {
    border-color: #333;
}

.globalsearch .textboxcontainer {
    margin-bottom: 9px;
    border: none;
    width: 54%;
    right: 150px;
    position: absolute;
}

.globalsearch .textboxcontainer span {
    border: none;
    width: 100%;
}

.globalsearch input.textbox {
    width: 98%;
}

.navbar_advanced_search {
    padding-right: 0px;
}

.navbar_advanced_search li {
    clear: none;
    margin-left: 30px;
}

ul.ngb-elsewhere {
    position: absolute;
    top: -40px;
    right: 0px;
}

.ngb-elsewhere > li {
    margin-left: 8px;
}

/* Body and content */
.body_wrapper {
    border-radius: 6px;
}

#forums > li > div.forumhead.collapse {
    border: none;
    padding: 7px;
    margin-top: 14px;
    margin-bottom: 7px;
    border-radius: 6px;
}

.forumhead .collapse {
    position: relative;
    top: 19px;
    right: -25px;
}

.forumhead .collapse img {
    min-width: 14px;
    min-height: 14px;
}

#forums .forumbit_post {
    border: none;
    margin: 0px;
    margin-bottom: 3px;
    border-radius: 6px;
}

#forums .forumrow.table {
    border-radius: 6px;
    border: 1px solid #333;
}

ol.childforum .datacontainer .forumdescription {
    position: relative;
    top: -1px;
}

.forumrow {
    padding-top: 6px;
    padding-bottom: 3px;
}

ol.childforum > li > div {
    padding-top: 7px;
    padding-bottom: 8px;
}

ol.childforum > li:nth-of-type(2n) {
    background-color: #202020;
}

ol.childforum .datacontainer > div {
    line-height: 1.024rem;
}

ol.childforum .subforums {
    display: inline-block;
    width: 100%;
}

ol.childforum .subforums img {
    position: relative;
    top: 2px;
}


ol.childforum .forumstats {
    padding-top: 0.35rem;
    line-height: 1.1rem;
}

ol.childforum .forumlastpost {
    line-height: 1.04rem;
}

.lastposttitle img {
    position: relative;
    top: 2px;
    width: 12px;
}

.wgo_block .blockhead {
    border-radius: 6px 6px 0px 0px;
}

.wgo_block.block > div {
    border-radius: 0px 0px 6px 6px;
}

/* Forum page */
.above_threadlist_controls {
    padding: 7px;
    border-radius: 6px;
    border: none;
    margin: 0px;
}

.threadlisthead.table,
.noinlinemod,
.announcements,
.announcements .announcerow {
    border: none;
    margin: 0px;
    margin-bottom: 2px;
    border-radius: 6px;
}

.announcements {
    margin-top: 7px;
}

.forumhead + ol > li:first-child,
.threadlist .threadlisthead + ol,
.blockrow:first-child,
.searchbits > li:first-child,
ol#threadlist > li:first-child,
#usercp_content ol#threads > li:first-child {
    border: none;
}

.forumdisplaypopups {
    margin-top: 4px;
}

li.threadbit.attachments,
li.threadbit {
    margin-bottom: 3px;
    border: none;
}

li.threadbit > div {
    border: none;
}

.author {
    position: relative;
}

.threadbit .pagination {
    position: absolute;
    top: -3px;
}

.threadbit .pagination a {
    padding: 2px 5px;
    margin-right: 3px;
}

.threadbit .pagination .separator {
    line-height: 1.024rem;
}

ol.stickies > li:last-of-type,
ol.threads > li:last-of-type {
    border: none;
    margin-bottom: 14px;
}

ol.threads > li.threadbit:nth-of-type(2n) > div {
    background-color: #202020;
}

ol.stickies > li.threadbit > div {
    background-color: #000;
}

ol.stickies > li.threadbit:nth-of-type(2n) > div {
    background-color: #161616;
}

ol.stickies > li.threadbit > div,
ol.threads > li.threadbit > div {
    border-radius: 6px;
    border: 1px solid #444;
}

ol.stickies .threadtitle > img {
    margin-top: 0.5rem;
}

ol.threads .threadtitle > img {
    margin-top: 0.5rem;
}

.threadbit .nonsticky,
.threadbit .sticky {
    background-color: transparent;
}

li.threadbit > .threadinfo:nth-of-type(odd) .inner {
    background-color: #202020;
}

li.threadbit .threadinfo {
    line-height: 1.175rem;
    padding-top: 7px;
}

li.threadbit .threadstats {
    border-left: 1px solid #5a5a5a;
    border-right: 1px solid #5a5a5a;
    line-height: 1.1rem;
    padding-top: 14px;
    padding-bottom: 14px;
}

li.threadbit .threadlastpost {
    padding-top: 14px;
    padding-bottom: 14px;
    line-height: 1.024rem;
}

.searchbits li.threadbit .threadstats,
.searchbits li.threadbit .threadpostedin {
    padding-top: 14px;
    padding-bottom: 14px;
}

.threadbit .pagination > dd > span:first-child a {
    border-radius: 6px 0px 0px 6px;
}

.threadbit .pagination > dd > span:last-child a {
    border-radius: 0px 6px 6px 0px;
}

.threadbit .lastpostdate {
    margin-left: 5px;
}

.threadbit .lastpostdate img {
    position: relative;
    top: 2px;
    width: 12px;
}

.blockhead {
    border: none !important;
    border-radius: 6px 6px 0px 0px;
}

.blockbody {
    border-radius: 0px 0px 6px 6px;
    border: 1px solid #444 !important;
}

#nrreview .postbit {
    border-color: #555;
    border-radius: 0px;
    padding: 6px 9px;
}

#post_preview .blocksubhead {
    border-color: #444;
}

blockquote.preview .signature {
    border-color: #444;
}

blockquote.preview hr,
blockquote.restore hr {
    border-color: #444;
}

.floatcontainer.footer {
    border-radius: 6px;
    border-color: #444;
}

a.newcontent_textcontrol {
    padding: 6px 12px !important;
    border-radius: 6px !important;
    border-left: 1px solid #777 !important;
    border-top: 1px solid #888 !important;
}

.pagination span a.popupctrl {
    padding: 4px 9px 4px 23px;
}

.pagination span a,
.pagination span.selected a,
.pagination span.prev_next a,
.pagination span.first_last a {
    line-height: 1.024rem;
    border: 1px solid #444;
}

.pagination span a:hover {
    border: 1px solid #888;
}

.pagination span.separator {
    line-height: 1.7rem;
    margin: 0px 6px;
}

.pagination span a,
.pagination span.selected a {
    padding: 4px 8px;
}

.pagination .popupctrl {
    border-radius: 6px 0px 0px 6px;
}

.pagination span.prev_next a,
.pagination span.first_last a {
    padding-top: 1px;
    padding-bottom: 7px;
}

.pagination span:last-of-type a {
    border-radius: 0px 6px 6px 0px;
}

.navpopupmenu.popupmenu.nohovermenu a.textcontrol,
.navpopupmenu.popupmenu.nohovermenu a.popupctrl,
.options_input_block input,
.controlbar a.textcontrol,
.groupcontrols a.textcontrol {
    padding: 6px 12px;
    margin-left: 3px;
    border: 1px solid #666;
    border-radius: 6px;
    border-left: 1px solid #777 !important;
    border-top: 1px solid #888 !important;
}

/* Thread view */
.postlist_popups {
    margin-top: 4px;
}

.thread_controls {
    border: none;
    margin: 0px;
    margin-bottom: 12px;
    border-radius: 6px;
    padding: 4px 0px;
}

.posts .postcontent {
    padding-top: 0.25rem;
}


.bbcode_container div.bbcode_code,
.bbcode_container pre.bbcode_code {
    border-radius: 6px;
}

.posts .posthead {
    border: 1px solid #333;
    border-radius: 6px 6px 0px 0px;
    padding: 6px 0px;
}

.postbit .posthead .nodecontrols a,
.postbitlegacy .posthead .nodecontrols a,
.eventbit .eventhead .nodecontrols a {
    top: 0.3em;
}

.forumbit_nopost .forumbit_nopost .forumrow,
.threadbit > div,
.postbit,
.postbitlegacy {
    border-color: #333;
    border-radius: 6px;
}

.forumbit_post .forumrow {
    border-color: #333;
}

.toolsmenu {
    border: none;
    border-radius: 6px 6px 0px 0px;
    line-height: 1.024rem;
}

.forumhead,
.threadlisthead {
    border: none;
    border-radius: 6px 6px 0px 0px;
}

.toolsmenu {
    border-radius: 6px;
    padding: 0px 0px 5px 0px;
}

.postbitlegacy .postbody,
.eventbit .eventdetails .eventbody {
    border-left-color: #444;
}

.postbody img.inlineimg {
    vertical-align: bottom;
    position: relative;
    top: -0.16rem;
}

.postbit .postfoot .textcontrols,
.postbitlegacy .postfoot .textcontrols,
.eventbit .eventfoot .eventcontrols {
    border-color: #333;
    padding: 8px 0px;
    border-radius: 0px 0px 6px 6px;
}

.bbcode_container div.bbcode_quote {
    padding-top: 4px;
    border-radius: 6px;
}

.postbitlegacy .fish_thx_list {
    border-color: #444;
    border-radius: 6px;
    padding: 6px 10px;
}

.signature {
    border-color: #444;
    line-height: 1.2rem;
}

.postlistfoot {
    border: none;
    padding: 6px 0px 12px 0px;
    border-radius: 6px;
}

#forums .forumbit_post .foruminfo .viewing {
    margin-top: 0px;
    line-height: 1.1rem;
}

.forumbits > ol li:last-of-type,
.forumbits > ol li:last-of-type div {
    border-radius: 0px 0px 6px 6px;
}

.forumbit_post:nth-of-type(2n) {
    background-color: #111;
}

.forumbit_post .datacontainer {
    line-height: 1.024rem;
}

.forumstats_2 {
    line-height: 1.024rem;
}

.threadbit .nonthread {
    border: none;
}

.noinlinemod.forumfoot {
    margin-bottom: 8px;
}

.below_threadlist .breadcrumb {
    margin-top: 1px;
}

.breadcrumb {
    margin-bottom: 12px;
}

.breadcrumb .navbithome {
    position: relative;
    top: 0px;
    left: 3px;
}

.breadcrumb a {
    margin-top: 2px;
}

.breadcrumb .lastnavbit > span {
    line-height: 1.1rem;
}

.below_postlist {
    margin-bottom: 30px;
}

.texteditor {
    border-radius: 6px;
    border: none;
}

.texteditor .blockrow {
    border-radius: 0px 0px 6px 6px;
    margin-bottom: 12px;
    border: 1px solid #444;
    border-top: none;
}

.texteditor .blockrow:first-of-type {
    border-radius: 6px 6px 0px 0px;
    margin-bottom: 0px;
    border-top: 1px solid #444;
    border-bottom: 1px solid #444;
}

.postrow .blocksubfoot.actionbuttons {
    background-color: transparent;
}

/* Profile */
#usercp_nav .blockhead {
    border-radius: 6px 6px 0px 0px;
}

#usercp_nav .blockbody ul li:first-child .blocksubhead {
    border-top: none;
}

#usercp_nav .blocksubhead {
    border-color: #555;
}

.cp_content .threadbit .nonsticky {
    border-top: 1px solid #444;
}

.cp_content .threadbit:nth-of-type(2n) .nonsticky {
    background-color: #161616;
}

#usercp_content .threadbit .threadinfo img:last-of-type {
    top: 32px;
}

#usercp_content .threadbit .threadlastpost {
    padding-top: 14px;
    padding-bottom: 14px;
}

#usercp_content .userlist li {
    border-color: #444;
    border-radius: 6px;
}

#sidebar_container.member_summary {
    line-height: 1.024rem;
    border-color: #444;
    border-radius: 6px;
}

#sidebar_container .underblock {
    border-color: #444;
}

dd.userprof_module,
dd.userprof_moduleinactive,
dd.userprof_content {
    border: none;
    border-radius: 6px 6px 0px 0px;
    margin-right: 3px;
}

.profile_content a.floatright:first-child + .subsectionhead {
    border: none;
}

.profile_content div.subsectionhead,
.profile_content .texteditor {
    border-radius: 6px 6px 0px 0px;
    border: none;
}

.profile_content .userprof_content {
    margin-bottom: 20px;
    line-height: 1.024rem;
}

#view-aboutme {
    border: none;
}

#view-aboutme .blocksubhead:nth-child(2) {
    border-radius: 0px !important;
}

.userprof_content.blockbody {
    border: none !important;
}

.profile_content .friends_list li:first-child,
.profile_content .userprof_content {
    border-color: #444;
}

.threadbit .nonsticky,
.threadbit .deleted,
.threadbit .discussionrow,
.threadbit .ignored {
    border-color: #444;
}

#threads .threadbit {
    margin-bottom: 3px;
    border-radius: 6px;
}

.profile_content #threads .threadbit:nth-of-type(2n) {
    background-color: #161616;
}

.forumfoot {
    border: none;
    border-radius: 6px;
}

#forum_icon_legend h5 {
    border-radius: 6px 6px 0px 0px;
    border: none;
}

#subscription_info #forum_icon_legend dl {
    border-radius: 0px 0px 6px 6px;
    border-color: #444;
}

.settings_form_top_border {
    border: none;
}

#above_postlist .folderstats {
    margin: 10px 0px 8px 0px;
    float: left;
    clear: both !important;
}

#pmfolderlist .blocksubhead.sortrow {
    border: none;
}

#pmfolderlist .blocksubhead {
    border-color: #555;
    padding: 6px 9px;
    background-color: #000;
}

#pmfolderlist .pmbit {
    padding: 9px 12px;
    border-color: #444;
}

#pmfolderlist .pmbit:nth-of-type(2n) {
    background-color: #282828;
}

.postbody .bbcode_container {
    margin: 0px;
    margin-bottom: 14px;
}


blockquote.preview.forumcontent {
    line-height: 1.1rem;
}

.foldercount {
    position: relative;
    top: 2px;
}

.blockbody.settings_form_border .blocksubhead {
    border-top: none;
}

.formcontrols .blockrow {
    border-color: #444;
}

.postbit .posthead,
.postbitlegacy .posthead,
.eventbit .eventhead {
    border-color: #444;
    border-radius: 6px 6px 0px 0px;
    padding: 7px 0px;
}

/* New topcis and search */
.block.searchresults {
    border-radius: 6px;
    margin-top: 20px;
}

.searchresults .blockbody {
    border: none !important;
}

h2.searchlisthead {
    border: none;
    margin: 0px;
    margin-bottom: 2px;
    border-radius: 6px;
}

.searchbits > li {
    border-radius: 6px;
    margin-bottom: 3px;
}

.searchbits > li.threadbit > div {
    border: 1px solid #444;
}

.searchbits > li.threadbit:nth-of-type(2n) > div {
    background-color: #111;
}

.searchbits .pagination {
    top: -4px;
}

.searchbits li.threadbit .threadstatus {
    position: relative;
    top: 3px;
}

.searchbits li.threadbit .threadinfo {
    line-height: 1.024rem;
}

.searchbits li.threadbit .threadstats {
    line-height: 1.1rem;
}

.searchbits li.threadbit .threadlastpost {
    line-height: 1.1rem;
}

.searchbits li.threadbit .threadlastpost img {
    position: relative;
    top: 2px;
}

.searchbits li.threadbit .threadpostedin {
    line-height: 1.1rem;
}

.searchbits li.threadbit .threadstats,
.searchbits li.threadbit .threadpostedin {
    border-color: #444;
}

/* Actity */
#activitylist {
    border: none;
}

#activitylist .activitybit {
    border-color: #444;
    margin: 0px;
}

#activitylist .activitybit:nth-of-type(2n) {
    background-color: #262626;
}

#activitylist .activitybit:last-of-type {
    border-radius: 0px 0px 6px 6px;
}

#activitylist #olderactivity hr {
    border-top: 1px solid #666;
    border-bottom: none;
}

.activitystream .blockbody {
    border-top: none !important;
}

.newactivity,
.moreactivity {
    border-radius: 6px;
    border-color: #444;
}

/* Own posts */
.block.searchresults .searchbits .postbitcontainer {
    border: 1px solid #444;
    margin-bottom: 3px;
}

.block.searchresults .searchbits .postbitcontainer:first-of-type {
    margin-top: 6px;
}

.postbit .posthead {
    border: none;
    margin: 0px;
}

.postbit .posttitle,
.postbitlegacy .title {
    border: none;
    padding: 6px 9px;
}

.postbit .posttitle,
.postbitlegacy .title {
    border-color: #444;
}

.postbit .userinfo_noavatar {
    width: 100%;
    line-height: 1.024rem;
    border-bottom: 1px solid #444;
}

.postbit .postbody {
    border-radius: 0px 0px 6px 6px;
    background-color: #333;
}

.postbit .postbody .postcontent {
    line-height: 1.024rem;
}

/* showgroups */
.showgroups_table th,
.showgroups_table .groupbit td {
    border-color: #444;
}

.groupbit .postcontrols a {
    padding: 3px 6px;
}

.groupbit:hover .postcontrols a {
    border-color: #555;
}

/* General styles */
select,
input {
    border: none;
}

.actionbuttons .group {
    padding: 0px;
}

.actionbuttons .group .button,
.singlebutton .button {
    padding: 6px 12px !important;
    border-radius: 6px;
}

.menusearch input.button {
    padding: 4px 6px !important;
}

.checkradio input {
    position: relative;
    top: 5px;
}

.texteditor.forum .cke_skin_kama textarea.cke_source,
.texteditor.forum textarea {
    line-height: 1.024rem;
    padding: 3px 6px;
}

.footer_links {
    margin-top: 5px;
}
}
