@-moz-document domain(politiken.dk) {
	.theme--adnative,
	.ad,
	.ad--horseshoe,
	.body__ad,
	.body__inline-content,
	.body__inline-article,
	.qc-cmp-ui-container {
		display: none !important;
	}

	body.qc-cmp-ui-showing {
		overflow: inherit !important;
	}

	.user--nosub .teaser .premium-stamp,
	.user--nosub .teaser .icon--premium,
	.user--nosub .list .premium-stamp,
	.user--nosub .list .icon--premium,
	.user--nosub .taginfo-special .premium-stamp,
	.user--nosub .taginfo-special .icon--premium {
		display: inline-block !important;
	}
}
