css:
@-moz-document domain("twitter.com") {
/*test*/
:root {
	--shade0:#252525;
	--shade1:#1d1d1d;
	--text:#DDDDDD;
	--main:#030303;
	--accentcolor:#3070ff;
	--accentcolorhighlight:#4080ff;
	--shade2:#0d0d0d;
}
body {
	color:var(--text)!important;
}
iframe {
	background:var(--shade2);
}
.wrapper, #page-outer {
	background:var(--main);
}
.tweet {
	background:var(--main);
	border-bottom: 1px solid var(--shade0);
}
.fullname {
	color:var(--text)!important;
}
.tweet:hover {
	background:var(--shade1);
}
.avatar {
	border-radius:0px;
}
.ProfileAvatar {
    border-radius:0px;
	border:1px solid var(--shade1);
}
.ProfileAvatar-image, .ProfileAvatar-placeholderImage {
	border-radius:0px;
}
.global-nav-inner, .ProfileCanopy-navBar {
	background:var(--shade1)!important;
	background-color:var(--shade1)!important;
}
.u-bgUserColor, .u-bgUserColorHover:hover, .u-bgUserColorHover:focus {
	background-color:var(--shade0)!important;
}
.ProfileHeaderCard-nameLink {
	color:var(--text)!important;
}
.TwitterCard-container--clickable {
	background:var(--shade0);
	transition:none;
}
.TwitterCard-container--clickable:hover {
	background:var(--shade1)!important;
	transition:none;
}
.ProfileHeading-content {
	background-color:var(--shade1);
	border:none;
}
.ProfileHeading-toggleItem.is-active, .ProfileHeading-toggleItem.is-active:hover, .ProfileHeading-toggleItem.is-active:focus {
	color:var(--text);
}

.ProfileNav-stat, .ProfileNav-value {
	transition:none;
}
.TwitterCard--animation {
	animation:none!important;
}
.module .flex-module, .SidebarCommonModules .Trends {
	background-color:var(--shade1);
	border:none;
}
.ProfileSidebar {
	background:var(--shade1);
}
.new-tweets-bar, .new-tweets-bar:hover {
	background-color:var(--shade0);
	border:none;
}
a, .btn-link, .btn-link:focus, .icon-btn, .pretty-link b, .pretty-link:hover s, .pretty-link:hover b, .pretty-link:focus s, .pretty-link:focus b, .metadata a:hover, .metadata a:focus, a.account-group:hover .fullname, a.account-group:focus .fullname, .account-summary:focus .fullname, .message .message-text a, .message .message-text button, .stats a strong, .plain-btn:hover, .plain-btn:focus, .dropdown.open .user-dropdown.plain-btn, .open > .plain-btn, #global-actions .new:before, .module .list-link:hover, .module .list-link:focus, .stats a:hover, .stats a:hover strong, .stats a:focus, .stats a:focus strong, .find-friends-sources li:hover .source, .stream-item a:hover .fullname, .stream-item a:focus .fullname, .stream-item .view-all-supplements:hover, .stream-item .view-all-supplements:focus, .tweet .time a:hover, .tweet .time a:focus, .tweet .details.with-icn b, .tweet .details.with-icn .Icon, .stream-item:hover .original-tweet .details b, .stream-item .original-tweet.focus .details b, .stream-item.open .original-tweet .details b, .client-and-actions a:hover, .client-and-actions a:focus, .dismiss-btn:hover b, .tweet .context .pretty-link:hover s, .tweet .context .pretty-link:hover b, .tweet .context .pretty-link:focus s, .tweet .context .pretty-link:focus b, .list .username a:hover, .list .username a:focus, .list-membership-container .create-a-list, .list-membership-container .create-a-list:hover, .new-tweets-bar, .card .list-details a:hover, .card .list-details a:focus, .card .card-body:hover .attribution, .card .card-body .attribution:focus {
	color:var(--accentcolor)!important;
}
.EdgeButton--primary, .EdgeButton--primary:focus {
	background-color:var(--accentcolor);
}
.stream-item:not(.no-header-background-module) {
	border:none;
	background:var(--main);
}
.ProfileNav-item--userActions {
	background-color:var(--shade1);
}
.ProfileHeaderCard-bio {
	color:var(--text);
}
.u-borderUserColor, .u-borderUserColorHover:hover, .u-borderUserColorHover:focus {
	border-color:var(--accentcolor)!important;
}
.u-textUserColor, .u-textUserColorHover:hover, .u-textUserColorHover:hover .ProfileTweet-actionCount, .u-textUserColorHover:focus {
	color:var(--text)!important;
}
.EdgeButton, .EdgeButton:visited {
	border-radius:0px;
	border:none;
}
.global-nav .search-input {
	border-radius:0px;
	border:none;
	background-color:var(--shade0);
}
.DashboardProfileCard {
	background:var(--shade1);
	border:none;
}
.DashboardProfileCard-avatarLink {
	border-radius:0px;
	background-color:transparent;
}
a>img:first-child:last-child {
	transition:none;
}
.DashboardProfileCard-avatarImage {
	border-radius:0px;
	border:none;
}
.EdgeButton--secondary {
	background-color:var(--shade0)
}
.EdgeButton--secondary:hover {
	background-color:var(--shade1)
}
.home-tweet-box, .LiveVideo-tweetBox, .RetweetDialog-commentBox {
	background-color:var(--shade1);
}
.top-timeline-tweetbox .timeline-tweet-box {
	border:none
}
.RichEditor, .TweetBoxAttachments {
	border:none;
	border-radius:0px;
}
input, textarea, div[contenteditable], .t1-select {
	background-color:var(--shade0)
}
.flex-module-header h3 {
	color:var(--text);
}
.wtf-module .UserSmallListItem {
	border-bottom: 1px solid var(--shade0);
}
.Trends {
	background-color:var(--shade1);
	border:none;
}
.content-header, .content-no-header {
	border:none;
}
.NotificationsHeadingContent {
	border:none;
	background-color:var(--shade1);
}
.ActivityItem {
	border-bottom: 1px solid var(--shade0);
}
.QuoteTweet-fullname {
	color:var(--text);
}
.QuoteTweet .tweet-content, .QuoteTweet-text a, .QuoteTweet-text a:hover, .QuoteTweet-text a:focus, .QuoteTweet-text a:active, .QuoteTweet-text .pretty-link b, .QuoteTweet-text .pretty-link s, .QuoteTweet-text .pretty-link:hover b, .QuoteTweet-text .pretty-link:hover s, .QuoteTweet-text .pretty-link:focus b, .QuoteTweet-text .pretty-link:focus s, .QuoteTweet-text .pretty-link:active b, .QuoteTweet-text .pretty-link:active s {
	color:var(--text);
}
.ActivityItem:not(.has-clickAction) .QuoteTweet--slim {
	background:var(--shade1);
	border:none;
	border-radius:0px;
}
.ActivityItem:not(.has-clickAction) .QuoteTweet--slim:hover {
	background:var(--shade0);
}
.hovercard.profile-card {
	border-radius:0px;
}
.hovercard {
	background-color:var(--shade1);
}
.hovercard.profile-card .ProfileCard-bg {
	border-radius:0px;
}
.hovercard.profile-card .ProfileCard-avatarImage {
	border:none;
}
.ProfileCard-avatarImage {
	border-radius:0px;
}
.ProfileCard-avatarLink {
	background-color:transparent;
}
.nav .session .dropdown-toggle:before {
	box-shadow:none;
	border-radius:0px!important;
}
.Avatar {
	border-radius:0px;
}
.nav .session .dropdown-toggle:before {
	border-radius:0px;
}
.nav .session .dropdown-toggle {
	border-radius:0px;
}
.DMActivity-header {
	background-color:var(--shade1);
	color:var(--text);
	border-bottom:1px solid var(--shade0);
}
.DMActivity-title {
	color:var(--text);
}
.DMInboxItem {
	background:var(--main);
	border-bottom:1px solid var(--shade0);
}
.DMInboxItem:hover {
	background:var(--shade1)!important;
}
.DMAvatar {
	border-radius:0px;
}
.DMActivity {
	border-radius:0px;
}
.DMInbox-content {
	background:var(--main);
}
.ProfileSidebar .flex-module-header h3 {
	color:var(--text);
}
.EdgeButton--secondary, .EdgeButton--secondary:hover, .EdgeButton--secondary:focus, .EdgeButton--secondary:active {
	color:var(--accentcolor);
}
.EdgeButton--primary:hover {
	background-color:var(--accentcolorhighlight);
}
.global-nav .people .count .count-inner {
	background:var(--accentcolor);
}
.ProfileNav-value {
	color:var(--text);
}
.u-borderUserColor:hover, .u-borderUserColorHover:hover {
	color:var(--accentcolorhighlight)!important;
}
.ProfileHeaderCard-location:hover {
	color:var(--accentcolorhighlight)
}
#global-actions > li > a {
	border-bottom-color:var(--accentcolor);
}
.DMActivity-body {
	background-color:var(--shade2)
}
.DirectMessage--sent .DirectMessage-text {
	background-color:var(--accentcolor)!important;
	color:var(--text);
}
.DirectMessage-contentContainer>*:last-child {
	background-color:var(--shade0);
	color:var(--text)
}
.DirectMessage-container, .DirectMessage-message:not(.with-cta) .DirectMessage-contentContainer {
	border-radius:0px;
}
.TwitterCard[data-card-client=dm][data-corners$="true,false"] .TwitterCard-container {
	border:none;
}
.DirectMessage-message {
	border-radius:0px
}
.TwitterCard[data-card-client=dm][data-corners$="true,false"] .TwitterCard-container {
	border-radius:0px;
}
.u-borderRadiusInherit {
	border-radius:0px!important;
}
.u-bgUserColorLightest {
	background-color:var(--shade1)!important;
	border-top:1px solid var(--shade0)
}
.u-borderUserColorLighter {
	border:none
}
.DMComposer-container {
	background-color:var(--shade1);
}
.DMComposer.tweet-form .RichEditor, .DMComposer.tweet-form .RichEditor.is-fakeFocus {
	border:none;
	background:transparent;
}
.RichEditor div[contenteditable], .RichEditor div[contenteditable]:focus, .RichEditor div[contenteditable].fake-focus {
	color:var(--text)
}
.DirectMessage-avatar img {
	border-radius:0px;
}
.TwitterCard-container {
	border:none;
}
.DirectMessage-container, .DirectMessage-message:not(.with-cta) .DirectMessage-contentContainer {
	align-items:flex-start
}
.DirectMessage--sent .DirectMessage-container, .DirectMessage--sent .DirectMessage-contentContainer {
	flex-direction:row;
}
.DirectMessage--sent .DirectMessage-footer {
	flex-direction:row;
}
.DirectMessage-message {
	max-width:unset
}
.DirectMessage-footerItem {
	margin:auto;
}
.stream-end-inner {
	background-color:var(--shade1);
}
.stream-end-item, .stream-end, .stream-loading, .stream-placeholder {
	border:0px;
}
.QuoteTweet {
	border:none;
	background:var(--shade2);
	border-radius:0px;
}
}
