css:
@-moz-document domain(trv-science.ru) {
  /* the very wide back - black */
  body.custom-background {
    background-color: #000;
  }
  /* the main text */
  body {
    color: #d1d1d1;
	text-align: justify;
  }
  /* the main text container - background */
  .rsrc-container {
    background-color: #303030;
  }
  /* title of an article */
  .page-header, .archive-article header a {
    color: #fff;
  }
  /* brief text on the main page */
  .entry-summary {
    color: #c1c1c1;
  }
  /* right column on the main page - links */
  .blacka a {
    color: #fff;
  }
  /* panel - about books and comments */
  .panel {
    margin-bottom: 20px;
    background-color: #303030;
  }
  /* header - the hat */
  .header-section {
    background: #000;
  }
  /* logo - inverted */
  .header-section .container {
    filter: invert(1);
  }
  /* the very top */
  .top-section {
    background: #303030;
  }
  /* links at very top */
  .rsrc-top-nav .navbar-inverse .navbar-nav > li > a {
    color: #fff;
  }
  /*  links highlight */
  a:hover, a:focus {
    color: #FB9246;
    text-decoration: underline
  }
  /* caption under images */
  .wp-caption-text {
    color: #000;
  }
}
