/* ==UserStyle==
@name           code.org Dark Theme
@namespace      Colin's Code.org Dark
@version        0.9.4
@description    A simple dark theme for code.org, loosely based on solarized dark
@author         Colin Lewis (http://colincon.ga)
==/UserStyle== */
@-moz-document domain("code.org") {
    body,
    div.user-stats-block,
    .dns-notes textarea,
    div#recent-courses > div > div > div:first-child > div > div > a,
    .full_container.submitted_readonly/*multiple choice/ quiz page thing*/
    ,
    #design-properties,
    #design-toolbox/*app lab design mode*/
    {
        background-color: #002B36;
    }
    #eventsTab/*app lab design mode*/
    {
        background-color: #002B36 !important;
    }
    div.user-stats-block > div > div > table > thead > tr:first-child,
    tr,
    div.panel-body,
    div.user-stats-block > div > div > div > div > div > div:not(#heading) > div > div,
    div.uitest-CourseScript,
    div#recent-courses > div > div > div:first-child > div,
    div#recent-courses > div > div > div > div div[style*="font-size: 14px;"],
    div.announcement-notification > div,
    div.announcement-notification > div > div > div > div,
    div#netsim-visualization > svg,
    div.netsim-tabs,
    div.editor-column > div > div,
    .droplet-palette-element div,
    .droplet-palette-element svg,
    textarea.response,
    div#recent-courses > div > div > div:last-child div/*find a course box*/
    ,
    #designWorkspaceBody,
    #propertiesTab/*app lab design mode*/
    {
        background-color: #33555E !important;
    }
    div.courseblock-tall {
        background-color: #93A1A1;
    }
    #phoneFrameWrapper > div {
        background-color: #93A1A1 !important;
    }
    div.netsim-packet,
    div.packet {
        background-color: rgba(0, 0, 0, 0) !important;
    }
    p,
    div.labelblock,
    div.singleitemblock,
    h3,
    div#section_instructions,
    div.multi-question,
    i.fa,
    i.fa + span,
    div#course_overview > div > div,
    div.content,
    div.editor-column > div > div p,
    textarea.response,
    li,
    #hidden-stage {
        color: white;
    }
    div.heading > h3,
    div.smalltext,
    table:not(.maximized-packet) > tbody > tr > td,
    td > a > div > i.fa + div,
    .droplet-palette-group-header-selected {
        color: white !important;
    }
    a.uitest-ProgressPill > div {
        background-color: #0ebe0e !important;
    }
    div.user-stats-block > div > div > table > thead > tr,
    td > a > div,
    *[style*="color: rgb(91, 103, 112);"],
    div.netsim-tabs h1,
    .dns-notes textarea,
    div.editor-column > div > div,
    .droplet-palette-group-header:not(.droplet-palette-group-header-selected) {
        color: #d0edf7 !important;
    }
    div.user-stats-block > div > div > table > tbody > tr,
    #designWorkspaceBody input/*app lab design mode*/
    {
        color: #33555E !important;
    }
    g.viz-wire {
        stroke: #d0edf7 !important;
    }
    /*code editor theme, taken from https://github.com/ajaxorg/ace/blob/master/lib/ace/theme/solarized_dark.css*/
    .ace-chrome .ace_gutter {
        background: #01313f;
        color: #d0edf7
    }
    .ace-chrome .ace_print-margin {
        width: 1px;
        background: #33555E
    }
    .ace-chrome {
        background-color: #002B36;
        color: #93A1A1
    }
    .ace-chrome .ace_entity.ace_other.ace_attribute-name,
    .ace-chrome .ace_storage {
        color: #93A1A1
    }
    .ace-chrome .ace_cursor,
    .ace-chrome .ace_string.ace_regexp {
        color: #D30102
    }
    .ace-chrome .ace_marker-layer .ace_active-line,
    .ace-chrome .ace_marker-layer .ace_selection {
        background: rgba(255, 255, 255, 0.1)
    }
    .ace-chrome.ace_multiselect .ace_selection.ace_start {
        box-shadow: 0 0 3px 0px #002B36;
    }
    .ace-chrome .ace_marker-layer .ace_step {
        background: rgb(102, 82, 0)
    }
    .ace-chrome .ace_marker-layer .ace_bracket {
        margin: -1px 0 0 -1px;
        border: 1px solid rgba(147, 161, 161, 0.50)
    }
    .ace-chrome .ace_gutter-active-line {
        background-color: #0d3440
    }
    .ace-chrome .ace_marker-layer .ace_selected-word {
        border: 1px solid #073642
    }
    .ace-chrome .ace_invisible {
        color: rgba(147, 161, 161, 0.50)
    }
    .ace-chrome .ace_keyword,
    .ace-chrome .ace_meta,
    .ace-chrome .ace_support.ace_class,
    .ace-chrome .ace_support.ace_type {
        color: #859900
    }
    .ace-chrome .ace_constant.ace_character,
    .ace-chrome .ace_constant.ace_other {
        color: #CB4B16
    }
    .ace-chrome .ace_constant.ace_language {
        color: #B58900
    }
    .ace-chrome .ace_constant.ace_numeric {
        color: #D33682
    }
    .ace-chrome .ace_fold {
        background-color: #268BD2;
        border-color: #93A1A1
    }
    .ace-chrome .ace_entity.ace_name.ace_function,
    .ace-chrome .ace_entity.ace_name.ace_tag,
    .ace-chrome .ace_support.ace_function,
    .ace-chrome .ace_variable,
    .ace-chrome .ace_variable.ace_language {
        color: #268BD2
    }
    .ace-chrome .ace_string,
    .item-label,
    .item-mark/*multiple choice selection dots and letters*/
    {
        color: #2AA198
    }
    .ace-chrome .ace_comment {
        font-style: italic;
        color: #657B83
    }
    .ace-chrome .ace_indent-guide {
        background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAAEklEQVQImWNg0Db1ZVCxc/sPAAd4AlUHlLenAAAAAElFTkSuQmCC) right repeat-y
    }
}
