css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("fr.org"), regexp(".*online\.anidub\.com/.*")

{
body,
html
{
    background-image: url(https://pp.userapi.com/c845217/v845217814/eef53/83XU474Dpqg.jpg) !important;
    background-color: #010101 !important;
    background-attachment: fixed !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-size: cover !important;
    /* font-family: Century Gothic!important */

}

#slideout > div > img
{
    opacity: .0 !important
}

#slideout
{
    background: rgba(0, 0, 0, .05)url(http://i.imgur.com/YOn3L.gif)!important;
    background-size: contain !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
}

.searchform
{
    background: rgba(0, 0, 0, 0.7)!important;
    box-shadow: 0px 0px 3px rgba(204, 204, 204, .9)inset !important
}

.s_and_s .twitter
{
    background: url(https://i.imgur.com/r0anqQC.png) no-repeat scroll transparent !important;
    background-size: contain !important;
    background-position: right !important
}

.s_and_s
{
    background: rgba(0, 0, 0, 0.87)!important
}

.s_and_s .speedbar,
.s_and_s .search,
.searchform .submit input
{
    background: transparent !important;
    color: #111111 !important
}

.twit
{
    height: 61px !important;
    width: 140px !important;
}

.s_and_s
{
    border-radius: 25px 25px 0px 0px !important
}

div.lblockcon:nth-child(9) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1),
#lcol > div:nth-child(6) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1)
{
    opacity: .1 !important;
    transform: scale(0.2) !important;
    transition: all 4s ease-in-out !important
}

div.lblockcon:nth-child(9) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1):hover,
#lcol > div:nth-child(6) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1):hover
{
    opacity: .85 !important;
    transform: scale(1.02) !important;
    transition: all 4s ease-in-out !important
}

.content,
.player,
ul.tabs li.active,
#feedHeader,
#feedBody,
#feedError
{
    background: rgba(0, 0, 0, 0.5)!important
}

.na_inp,
.bbcodes,
.askButs
{
    background: rgba(0, 0, 0, 0.5)!important;
    color: #222 !important
}

.editor textarea
{background-color:#999}

#slideout_inner
{
    background: rgba(0, 0, 0, 0.9)!important;
    color: #ccc !important
}

.lblockh ead,
.lblockhead
{
    background: rgba(35, 35, 35, 0.3)!important;
    border-radius: 25px 25px 0px 0px !important
}

.lblockcon
{
    background: rgba(35, 35, 35, 0.05)!important
}

.rate_view,
.day-current
{
    background: rgba(35, 35, 35, 0.8)!important;
    color: #ccc !important
}

.l_col1 .dcont .dcont,
.contd .submenu,
.ask_author
{
    background: rgba(35, 35, 35, 0.4)!important;
    color: #ccc !important;
    border-radius: 0px 0px 25px 0px !important
}

.l_col1 .headcond
{
    background: rgba(35, 35, 35, 0.6)!important;
    color: #ccc !important;
    border-radius: 25px 0px 0px 0px !important
}

.color3_bg
{
    background-color: #00ff00 !important
}

.related .relback,
.info,
th.workday,
th.weekday,
#newsupdate .day-active-v,
.day-active,
.bbcodes:hover
{
    background: rgba(35, 35, 35, 0.19)!important;
    color: #fff !important;
    border-radius: 25px 0px 25px 0px !important
}

scriptcode,
.title_quote,
.quote,
.title_spoiler,
.text_spoiler
{
    background: rgba(35, 35, 35, 0.19)!important;
    border-radius: 10px !important
}

.navigation span,
.navigation a:hover
{
    background: rgba(35, 35, 35, 0.29)!important
}

.navigation a,
ul.tabs li,
#sel,
#sel2
{
    background: rgba(0, 0, 0, 0.5)!important
}

.nyatext,
.leftinput .password,
.leftinput .name,
#sel,
#sel2
{
    background: rgba(0, 0, 0, .0)!important;
    box-shadow: 0px 0px 3px rgba(35, 35, 35, .9)inset !important
}

.loginhead,
.sliderbox,
.contd .submenu li a:hover
{
    background: rgba(0, 0, 0, 0.7)!important;
    box-shadow: 0px 0px 8px rgba(35, 35, 35, .9)inset !important;
    border-radius: 25px 0px 25px 0px !important;
    color: #fff !important
}

.sliderbox2,
.foothead
{
    background: rgba(0, 0, 0, 0.9)!important;
    border-radius: 0px 0px 0px 0px !important
}

.footblack
{
    background: rgba(0, 0, 0, 0.9)!important
}

.theadmenu
{
    background: rgba(0, 0, 0, 0.7)!important;
    box-shadow: 0px 0px 8px rgba(35, 35, 35, .9)inset !important;
    border-radius: 30px !important;
    color: #fff !important
}

.newsmore a,
.askButs:hover
{
    background: rgba(0, 0, 0, 0.7)!important;
    box-shadow: 0px 0px 8px rgba(35, 35, 35, .9)inset !important;
    border-radius: 15px 0px 15px 0px !important
}

.contd ul b a:hover,
.contd ul b span:hover
{
    background: rgba(0, 0, 0, 0.7)!important;
    box-shadow: 0px 0px 8px rgba(35, 35, 35, .9)inset !important;
    border-radius: 25px 0px 25px 0px !important;
    color: #fff !important;
    text-shadow: none !important;
}

.theadmenu ul#thmenu li a strong:hover
{
    background: rgba(0, 0, 0, 0.8)!important;
    box-shadow: 0px 0px 8px rgba(35, 35, 35, .9)inset !important;
    color: #fff !important;
    transition: all 2s cubic-bezier(0.1, 0.8, 0.5, 2), color 2s 1s linear!important
}

.iconhead li
{
    background: rgba(0, 0, 0, 0.7)!important;
    box-shadow: 0px 0px 6px rgba(35, 35, 35, .9)inset !important;
    border-radius: 25px 0px 25px 0px !important;
}

div.news_short > div > div > div > a > img
{
    box-shadow: 0 0 400px rgba(200, 200, 200, .5) !important;
}

.comm_cloud:after
{
    border-color: transparent transparent transparent rgba(35, 35, 35, 0.9)
}

.background,
.loginhead .linksin .borderr,
.r_col .bannertop,
.r_col .navi,
.navibut,
.r_col .newstitle,
.r_col .newsinfo,
.r_col .news_short,
.r_col .news_short .newsfoot,
l_col1 .dcont .dcont,
.l_col1 .dcont,
.contd ul b a,
.contd ul b span,
.r_col .bannerbot,
.r_col .bannerbb,
.footer,
.topbord,
.rell,
ul.relink li a,
.brelated,
.infob,
.comm_str,
.commleft,
.commi,
.comm_title,
.comm_inf,
.comm_text,
ul.inftext,
.comm_strr,
.comm_cloud_user,
.r_col .newsinfo .str,
.contd .submenu li a,
.basecont,
.commform,
.mass_comments_action,
.askButs a
{
    background: transparent !important
}

.r_col .news_short .news,
div.str,
.nyatext,
.news_full,
.tags,
#animeService,
scriptcode,
.title_quote,
.quote,
.title_spoiler,
.text_spoiler,
.na_inp,
.bbcont,
#feedHeader,
#feedBody,
#feedError,
h1,
ul.relink li a,
#sel,
#sel2
{
    border: none !important
}

.comm_cloud,
.bbcodes
{
    border-color: #CCCCCC !important
}

.lblockhead,
.newsmore a:hover,
.sxlist a:hover,
.lblockhead,
ul.tabs li.active,
.news_full,
.comm_title a,
ul.inftext li,
.comm_title .comm_id,
.nficon li,
.tags div,
.comm_cloud,
.comm_best li:first-child .comm_cloud,
.comm_best li:last-child .comm_cloud,
.contd .submenu li a,
.s_and_s .speedbar a:hover,
h1,
#feedBody,
#feedError,
a:hover,

.r_col .news_short .news,
.infocon
{
    color: #CCCCCC !important;
    text-decoration: none !important
}


.theadmenu ul#thmenu li a strong,
.leftinput .name,
.loginhead .linksin .ll,
.r_col .newsinfo,
.newsmore a,
html,
body,
.box2 .item span.tt,
.contd ul b a,
.contd ul b span,
.sxlist a,
.textanime,
.comm_title a:hover,
.s_and_s .speedbar a,
.searchform .ifield input,
#feedHeader,
h2,
a,
#sel2,
.regist_nya .ll
{
    color: #606060 !important;
    text-decoration: none !important;
    text-shadow: none !important;
}

.r_col .newstitle .title a
{
    color: #000 !important;
    text-decoration: none !important;
    text-shadow: 0px 0px 14px rgba(255, 255, 255, 1) !important;
}

.footblack,
#feedContent .lastUpdated,
.ask_info
{
    color: #404040 !important;
    text-decoration: none !important
}

.rightrss div
{padding-right: 42px !important}

.soc_link > a:nth-child(1) > img:nth-child(1),
.soc_link > a:nth-child(2) > img:nth-child(1)
{opacity: 1.0 !important}

.relcont > img:nth-child(1),
.itags > img:nth-child(1)
{opacity: .0 !important}

.commcont
{opacity: .4 !important}

.help_soc
{opacity: .4 !important}

/* Может быть потом я сделаю синюю кнопку "Наверх", но не сегодня
.scrollTop
{
    background: url(http://i3.beon.ru/59/13/1291359/40/44111540/fbea979bd01f.png) no-repeat scroll 0px 0px transparent;
    background-size: contain !important;
    transform: scale(2, 2) !important;
}

.scrollTop:hover
{
    background: url(http://i3.beon.ru/59/13/1291359/40/44111540/fbea979bd01f.png) no-repeat scroll 0px 0px transparent;
    background-size: contain !important;
    transform: scale(2.5, 2.5) !important;
}
*/

.comm_best li:first-child .comm_cloud,
.comm_best li:last-child .comm_cloud
{
    background-color: #000002;
    opacity: 0.8;
    background-size: contain !important;
    background-position: right !important
}

.comm_cloud
{
    background-color: #000200;
    opacity: 0.8;
    background-size: contain !important;
    background-position: left !important
}

.infocon
{
    background: url(http://png-3.findicons.com/files/icons/2198/dark_glass/128/presence_offline.png) no-repeat scroll 10px 9px transparent !important;
    background-size: contain !important
}

.nyaregok
{
    background: url(http://2.bp.blogspot.com/-Ew1vhA5Rkmw/UR48xahVMnI/AAAAAAAAAIk/oBI-zAXCOJ0/s1600/82765png32kzkzgaara-colle.png) no-repeat scroll 0% 0% transparent !important;
    background-size: contain !important
}

.nficon .icomm
{
    background-size: contain !important
}


.soc_link
{
    background: url(http://s018.radikal.ru/i506/1504/13/186356c5d2fe.png) no-repeat scroll 0% 0% transparent !important;
    background-size: contain !important
}

.related
{
    background: url(http://www.udomporntours.co.th/images/recommend.png) no-repeat scroll 0% 0% transparent !important;
    background-size: contain !important;
    background-position: right !important;
    z-index: 500 !important;
}

span.itags
{
    background: url(http://www.iconsearch.ru/uploads/icons/softwaredemo/128x128/shield_red.png) no-repeat scroll 0% 0% transparent !important;
    background-size: contain !important
}

}
