css:
@-moz-document regexp("https://www.gaiaonline.com/forum/.*") {
body {
    color: #aaa;
}

#content-padding,
#gaia_content,
#forum-modtools,
#thread_leaderboard_ad,
#thread_header,
#gaia_footer,
#content #like_count,
.post .forum_userstatus,
#gaia_content .subforums,
table.forum-list tbody tr.rowon,
table.forum-list tbody tr.rowoff,
#thread_poll,
#gaia_content #c7,
#gaia_content.grid_billie_holiday,
#gaia_content.grid_billie_holiday #menu_shadow,
#gaia_content #bd,
#gaia_content #c27 #bottom_separator,
#gaia_content #c12 #bottom_separator,
#gaia_content #c6 #bottom_separator,
#gaia_content #c7 #bottom_separator,
#bd .main .bd,
#gaia_content .gaia-info.alt .bd,
#gaia_content input,
#gaia_content select,
#gaia_content textarea,
#gaia_content #c26
{
    background: #333;
}


#avatar_menu,
input.det-forum-pager,
input.det-forum-pager-active,
div.postcontent .quote .quoted
{
    background: #454545;
}

#avatar_menu a:hover
{
    background: #777;
}

/*
 * Stuff to make color white
 */
a,
a:link,
a:visited,
#gaia_footer,
#gaia_footer a,
#avatar_menu a,
.post-meta,
.subforums .forum_desc,
.subforums .bd h5,
#gaia_content a:link,
#gaia_content a:hover,
#gaia_content a:visited,
#gaia_content #bd a,
#gaia_modal #bd a,
#gaia_content #forum-stats #safety_links a
{
    color: #fefefe;
}

/*
 * stubborn elements
 */
.linklist a,
.rowon a,
.rowoff a,
#poll,
.user_name
{
    color: #fefefe !important;
}

/*
 * Force inverted colors white
 */
#poll
{
    color: #000 !important;
}

/*
 * Pagination page jumper
 */
input.det-forum-pager, 
input.det-forum-pager-active,
#forum_hd_topic_pagelinks input
{
    color: #fefefe;
    border: 1px solid #999;
    padding: 5px;
    width: 62px;
}

/*
 * Stuff with borders
 */
#content #like_count,
.post .forum_userstatus,
#gaia_content .subforums,
#gaia_content input,
#gaia_content select,
#gaia_content textarea
{
    border-color: #999;
}

/*
 * Thread voting stuff
 */
#content .like_dislike .score,
.like_dislike .score
{
    color: #999 !important;
}

/*
 * Thread poll stuff
 */
.graph {
    filter: grayscale(100%) contrast(.5) invert(1);
}

/*
 * Thread speech bubbles
 */
/*
.avi-speech,
.speech_bubble {
    filter: grayscale(100%) contrast(.59) invert(1);
}
*/
.speech_bubble.say .top-left,
.speech_bubble.say .top-right,
.speech_bubble.say .bottom-left,
.speech_bubble.say .bottom-right,
.speech_bubble.say .tail,
.speech_bubble.whisper .top-left,
.speech_bubble.whisper .top-right,
.speech_bubble.whisper .bottom-left,
.speech_bubble.whisper .bottom-right,
.speech_bubble.think .top-left,
.speech_bubble.think .top-right,
.speech_bubble.think .bottom-left,
.speech_bubble.think .bottom-right,
.speech_bubble.think .tail,
.speech_bubble.think .border-rt,
.ornate .avi-speech-rtc,
.ornate .avi-speech-ltc,
.ornate .avi-speech-rbc,
.ornate .avi-speech-lbc,
.ornate .avi-speech-lt,
.ornate .avi-speech-rt,
.document .avi-speech-hd,
.avi-speech-ltc,
.avi-speech-lbc,
.avi-speech-rtc,
.avi-speech-rbc,
.avi-speech-ft,
.avi-speech-lt,
.avi-speech-rt,
.avi-speech-hd,
.avi-speech-tail
{
    filter: grayscale(100%) contrast(.59) invert(1);
}

.speech_bubble.think .content,
.speech_bubble.whisper .content,
.speech_bubble.say .content,
.messagecontent .avi-speech-bd
{
    background-color: #333;
}

.chain .avi-speech-bd
{
    background: #444;
}

/*
 * Post action stuff
 */
.post .genRandomNumber, .post .rollDice {
    filter: grayscale(100%) contrast(.5) invert(1);
    color: #000;
}

/*
 * Main forum header stuff
 */
#forum-header h2,
#gaia_content table.forum-list tfoot td.corner_bl, 
table.forum-list tfoot td.corner_bl,
#gaia_content .subforums .hd h3
{
    filter: grayscale(100%) contrast(.8) invert(1);
}


#forum-header .icon,
#forum-header h2 {
    filter: grayscale(100%) contrast(.6) invert(1);
}

.subforums .rc_top_left, 
.subforums .rc_top_right, 
.subforums .rc_bottom_left, 
.subforums .rc_bottom_right {
    height: 0;
}

/*
 * post signatures
 */
.user-sig img {
    filter: contrast(0.8);
}

/*
 * Thread promotion stuff
 */
.forum-list .row_sparkle,
.forum-list .row_sparkle:hover,
.forum-list .row_alert,
.forum-list .row_alert:hover,
.forum-list .row_gold_pg,
.forum-list .row_gold_pg:hover
{
    background: transparent;
}


.forum-list .alert,
.forum-list .alert a,
.forum-list .sparkle,
.forum-list .sparkle a,
.forum-list .gold,
.forum-list .gold a
{
    color: #454545 !important;
}

.forum-list .alert,
.forum-list .alert:hover,
.forum-list .sparkle,
.forum-list .sparkle:hover,
.forum-list .gold,
.forum-list .gold:hover
{
    border: 0;
}

.forum-list .alert {
    background: linear-gradient(#ffebd8, #ffbfab) !important;
}

.forum-list .sparkle {
    background: linear-gradient(#e5faff, #93e2fb) !important;
}

.forum-list .gold {
    background: linear-gradient(#fff3a7, #f1c25a) !important;
}
}
