css:
@-moz-document domain("rbx-forum.com") {
/*OBC Theme | Created by: BladefuryDev*/
/*--------------------------------------------------------------------------*/
    
/*Main Background*/
body {
    background-color: #343434;
    margin: 0;
}

/*Int Background*/
body, .text, pre {
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif;
    color: #343434;
    font-size: 14px;
    line-height: 1.428;
}

#Body {
    min-height: 550px;
    padding: 5px 0 25px 0;
    margin: 0 auto;
    width: 970px;
    background-color: #343434;
}

/*Top Nav Buttons*/
#forum-nav a.menuTextLink {
    text-decoration: none;
    font-weight: 600;
    color: #d8993b;
}

/*Top Forum Nav*/
a.linkSmallBold:visited, a.linkMenuSink:visited {
    font-weight: normal;
    color: #d88c37;
}

/*Forum Header*/
body .forum-table-header, body .forum-table-header th, body .forum-table-footer {
    background-color: #d88c37;
}

/*Forum/Thread Title Content*/
.forum-content-background, .forum-table-row {
    background-color: #565656;
}

/*Global Text*/
.normalTextSmall, .normalTextSmallBold, .normalTextSmaller {
    color: #ccc;
}

.forumTitle {
    font-size: 1.0em;
    font-weight: bold;
    color: #ccc;
}

/*Thread Title Text*/
a.post-list-subject, a.post-list-subject:hover, a.post-list-author, a.post-list-author:hover {
    text-decoration: none;
    color: #d88c37;
}

/*Visited Link*/
a:visited {
    color: #d88c37;
}

/*Link*/
a:link, a:visited, a:active {
    color: #d88c37;
}

/*New Thread*/
h1, h1 a, h1 a:visited, h1 a:active, h1 a:link {
    font-size: 32px;
    font-weight: bold;
    margin: 12px;
    color: #d88c37;
    letter-spacing: -1px;
}

h3, h3 a {
    font-size: 15px;
    font-weight: bold;
    color: #ccc;
}

body, .text, pre {
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif;
    color: #ccc;
    font-size: 14px;
    line-height: 1.428;
}

/*Table Color Corrections*/
.table-header .first {
    border-left: 1px solid #d88c37;
}

.table-header th {
    /*border-left: 0px solid #d88c37;*/
    font-weight: 600;
    background-color: #d88c37;
    border-top: none;
}

.table td, .table th {
    padding: 5px;
    border-top: 1px solid #565656;
    margin: 0;
}

td.flatViewSpacing {
    height: 1px;
    background-color: #343434;
}

td, th {
    /* border: 1px solid #343434; */
}

/*tr:nth-child(even) {
    background-color: #565656;
}*/

h2, h2 a:link, h2 a:visited, h2 a:active, h2 .text {
    font-size: 30px;
    font-weight: normal;
    color: #d88c37;
    letter-spacing: -1px;
    text-decoration: none;
}

.table-header th {
    border-left: 1px solid #ccc;
    font-weight: 600;
    background-color: #d88c37;
    border-top: none;
}
}
