css:
@-moz-document url-prefix("https://forum.cheatengine.org/") {
/* i really want this to be global */
/* Made By xAlone#9124 */

.bodyline {
    background-color: transparent;
    border: 1px #00000000 solid;
}

body {
    background-color: #292626;
}

.maintitle {
    color: #e6e6e6;
}

.gen,
.genmed,
.gensmall {
    color: #dadada;
}

td.row1 {
    background-color: #232222;
}

td.row2 {
    background-color: #1f1e1e;
}

td.rowpic {
    background-color: #1f1e1e;
}

td.cat,
td.catHead,
td.catSides,
td.catLeft,
td.catRight,
td.catBottom {
    background-color: #232222;
    border: #ffffff00;
}

.forumline {
    background-color: #232323;
    border: 2px #00000000 solid;
}

th.thHead,
th.thSides,
th.thTop,
th.thLeft,
th.thRight,
th.thBottom,
th.thCornerL,
th.thCornerR {
    font-weight: bold;
    border: #10101000;
    border-style: solid;
    height: 28px;
}

th {
    color: #ffffff;
}

.postdetails {
    font-size: 10px;
    color: #e6e6e6;
}

td.row3 {
    background-color: #1f1f1f;
}

td.row3Right,
td.spaceRow {
    background-color: #1f1e1e;
}

span.postbody {
    color: #dcdcdc;
}

.name {
    color: #00abff;
}

.nav {
    color: #00b6ff;
}

input,
textarea,
select {
    color: #57b8ff;
    border-color: #00000000;
}

input.post,
textarea.post,
select {
    background-color: #171717;
}

input.liteoption {
    background-color: #1b1b1b;
    font-weight: normal;
}

.topictitle,
h1,
h2 {
    font-weight: bold;
    font-size: 11px;
    color: #ffffff;
}

.quote {
    color: #2091f5;
    background-color: #1d1b1b;
    border: #8e8e8e00;
}

td.attachheader {
    border-color: transparent;
    background-color: #0c0c0c;
}

table.attachtable {
    border-color: #0062ff00;
}

td.attachrow {
    font: normal 11px Verdana, Arial, Helvetica, sans-serif;
    color: #696969;
    border-color: #0062ff00;
}

/* Code tab fix */ 
 .code {
    color: #006600;
    background-color: #2d292900;
    border: #8e8e8e00;
}
}
