css:
@-moz-document domain("n.maps.yandex.ru"), domain("mapeditor.yandex.com"), domain("mapeditor.yandex.com.tr") {
/* ===== Dark Theme for Yandex Map Editor ===== */

@keyframes nk-list-item-view_flashing{20%{background-color:hsl(48,50%,25%)}40%{background-color:hsl(48,50%,35%)}60%{background-color:hsl(48,50%,25%)}}
@keyframes nk-button_theme_air_view_action_focused_hard{0%,to{background-color:#222}40%,60%{background-color:hsl(47deg 23% 32%)}}
@keyframes nk-button_theme_air_view_feedback_focused_hard{0%,to{background-color:#0080ff}40%,60%{background-color:#4da6ff}}
@keyframes nk-button_theme_islands_view_action_focused_hard{0%,to{background-color:hsla(0,0%,100%,.078)}40%,60%{background-color:hsla(48,100%,75%,.3)}}
@keyframes nk-button_theme_islands_view_feedback_focused_hard{0%,to{background-color:#0080ff}40%,60%{background-color:#4da6ff}}
@keyframes nk-button_theme_islands_view_secondary_focused_hard{0%,to{background-color:var(--button_theme_islands_view_secondary_pressed--bg-color)}40%,60%{background-color:var(--button_theme_islands_view_secondary--bg-color)}}

.nk-page.nk-page_color-scheme_auto, .nk-page.nk-page_color-scheme_light, .nk-page.nk-page_color-scheme_dark {
    --page--bg-color: #222;
    --page--font-color: #eee;
    --icon--color: #ccc;
    --icon_hovered--color: #eee;
	--icon_checked--color: #ffe680;
    --icon_disabled--color: #666;
	--icon_checked_hovered--color: hsl(48,100%,85%);
    --icon_dark--color: #eee;
    --control_theme_islands--bg-color: var(--page--bg-color);
    --control_theme_islands--font-color: var(--page--font-color);
    --control_theme_islands--border-color: hsla(0,0%,100%,.3);
    --control_theme_islands_hovered--border-color: hsla(0,0%,100%,.5);
	--control_theme_islands_vandalism--bg-color: hsl(0,20%,20%);
	--control_theme_islands_vandalism--border-color: hsla(0,100%,67%,.6);
	--control_theme_islands_vandalism_hovered--border-color: hsla(0,100%,67%,.8);
    --control_theme_islands_disabled--border-color: rgba(0,0,0,.2);
    --control_theme_islands_invalid--border-color: #ff5757;
    --control_theme_islands_invalid--font-color: #ff5757;
    --control_theme_air--bg-color: var(--page--bg-color);
    --control_theme_air--font-color: var(--page--font-color);
    --button_theme_islands--bg-color: rgba(255,255,255,.078);
    --button_theme_islands_hovered--bg-color: rgba(255,255,255,.1);
    --button_theme_islands_pressed--bg-color: rgba(255,255,255,.078);
    --button_theme_islands_checked--bg-color: hsl(48,50%,25%);
    --button_theme_islands_checked--border-color: #ffe680;
    --button_theme_islands_checked--font-color: #ffe680;
    --button_theme_islands_checked_pressed--bg-color: hsl(48,100%,85%);
    --button_theme_islands_disabled--font-color: #888;
    --button_theme_islands_view_secondary--bg-color: #111;
    --button_theme_islands_view_secondary--font-color: #eee;
    --button_theme_islands_view_secondary_pressed--bg-color: #262626;
    --button_theme_islands_view_secondary_pressed--font-color: #fff;
    --button_theme_air_checked--bg-color: #222;
	--button_theme_air_checked--border-color: hsla(48,100%,75%,.8);
	--button_theme_air_checked_hovered--bg-color: #282828;
	--button_theme_air_checked_hovered--border-color: hsl(48,100%,85%,.8);
	--button_theme_air_checked--font-color: #ffe680;
    --input_theme_islands__clear--color: #eee;
    --input_theme_islands_disabled--bg-color: rgba(0,0,0,.2);
    --input_theme_islands_disabled--font-color: #666;
    --input_theme_islands_disabled__placeholder--font-color: #333;
    --input_theme_air__clear--color: #eee;
    --file-input_theme_islands_invalid--font-color: #ff5757;
    --checkbox_theme_islands__tick--color: #eee;
    --menu_theme_islands__group--border-color: #444;
    --menu-item_theme_islands_hovered--bg-color: #333;
    --suggest_theme_islands__group--border-color: #666;
    --suggest_theme_islands__item_hovered--bg-color: #333;
    --link_theme_islands--font-color: #7abdff;
    --link_theme_islands_hovered--font-color: #ff7070;
    --link_theme_islands_view_action--font-color: #a2abd8;
    --link_theme_islands_view_text_hovered--font-color: #ff7070;
}
.nk-page.nk-page_color-scheme_auto, .nk-page.nk-page_color-scheme_light, .nk-page.nk-page_color-scheme_dark {
    --app-page__app-name__color: #eee;
    --unauthenticated-app--bg-color: #333;
    --unauthenticated-app__logo--color: #bbb;
    --island--bg-color: var(--page--bg-color);
    --scrollable--bg-color: var(--page--bg-color);
    --scrollable__gradient-to--bg-color: rgba(51,51,51,0);
    --section--border-color: #444;
    --tabs-bar--bg-color: #333;
    --tabs-bar__bar_selected--font-color: var(--page--font-color);
    --tabs-bar__selection--color: var(--tabs-bar__bar_selected--font-color);
    --link-panel--bg-color: #333;
    --link-panel__title--font-color: #a2abd8;
    --list-item_hovered--bg-color: #333;
    --list-item_expanded--bg-color: #333;
    --list-item_expanded--border-color: #444;
    --list-item__progress--bg-color: rgba(0,0,0,.3);
    --chat-widget--bg-color: var(--page--bg-color);
    --chat-widget--font-color: var(--page--font-color);
    --map-zoom-control__separator--color: #444;
    --map-layers-control__layer--font-color: #888;
    --map-layers-control__layer_checked--font-color: var(--page--font-color);
    --map-region__path--font-color: #eee;
    --map-type-control--border-color: var(--page--bg-color);
    --map-sat-image-date--bg-color: rgba(0,0,0,.8);
    --map-sat-image-date--font-color: #eee;
    --map-coords--bg-color: rgba(0,0,0,.8);
    --map-coords--font-color: #eee;
    --sidebar-control__label--font-color: #bbb;
    --user-tasks-control--border-color: var(--page--bg-color);
    --form-hint--bg-color: #111;
    --form-hint--font-color: #eee;
    --form-control__label--font-color: #bbb;
    --form-submit__progress-label--font-color: #eee;
    --nk-name-row-layout__name-type--font-color: #888;
    --table__row_hovered--bg-color: #333;
    --table__header--bg-color: #333;
    --table__header--border-color: #444;
    --table__header--font-color: #bbb;
    --object-add-comment-form--bg-color: #333;
    --add-commit-annotation-comment--bg-color: #333;
    --commit-annotation-comment_hovered--bg-color: #666;
    --user-name_colored--font-color: var(--page--font-color);
    --user-name_colored__first-letter--font-color: #ff5757;
    --user-link_colored_hovered--font-color: #ff5757;
    --user-link_disabled--font-color: var(--page--font-color);
    --share--bg-color: #333;
    --share--font-color: #eee;
    --comment-form--bg-color: #333;
    --confirmation_view_dark--bg-color: #333;
    --assessment-grade__grade-value_type_incorrect--font-color: #ff5757;
    --assessment-grade-unit-form--bg-color: #333;
    --tools-control__column--border-color: #444;
    --badge--font-color: #eee;
	--badge_color_red--bg-color: hsl(0, 50%, 45%);
	--badge_color_orange--bg-color: hsl(25, 50%, 45%);
	--badge_color_yellow--bg-color: hsl(48, 50%, 45%);
    --badge_color_green--bg-color: hsl(92, 50%, 35%);
	--notification_color: #eee;
	--notification_error--bg-color: hsla(0,50%,45%,.9);
	--notification_suggest--bg-color: hsla(48,50%,35%,.9);
	--notification_success--bg-color: hsla(92, 50%, 35%, .9);
    --tag--bg-color: hsl(48,40%,25%);
    --tag--border-color: hsl(48,40%,35%);
    --tag--font-color: #eee;
    --user-bar__user-icon--border-color: #222;
    --category-selector-groups__group-label--font-color: #eee;
    --category-selector-groups__group-label__hovered--font-color: #fff;
    --feedback-feed-item--bg-color: #333;
    --polyline-geometry-on-map-editor__marker--bg-color: rgba(0,0,0,.5);
    --ymaps-editor__vertex-menu--bg-color: var(--page--bg-color);
    --ymaps-editor__vertex-menu--font-color: var(--page--font-color);
    --ymaps-editor__vertex-menu__item_hovered--bg-color: var(--menu-item_theme_islands_hovered--bg-color);
    --ymaps-copyright--bg-color: rgba(0,0,0,.8);
    --ymaps-copyright--font-color: #eee;
    --ymaps-scaleline--bg-color: rgba(0,0,0,.8);
    --ymaps-scaleline--font-color: #eee;
    --indoor-bar__level_current--bg-color: #ffe680;
    --indoor-bar__close--bg-color: #262626;
    --onboarding--bg-color: var(--page--bg-color);
    --onboarding-page--font-color: #eee;
	--onboarding-page__h2--bg-color: #333;
}
.nk-page.nk-page_color-scheme_auto, .nk-page.nk-page_color-scheme_light, .nk-page.nk-page_color-scheme_dark {
    --geoobject-notice_blocked--bg-color: #333;
    --geoobject-notice_blocked--font-color: #eee;
    --geoobject-editor__onboarding-hint--bg-color: rgba(59,73,141,.7);
    --flat-ranges-viewer__label--font-color: #bbb;
    --flat-ranges-viewer__loader--bg-color: rgba(0,0,0,.3);
    --transport-routes-viewer__label--font-color: #bbb;
    --transport-routes-viewer__route_not-operating--bg-color: #444;
    --transport-routes-viewer__route_not-operating--border-color: #555;
    --transport-thread-stops-viewer__travel-time--font-color: #bbb;
    --transport-thread-time-editor__label--font-color: #bbb;
    --transport-thread-stop-editor__travel-time--font-color: #bbb;
    --business-info-viewer--bg-color: #111;
    --business-info-viewer__group-item-label--font-color: #bbb;
    --poi-conflicts-viewer--bg-color: #111;
	--poi-conflicts-viewer__geoobject-link_severity_critical--font-color: #ff5757;
}
.nk-page.nk-page_color-scheme_auto, .nk-page.nk-page_color-scheme_light, .nk-page.nk-page_color-scheme_dark {
    --user-profile__name-first-letter--font-color: #ff5757;
    --user-profile__group-title--font-color: #bbb;
    --user-profile__status--font-color: #ff5757;
	--user-profile__ban-info--bg-color: hsla(0,50%,35%);
    --user-profile__email_not-validated--font-color: #ff5757;
    --user-stat-ugc--bg-color: #333;
    --rating__list-headers--border-color: #555;
    --rating__list-item--border-color: #444;
    --rating__list-item_hovered--bg-color: #333;
	--ban-form--bg-color: #333;
}
.nk-page.nk-page_color-scheme_auto, .nk-page.nk-page_color-scheme_light, .nk-page.nk-page_color-scheme_dark {
    --moderation-regions__region_urgent__title--font-color: #ff5757;
    --moderation-regions__additional-links--bg-color: #1a1a1a;
    --moderation-regions__additional-links-title--font-color: #bfbfbf;
}

/* Mod & Yndx */
.nk-page.nk-page_color-scheme_auto, .nk-page.nk-page_color-scheme_light, .nk-page.nk-page_color-scheme_dark {
    --acl-filter__label--font-color: #666;
    --acl-policy_public--bg-color: hsl(92,40%,25%);
    --acl-policy_public--border-color: hsl(92deg 40% 45% / 90%);
    --acl-policy_public--font-color: #eee;
	--acl-user-permissions-tree-node__label-disabled--font-color: rgba(0,0,0,.4);
}
.nk-page.nk-page_color-scheme_auto, .nk-page.nk-page_color-scheme_light, .nk-page.nk-page_color-scheme_dark {
    --pois-conflicts-geoobject-conflicts__geoobject-conflicts--bg-color: hsl(48,30%,25%);
    --poi-conflicts-viewer__geoobject-conflicting-geoobjects_severity_critical--font-color: #ff5757;
    --road-marking-linear-primary-item--bg-color: hsl(48,30%,25%);
}

/* ===== Scroll ===== */
/*::-webkit-scrollbar {width: 12px; height: 12px;}
::-webkit-scrollbar-button {width: 0px; height: 0px;}
::-webkit-scrollbar-thumb {background: #666; border-radius: 6px; border: 2px solid #222;}
::-webkit-scrollbar-thumb:hover {background: #777;}
::-webkit-scrollbar-thumb:active {background: #999;}
::-webkit-scrollbar-track {background: #222;}
::-webkit-scrollbar-corner {background: #222;}*/
html {color-scheme: light dark;}

.nk-scrollable__scrollbar-thumb:before {background: #fff;}

/* ===== Page ===== */
.nk-app-view__map {background-image: url(https://yastatic.net/s3/front-maps-static/maps-front-nmaps/_/226e8dd865f88ece4ab6.png);}
/*[[mapbrightness]]*/

.nk-button_theme_air.nk-button_checked:before {box-shadow: 0 0 0 2px var(--button_theme_air_checked--border-color), 0 2px 3px 1px rgb(0 0 0 / 20%);}
.nk-button_theme_air.nk-button_checked.nk-button_hovered:before {box-shadow: 0 0 0 2px var(--button_theme_air_checked_hovered--border-color), 0 3px 4px 1px rgb(0 0 0 / 30%);}

ymaps[class$="copyright__fog"], ymaps[class$="copyright_fog_yes"]:after {background: var(--ymaps-copyright--bg-color); color: var(--page--font-color);}
ymaps a[class$="copyright__link"]:hover  {color: var(--link_theme_islands_hovered--font-color) !important;}

ymaps[class$="scaleline__label"]{background: var(--map-coords--bg-color); color: var(--map-coords--font-color);}

/* ===== Pop Up ===== */
.nk-popup_theme_islands {box-shadow: 0 0 0 1px rgba(255,255,255,.1), 0 10px 20px -5px rgba(0,0,0,.4);}

.nk-notification-view_type_error {background: var(--notification_error--bg-color); color: var(--notification_color);}
.nk-notification-view_type_suggest {background: var(--notification_suggest--bg-color); color: var(--notification_color);}
.nk-notification-view_type_success {background: var(--notification_success--bg-color); color: var(--notification_color);}

.nk-modal_theme_islands .nk-modal__content {border-color: var(--section--border-color);}

/* ===== Layers ===== */
.nk-map-layers-control-view__layer.nk-menu-item_hovered .nk-map-layers-control-view__layer-settings-switcher:after {background: var(--section--border-color);}

/* ===== Help ===== */
