/* ===== Moderation ===== */
.nk-moderation-task-view__actions-button_action_revert-vandalism::before {background: var(--control_theme_islands_vandalism--bg-color); border-color: var(--control_theme_islands_vandalism--border-color);}
.nk-button_theme_islands.nk-button_hovered.nk-moderation-task-view__actions-button_action_revert-vandalism::before {border-color: var(--control_theme_islands_vandalism_hovered--border-color);}

/* ===== User Profile ===== */
.nk-user-bar-view__name:first-letter{color: var(--user-profile__name-first-letter--font-color);}

.nk-user-profile-view__login, .nk-user-profile-view__registration-date {color: var(--nk-name-row-layout__name-type--font-color);}

.nk-user-profile-view__ban-info .nk-scrollable__container:before {background: linear-gradient(var(--user-profile__ban-info--bg-color),#0000);}
.nk-user-profile-view__ban-info-content {background-color: var(--user-profile__ban-info--bg-color);}
.nk-user-profile-view__ban-info .nk-scrollable__container:after {background: linear-gradient(#0000,var(--user-profile__ban-info--bg-color));}

.nk-ban-record-view__reason {color: var(--nk-name-row-layout__name-type--font-color);}

.nk-user-stat-total-view__name {color: var(--nk-name-row-layout__name-type--font-color);}
.nk-user-stat-category-group-view {border-color: var(--section--border-color);}
.nk-user-stat-category-group-view__category-group {border-color: var(--section--border-color);}
