/* ===== Edit Block Form ===== */
.nk-island {box-shadow: 0 0 0 1px rgba(255,255,255,.1), 0 3px 0 0 rgba(0,0,0,.05);}

.nk-section:not(:empty)~.nk-section_level_1:not(:empty) {border-top-width: 2px;}
.nk-section:not(:empty)~.nk-section_level_2:not(:empty):before {height: 2px;}

.nk-icon_colored {color: var(--link_theme_islands--font-color);}
.nk-sidebar-header-view__title-action circle[fill="#ccc"] {fill: var(--section--border-color);}

.nk-button_theme_islands:before {background: var(--button_theme_islands--bg-color); border-color: #0000; border-width: 2px; box-shadow: 0 2px rgb(0 0 0 / 10%);}
.nk-button_theme_islands.nk-button_hovered:before {background: var(--button_theme_islands_hovered--bg-color); border-color: #0000;}
.nk-button_theme_islands.nk-button_focused:before {border-color: #0000;}

.nk-checkbox .nk-button_theme_islands:before, .nk-radio .nk-button_theme_islands:before {right: 1px;}
.nk-button_theme_islands.nk-button_checked {color: var(--button_theme_islands_checked--font-color);}
.nk-button_theme_islands.nk-button_checked:before {background-color: var(--button_theme_islands--bg-color); border-color: #0000; box-shadow: 0 2px var(--button_theme_islands_checked--border-color);}
.nk-button_theme_islands.nk-button_checked.nk-button_hovered:before {background-color: var(--button_theme_islands_hovered--bg-color);}

.nk-button_theme_islands.nk-button_view_action {color: var(--button_theme_islands_checked--font-color);}
.nk-button_theme_islands.nk-button_view_action:before {background: var(--button_theme_islands--bg-color); border: 2px solid var(--button_theme_islands_checked--border-color);}
.nk-button_theme_islands.nk-button_view_action.nk-button_hovered:before {background: var(--button_theme_islands_hovered--bg-color); border-color: var(--button_theme_islands_checked--border-color);}

.nk-text-input_theme_islands:before {border-width: 2px;}

.nk-boolean-text-control__value:before {color: var(--form-control__label--font-color);}

/* Speed */
.nk-link_theme_islands.nk-link_view_minor {color: var(--link_theme_islands_view_action--font-color);}
.nk-link_theme_islands.nk-link_view_minor:hover {color: var(--link_theme_islands_hovered--font-color);}

/* Checkbox*/
.nk-checkbox_theme_islands.nk-checkbox_checked .nk-checkbox__box:before, .nk-checkbox_theme_islands.nk-checkbox_checked_indeterminate .nk-checkbox__box:before {background: var(--button_theme_islands_checked--bg-color);}

/* Badge */
.nk-badge_color_red {background-color: var(--badge_color_red--bg-color);}
.nk-badge_color_orange {background-color: var(--badge_color_orange--bg-color);}
.nk-badge_color_yellow {background-color: var(--badge_color_yellow--bg-color); color: var(--badge--font-color);}

.nk-grid .nk-icon svg[width="22"][height="22"] circle[fill="#FF6464"] {fill: var(--badge_color_red--bg-color);}
.nk-grid .nk-icon svg[width="22"][height="22"] circle[fill="#FC5"] {fill: var(--badge_color_yellow--bg-color);}
.nk-grid .nk-icon svg[width="22"][height="22"] circle[fill="#8FCB69"] {fill: var(--badge_color_green--bg-color);}

/* Bus Thread */
.nk-transport-thread-schedule-row-edit-control__expand-control-tick {filter: invert(1);}

.nk-transport-thread-schedule-row-edit-control__expand-control_invalid {color: var(--control_theme_islands_invalid--font-color);}
.nk-transport-thread-schedule-row-items-edit-control__required:not(:hover) {color: var(--control_theme_islands_invalid--font-color);}

.nk-transport-thread-stops-editor-view__placeholder-label {color: var(--transport-thread-time-editor__label--font-color);}
.nk-transport-thread-stops-editor-view__placeholder::after {background: var(--button_theme_islands_checked--bg-color);}
.nk-transport-thread-stop-editor-view_selected .nk-transport-thread-stop-editor-view__title::before {background: var(--button_theme_islands_checked--bg-color);}
.nk-transport-thread-stop-editor-view_selected .nk-transport-thread-stop-editor-view__title::after {color: var(--button_theme_air_checked--font-color);}

/* Feefback*/
.nk-feedback-tasks-view__sub-header {color: var(--nk-name-row-layout__name-type--font-color);}
.nk-feedback-tasks-view__group-label_id_rejected {color: var(--page--font-color);}
.nk-feedback-tasks-view__task-date {color: var(--nk-name-row-layout__name-type--font-color);}

@media (max-width: 768px) {

.ya-chat-popup-ui {
    --yandex-mssngr-widget-popup-ui-fixed-offset-bottom: 26px;
    --yandex-mssngr-widget-popup-ui-fixed-offset-right: 8px;
}
.ya-chat-popup {
    --yandex-mssngr-widget-popup-width: 390px;
    --yandex-mssngr-widget-popup-height: 400px;
}

/* Header */
.nk-header-view {margin-top: 46px; min-width: 500px;}
.nk-header-view:before {height: 92px;}

.nk-logo-view {display: none;}
.nk-search-view {position: fixed; top: 0; width: 100%;}
.nk-search-view .nk-text-input_theme_air:before {border-radius: 0;}
.nk-search-view__suggest {max-width: unset;}

.nk-app-bar-view {border-radius: 19px; box-shadow: 0 2px 3px 1px rgb(0 0 0 / 20%); height: fit-content; width: unset;}
