css:
@-moz-document domain("toonator.com") {
body {
    font-family: CURSIVE;
    color: #9d4dd2;
    background-color: #111;
    font-size: 62.5%;
    margin: 0;
}
.toon_preview {
    background-color: #000;
    display: inline-block;
    zoom: 1;
    width: 210px;
    margin: 3px;
    text-align: center;
    padding: 5px 0 5px 0;
    -moz-border-radius: 6px;
    border-radius: 6px;
    position: relative;
}
a.nmenu {
    color: #7621ae;
    text-decoration: none;
}
a.nmenu:hover {
    color: #7621ae;
    text-decoration: underline;
}
.toon_preview .toon_name a,
.toon_preview .toon_name_top a {
    display: block;
    font-size: 12px;
    font-weight: bold;
    color: #9d4dd2;
    text-decoration: none;
}
a.nmenu.selected {
    color: #c5a0ff;
    font-weight: bold;
}
a.foreign {
    color: #c5a0ff !important;
}
a.anonymous {
    color: #a8a8a8 !important;
}
.red,
a.red {
    color: #c5a0ff !important;
    font-weight: bold;
}
.gray {
    background: #000;
}
.toon_preview {
    background-color: #000;
    display: inline-block;
    zoom: 1;
    width: 360;
    margin: 3px;
    text-align: center;
    padding: 5px 0 5px 0;
    -moz-border-radius: 6px;
    border-radius: 5px;
    position: relative;
    border: 0.7px solid #7621ae 
}
.large {
    width: 320px;
    margin: 0 0 10px 0;
    padding: 0 0 5px 0;
}
div.paginator ul li a:hover,
div.paginator ul li.current a {
    -moz-border-radius: 5px;
    border-radius: 5px;
    color: #c5a0ff;
    background: #000;
}
div.paginator ul li.current a {
    border: 1px solid #9d4dd2;
}
div.paginator ul li a {
    text-decoration: none;
    color: #9d4dd2;
    width: 35px;
    padding: 5px 0 2px 0;
    display: inline-block;
}
#header a {
    color: #9d4dd2;
    text-decoration: none;
}
img[src*="/img/toonator10.png"] {
    width: 0;
    height: 40px;
    padding-right: 150px;
    background: transparent url(http://i.imgur.com/mMAVHUC.png) top left no-repeat;
}


.sn .login-wrapper {
    position: relative;
    left: 50%;
    top: 50%;
    border: 1px solid #9d4dd2;
    width: 640px;
    -webkit-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
    background: #333333;
    border-radius: 10px;
    padding: 20px;
}

.sn label p,
.sn .label p {
    width: 250px;
    display: inline-block;
    color: #c5a0ff;
    text-align: left;
}

.sn .info-block {
    border-radius: 8px;
    background: #000000;
    padding: 20px;
    font: 12pt ToonatorFont;
}

a:link {
    color: #c5a0ff;
}

a:visited {
    color: #7621ae;
}

a:active {
    color: #c5a0ff;
}

a:hover {
    color: #c5a0ff;
}

.toon_comments a.new {
    float: right;
    text-decoration: none;
    color: #dddddd;
}

img.avatar {
    width: 80px;
    height: 40px;
    border: 1px solid #9d4dd2;
    border-radius: 5px;
}

#header a:hover {
    text-decoration: underline;
    color: #7621ae;
}

#toon_page .info .prize,
.rightpanel .prize {
    -moz-border-radius: 6px;
    border-radius: 6px;
    text-align: center;
    padding: 10px 0;
    margin: 2px 0;
    background: #000
}

#newmenu > li ul {
    position: absolute;
    top: 32px;
    right: 0;
    display: none;
    border: 1px solid #7621ae;
    background: #000000;
    z-index: 1000;
    margin: 0;
    padding: 0;
}

#newmenu li ul li a {
    display: block;
    padding: 0 8px;
    margin: 0;
    height: 24px;
    line-height: 24px;
    color: #9d4dd2;
    text-decoration: none;
    overflow: hidden;
    width: 150px;
}

.black {
    color: white;
    font-weight: bold;
}


#toon_page .info .buttons a:hover,
#toon_page .info .buttons a.active {
    color: white;
}

#toon_page .info .buttons a {
    text-decoration: none;
    color: #ffffff;
}

.toon_preview .toon_tagline a {
    color: #ffffff;
}

element.style {
    font: 8pt Tahoma;
    float: right;
    color: #5b5b5b;
}

img[src*="/img/new-assets.png"] {
    width: 0;
    height: 40px;
    padding-right: 150px;
    background: transparent url(http://i.imgur.com/mMAVHUC.png) top left no-repeat;
}

.m_last, .m_popular, .m_draw, .m_contest {
        border-radius: 6px
    
}

.m_contest {
    width: 105px
}

#newmenu #mnumessages > a, #newmenu #notify > a, #newmenu #spiders > a {
    border-radius: 6px
}

.reg_variant li {
    border: 1px solid #9d4dd2
}

.sn button.complete {
    background: #7621ae;
    border: 1px solid #9d4dd2;
    color: #c5a0ff
}

.sn h2 {
    border-bottom: 1px solid #7621ae
}

ul.leftmenu li a {
    color: #9d4dd2
}
.my-avatar{
    border: 1px solid #7621ae;
    border-radius: 6px
}
.sn .btn.info{
    border: 1px solid #7621ae;
    background:#9d4dd2;
    color: #c5a0ff
}

a.cite {
    color: #7621ae
}
}
