.island__article--small .block-title,
.latest-issue-spotlight__subheader,
.latest-issue-spotlight__subscription-header,
.mntl-sc-block-comparisonlist__heading,
.mntl-sc-block>ol>li,
.most-popular__article--medium .block-title,
.most-popular__article--small .block-title,
.most-popular__article--small--title-bold .block-title,
.news-latest__article .block-title,
.summary-list-item__superlative,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Sandy Brown __________________________________________________________*/

.commerce-sc-block-productrecord .star-rating:before,
.experts-spotlight__tagline,
.footer .social-follow-title,
.island__article-byline,
.mntl-byline__link,
.mntl-byline__text,
.mntl-sc-block-featuredlink.featuredon-link:before,
.mntl-sc-block-featuredlink.fullreview-link:before,
.news-latest__article .mntl-text-block,
h5,
h6 {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue ________________________________________________________________*/

.article-meta .updated-label,
.mntl-updated-stamp
{
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green _______________________________________________________________*/

p strong {
	color: #0f0!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

li strong {
	color: #9bff00 !important;
	font-weight: 700 !important;
	text-shadow: 1px 1px #000 !important;
}

/*___ Red _________________________________________________________________*/

li em {
	color: #f00!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________*/

.a-input-text,
.footer .mntl-newsletter-submit__input,
.footer .mntl-newsletter-submit__input::placeholder,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*________________________________________________________________________*/

::-moz-selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::-moz-selection {
	background: #ff9!important;
	color: #fff!important;
}

/*________________________________________________________________________*/

ppp {
	color: #71edd2;
}

.footer .logo .icon-logo,
.header .icon-logo use
{
    fill: #F0E890;
}



}

@-moz-document domain("lifewire.com") {
