css:
@-moz-document domain("pcpartpicker.com") {
:root {
    --bg0: #111;
    --bg1: #171717;
    --bg2: #1a1a1a;
    --bg3: #222;
    --bg4: #272727;
    --bg5: #2a2a2a;
    --bg6: #333;
	--colour: /*[[colour]]*/;
	--colourHover: /*[[colourHover]]*/;
}



.user-comments .details, .user-forum-topics .details, .about .details, .tos .details, .privacy .details, .disclosure .details, .individual-parts .details, .trends-index .details, .search-results .details, .partlist-by-merchant .details, .inbox .details, .part-compare .details, .system-build .details, .left-column, .details, .right-column, html, body {
    background: var(--bg0);
	border-color: var(--bg0);
}
.system-build .action-bar-wrapper, .system-build .part-list-action-bar, table.manual-zebra tr.zebra, .build-guides-hero, .ui-dialog {
	background: var(--bg1);
	border-color: var(--bg1);
}
nav, .image-gallery, table tbody tr:nth-child(2n+1), .create-btn, ul.list-toc li, .part-index .left-column .current-partlist .filter-list, .part-index .left-column .current-partlist .part-list, .est-wattage, .basic-table tbody tr:nth-child(2n+1) {
	background: var(--bg2);
	border-color: var(--bg2);
}
header, .blog-post, .featured-build, .image-triple-group, .search-bar input[type="text"], .search-bar textarea, .comment-edit-form textarea, .action-box input[type="text"], .action-box textarea, .ui-dialog-titlebar, table.merchant-preference-table input, table.merchant-preference-table textarea {
	background: var(--bg3);
	border-color: var(--bg3);
}
.primary-nav a:hover, .search-bar input[type="text"]:hover, .search-bar textarea:hover, .search-bar input[type="text"]:focus, .search-bar textarea:focus, .ui-dialog input[type="text"], .ui-dialog textarea, #partlist_save_existing_choice, ul.list-toc li:hover {
	background: var(--bg4);
	border-color: var(--bg4);
}
.blog-post .post-actions {
	background: var(--bg5);
	border-color: var(--bg5);
}
.system-build .part-list-action-bar .permalink-markup-social .markup-block .markup-buttons, .drop {
	background: var(--bg5);
}
.system-build .hero, .completed-builds .hero, .part-index .hero, .build .hero, .build-guide .hero, a.parts-tray-block {
	background: var(--bg6);
}
.btn-mds, .create-btn a, .ui-slider-horizontal .ui-slider-range, input[type="submit"], input[type="button"], .build-guides .btn-lgs, .btn-lgs {
	background: var(--colour);
}
.btn-mds:hover, .create-btn a:hover, input[type="submit"]:hover, input[type="button"]:hover, .build-guides .btn-lgs:hover, .btn-lgs:hover {
	background: var(--colourHover);
}









table tr {
	border: none !important;
}
table.manual-zebra tr.last, table thead th {
	border-color: var(--bg1);
}
.basic-table td {
    border-color: var(--bg2);
}
.system-build .part-list-action-bar .permalink-markup-social .markup-block .markup-buttons, .system-build .part-list-action-bar .permalink-markup-social .markup-block .markup-buttons .markup_bbcode, .system-build .part-list-action-bar .permalink-markup-social .markup-block .markup-buttons .markup_plaintext, .primary-nav a, .nav-wrapper, .sub-links, .build-list .build {
	border-color: var(--bg5);
}
.system-build .part-list-action-bar .wattage-actions .part-list-action, .user-nav li, .comment-item, .comment-data-row .comment-data, .part-table thead th, .obj-filters .obj-filter, .part-table thead .sorter-false:hover, .part-table thead .tablesorter-header:hover, .part-list .parts li, a.parts-tray-block {
	border-color: var(--bg6);
}
.tabnav-hero .page-container ul li a:hover, .tabnav-hero .page-container ul li a.active, .search-tray input#search_q {
	border-color: var(--colour);
}








.part-list-permalink input, .system-build .part-list-action-bar .wattage-actions .part-list-action {
	background: var(--bg4);
	box-shadow: none;
}














table th, .part-list .component-name a, body, .user-nav a, .blog-post .comments a, .featured-build .comments a, .featured-build .votes a, .build-list .build .comments, .build-list .build .votes, .comment-data .comment-username, table .tdname a, span.header-widget-right {
	color: #bbb;
}
.part-list-permalink, .markup-block, .compat-message, div.tickLabel, .blog-post .title a, .featured-build .title a, .build-list .build .title a, .hero h4, .hero p, .part-list .parts .name, table tr td:last-child, .part-list .part-list-compat-wattage .compat-message.compat-message-note span, input[value="Cancel"], ul.list-toc li a {
	color: #fff;
}
.primary-nav span, .build-list .build .title .price, a, .secondary-nav a:hover, .build-guides .title a:hover, .build-guides .guide-preview .title a:hover, .build-guides a.comments:hover, .build-guides a.votes:hover, .build-guides .guide .price, .featured-build .title a:hover, .build-guide .build-list .build .title .price, .blog-post .title a:hover, .user-nav a:hover, .customSelect.customSelectHover, .blog-post .comments a:hover {
	color: var(--colour);
}
.build-list .build .title .price:hover, a:hover, .part-list .parts a.name:hover {
	color: var(--colourHover)
}









/* RULES */
footer {
	display: none;
}
.system-build .part-list-action-bar .wattage-actions {
	border: none;
}
.single-action a:hover, .system-build .part-list-action-bar .wattage-actions .est-wattage a:hover {
	background: var(--bg6);
	color: #fff;
}
.system-build .part-list-action-bar .wattage-actions .est-wattage a {
	background: transparent url('https://i.imgur.com/93x4Mr0.png') no-repeat !important;
	background-size: 9px 12px !important;
	background-position: 17px 20px !important;
}
.system-build .part-list-action-bar .wattage-actions .est-wattage a:hover {
	background: var(--bg6) url('https://i.imgur.com/93x4Mr0.png') no-repeat !important;
	background-size: 9px 12px !important;
	background-position: 17px 20px !important;
}
.part-list .price-settings a.change-price span, .carousel-left-arrow, .carousel-right-arrow, .upvote, .downvote, .upvoted, .downvoted, .comment-vote-up, .comment-vote-down, .comment-voted-down, .comment-votearrow, .delete a, .ui-icon-closethick {
	filter: invert(1);
	opacity: 1;
}
.comment-voted-up {
	filter: brightness(300%) hue-rotate(-100deg);
}
.system-build .part-list-action-bar .permalink-markup-social .markup-block .markup-buttons li a img {
	filter: invert(83%) hue-rotate(180deg)
}
.nav-build:hover {
	background: var(--bg4) url('https://i.imgur.com/lGGqxfd.png') no-repeat !important;
	background-size: 22px 22px !important;
	background-position: 16px 18px !important;
}
.nav-guides:hover  {
	background: var(--bg4) url('https://i.imgur.com/EQIjVjv.png') no-repeat !important;
	background-size: 18px 24px !important;
	background-position: 16px 18px !important;
}
.nav-builds:hover {
	background: var(--bg4) url('https://i.imgur.com/QvkNVSU.png') no-repeat !important;
	background-size: 18px 24px !important;
	background-position: 16px 18px !important;
}
#browse-parts-button:hover {
	background: var(--bg4) url('https://i.imgur.com/ruohnk3.png') no-repeat !important;
	background-size: 22px 22px !important;
	background-position: 16px 18px !important;
}
.search a:hover {
	background: var(--bg4) url('https://i.imgur.com/0IILjcJ.png') no-repeat;
	background-size: 24px 24px;
	background-position: 50%;
}
.primary-nav a.active {
	background-color: var(--bg5);
}
h2 {
	border-bottom-color: var(--bg6);
}
.pcpp-header h1 {
	background: url('https://i.imgur.com/PpGsxaJ.png') no-repeat;
	background-size: 222px 48px;
}
.search-bar input[type="text"], .search-bar textarea, .comment-edit-form textarea, .action-box input[type="text"], .action-box textarea, .ui-dialog input[type="text"], .ui-dialog textarea, #partlist_save_existing_choice, table.merchant-preference-table input, table.merchant-preference-table textarea {
	box-shadow: none;
}
.action-box td {
	background: transparent;
	border-color: var(--bg4);
}
span.header-widget-right select, .search-bar select {
	color: #000;
}
.search a.active {
	background-color: var(--bg5);
}
.ui-dialog {
    border-radius: 0;
}


/* SETTINGS */
/*[[h-r-pk-pl-y-o-b-tl-g]]*/

}
