css:
@-moz-document domain("10fastfingers.com") {

body
  {
    background: #ffb7c5 !important;
  }

  #navbar-top-first a
  {
    padding-bottom: 8px;
    color: #000 !important;
  }

  a.navbar-brand
  {
    margin-top: 4px;
    margin-left: 11px;
    width: 123px;
    height: 39px;
    background-image: url('https://i.imgur.com/YxfVxYp.png') !important;
    background-size: 123px 39px;
  }

  #tagline
  {
    padding-top: 5px;
    color: #000 !important;
    margin-top: 15px;
    margin-left: 20px;
  }

  .top-first-bg
  {
    border-top: 1px solid #fff;
    background: url('https://i.imgur.com/PdvDKTQ.png') 0px 0px repeat-x #ffb7c5 !important;
    -webkit-background-size: cover !important;
    -moz-background-size: cover !important;
    -o-background-size: cover !important;
    background-size: cover;
    box-shadow: 0px 2px 1px 1px rgba(0, 0, 0, 0.2);
  }

  #speedtest-main #input-row
  {
    padding: 4px;
    background: #fde5ea !important;
  }

  #speedtest-main #words
  {
    padding: 6px 12px 30px 12px;
    margin-top: 0px;
    margin-bottom: 10px;
    height: 110px;
    overflow: hidden;
    color: #1d4851;
    font-family: Times New Roman, Times, serif;
    font-size: 2.2em;
    line-height: 1.6em;
    /*30px; line-height: 48px;*/
    background: #fde5ea !important;
    border: 2px solid #ffffff !important;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  }

  #speedtest-main #input-row #inputfield
  {
    border-top: 1px solid #ffffff !important;
    height: 50px;
    border-right: 1px solid #ffffff !important;
    border-bottom: 1px solid #ffffff !important;
    border-left: 1px solid #ffffff !important;
    font-size: 2.0em;
    line-height: 1.0em;
    background: url('https://i.imgur.com/PdvDKTQ.png') 0px -60px !important;
    font-family: Times New Roman, Times, serif;
    color: #000 !important;
  }

  element.style
  {
    display: block;
    color: #000 !important;
    padding-left: 10px;
    margin-top: 12px;
    margin-right: 10px;
  }

  #timer
  {
    padding-top: 10px;
    padding-bottom: 3px;
    height: 50px;
    text-align: center;
    font-size: 1.6em;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    color: #ff0071 !important;
    background: #ffffff !important;
  }
#timer.hide-time {
        padding-top: 10px;
    padding-bottom: 3px;
    height: 50px;
    text-align: center;
    font-size: 1.6em;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    color: #ffffff !important;
    background: #ffffff !important;
}

  .btn-primary
  {
    color: #ff0068 !important;
    background-color: #ffffff !important;
    border-color: #ffffff !important;
  }

  .btn-success
  {
    color: #ff0068 !important;
    background-color: #ffffff !important;
    border-color: #ffffff !important;
  }

  a
  {
    color: #000000 !important;
    text-decoration: none;
  }

  .highscore-box li a
  {
    background: #fff !important;
  }

  .highscore-box #table-data
  {
    background: #fff !important;
  }

  a.list-group-item.active,
a.list-group-item.active:hover,
a.list-group-item.active:focus
  {
    z-index: 2;
    color: #fff !important;
    background-color: #ffb7c5 !important;
    border-color: #d65770 !important;
  }

  .panel-primary > .panel-heading
  {
    color: #fff !important;
    background-color: #ffb7c5 !important;
    border-color: #d65770 !important;
  }

  #homepage #sidebar #language
  {
    margin-top: 40px;
    margin-bottom: 40px;
    padding: 5px;
    color: #fff;
    background: #ffb7c5;
    border-top: 1px solid #ff005e;
    border-bottom: 1px solid #ff005e;
    font-family: 'Raleway', sans-serif;
  }

  #homepage #sidebar #login-register, #homepage #sidebar #language
  {
    margin-top: 40px;
    margin-bottom: 40px;
    padding: 5px;
    color: #fff;
    background: #ffb7c5;
    border-top: 1px solid #ff005e;
    border-bottom: 1px solid #ff005e;
    font-family: 'Raleway', sans-serif;
  }

#words .wrong {
    color: #ff005e !important;
}
div.panel.panel-primary {
    border-color: #ff005e !important;
}
div#auswertung-result.col-md-6 h3 {
    background: #ff005e !important;
}
div#auswertung-result.col-md-6 {
    border: none !important;
    box-shadow: none !important;
}
div#auswertung-result {
    background: #fde5ea !important;
}
table#result-table.table.table-striped {
    background: #fde5ea !important;
}
table#result-table.table.table-striped tr{
    border-color: #fde5ea !important;
}
td#wpm.name {
    background: #fde5ea !important;
}
td#wpm.name strong {
    color: #ff005e !important;
}
td#wpm.name tr {
    border-color: #fde5ea !important;
}
tr {
    border-color: #fde5ea !important;
}
td.name, td.value {
    background-color: #fde5ea !important;
}
#box-title {
    background: #ffb7c5 !important;
}
div#user-online-box {
    color: black !important;
}
}
