css:
@-moz-document domain("stackstorage.com") {
/* General */
body {
    color: #eee;
    background: #1f1f1f;
}

a:focus, a:hover {
    color: #eee;
}

/* Navbars */
.navbar--primary {
    background-color: #000;
}

.navbar--secondary {
    background: #111;
    border-color: #111;
}

.navbar__item {
    border-color: #000;
}

.navbar__link {
    color: #eee;
}

.navbar__link svg * {
    fill: #eee;
}

/* Panels */
.panel {
    background: #333;
}

/* Alerts */
.alert-danger {
    color: #eee;
    background: #cc2222;
    border-color: #cc2222;
}

.alert__wrapper--warning {
    background: #c4710b;
    border-color: #c4710b;
}

/* Forms controls */
.form__input:not(.controls__search-input), .form__select, .node__input, .form-control {
    color: #eee;
    background: #000;
    border-color: #000;
}

.form__input:focus, .form__select:focus, .node__input:focus, .form-control:focus {
    color: #eee;
    background: #000;
    border-color: #178bff;
}

.node__input--error {
    box-shadow: none;
    color: #e9322d;
    border-color: #e9322d;
}

.node__input--error:focus {
    border-color: #e9322d;
}

/* Progressbar */
.progress {
    background: #000;
}

/* Switchbuttons */
.switchery {
    background: #111 !important;
    border-color: #444 !important;
}

/* Media board */
.mediaboard__container {
    background: #111;
}

/* Breadcrumb */
.breadcrumb__link:hover {
    color: #eee;
    opacity: 1;
}

/* Content */
.content {
    background: #111;
}

.node__container--spinner:before {
    background: #000;
}

.node--header {
    background: #000;
}

.node {
    border-color: #555;
}

.node--selected {
    background: #000;
}

.node:not(.node--header):hover {
    background: #222;
}

.controls__search-input.active, .controls__search-input:active, .controls__search-input:focus {
    color: #eee;
    background-color: #000;
}

/* Sidebar */
.sidebar__container {
    background: #111;
}

.sidebar__header {
    background: #000;
}

.sidebar__content {
    border-color: #555;
}

.sidebar__item:not(.sidebar__item--disabled):hover {
    background: #000;
}

/* Infobar */
.infobar {
    background: #000;
}

/* Settings */
.setting {
    border-color: #555;
}

/* Software */
.software__box--selected {
    background: #000;
    border-color: #000;
}

/* Footer */
.footer {
    background: #111;
    border-color: #555;
}

.footer__help-link {
    color: #eee;
}
}
