@-moz-document domain("google.com")
{
/* General */

/** Fonts **/
  body
  {
  /* The default font face is Roboto */
    font-family: 'Roboto', Arial, sans-serif;
  }

  a:hover
  {
  /* Remove underline for hovered links */
    text-decoration: none !important;
  }

  .g a
  {
  /* Default style for hyperlinks */
    color: #0D47A1 !important;
    transition: 0.2s;
  }

  .g a:hover
  {
  /* Use lighter color for hovered links */
    color: #1976D2 !important;
  }

/** Background **/
  body, #viewport, #gsr, #appbar
  {
    background: #FAFAFA;
  }

/* Home page */
  .hp #viewport
  {
  /* Home page background */
    background: url('https://i.imgur.com/uJhVzRK.jpg') no-repeat;
    /*background: #F1F1F1;*/
  }

  #hptl, .gb_qe.gb_R
  {
    display: none;
  }

  #hplogo
  {
  /* Shift the logo upwards */
    margin-top: -50px !important;
  }

  #hplogo a
  {
 /* Hide doodle elements */
    display: none;
  }

  #prm-pt
  {
  /* Shift the prompt upwards */
    margin-top: -30px !important;
  }

  .hp #tsf
  {
  /* Shift the searching area upwards to leave more space for the suggestion list */
    margin-top: -50px !important;
    margin-left: auto !important;
  }

  .hp #sfdiv.sbfcn
  {
    box-shadow: 0 2px 8px 0 rgba(0, 0, 0, 0.25);
  }

  .hp #sfdiv:before
  {
  /* Gradient effects for search bar */
    content: "";
    position: absolute;
    top: -3px;
    left: -3px;
    height: calc(100% + 6px);
    width: calc(100% + 6px);
    border-radius: 26px;
    background: linear-gradient(to right, #00AFFF, #00E676);
    opacity: 0;
    z-index: -1;
    transition: 0.2s;
  }

  .hp #sfdiv.sbfcn:before
  {
  /* Trigger effects when focused */
    opacity: 0.85;
  }

  .hp .gsfi
  {
  /* Larger font size for search term */
    font-size: 22px;
    color: rgba(0, 0, 0, 0.7);
  }

  .hp .sbib_b
  {
  /* Align input box */
    padding: 4px 18px;
  }

  .hp #fbar
  {
  /* Larger effective area */
    padding-top: 150px;
  }

  .hp #fbar
  {
  /* Hide footbar by default */
    opacity: 0;
    transform: translate(0, -6px);
    transition: 0.3s;
  }

  .hp #fbar:hover, .hp #fbar:focus-within
  {
 /* Footbar enters when hovered or focused */
    opacity: 1;
    transform: translate(0, 0);
  }

/* Results page */
  #tsf, #hdtb, #appbar
  {
  /* Shift the contents for less empty space */
    margin-left: -50px !important;
  }

  #center_col
  {
  /* Align the contents with header */
    margin-left: 84px !important;
  }

/* Logo */
  #logocont
  {
  /* Align the logo with search bar */
    padding: 0 10px 0 40px !important;
  }

  #logo img, #logocont img
  {
  /* Use the "G" logo varient */
    width: 50px;
    height: 50px;
    padding-top: 50px !important;
    border-radius: 50%;
    margin: 1px 5px 0 35px;
    background: url('http://i.imgur.com/YzVN66R.png') no-repeat;
    background-size: 50px;
    box-sizing: border-box;
  }

/* Header */
  #hdtb, .sfbgg, .sfbgx, #hdtbSum
  {
  /* Header background */
    background: #EEE !important;
  }

  #hdtb
  {
  /* Use shadow to divide header and contents */
    border: none !important;
    box-shadow: 0 1px 8px rgba(0, 0, 0, 0.2);
  }

  .hdtb-mitem, #hdtb-tls
  {
  /* Search options and tools */
    font-size: 15px;
    border: 0 !important;
    transition: color 0.2s;
  }

  .hdtb-mitem.hdtb-imb
  {
  /* Align search options */
    position: relative;
    vertical-align: top;
    padding-top: 25px !important;
    margin: 0 2px;
  }

  .hdtb-mitem.hdtb-msel
  {
  /* Style for selected item */
    padding-bottom: 18px !important;
    overflow: hidden;
  }

  .hdtb-mitem.hdtb-msel:before
  {
  /* Underline selected item */
    content: '';
    display: block;
    position: absolute;
    top: calc(100% - 4px);
    left: 0;
    width: calc(100% - 6px);
    border: 3px solid #4285f4;
    border-radius: 4px 4px 0 0;
  }

  g-header-menu.hdtb-mitem
  {
  /* Align search tools */
    margin-top: 15px;
  }

/** User portal **/
  #gbwa > div:first-child > div, #gbwa ~ div > div:first-child > div
  {
    display: none;
  }

  #gbwa > div:nth-child(2), #gbsfw, #gbwa + div + div > div:nth-child(2)
  {
    top: 40px;
    right: -10px;
    border: none;
    border-radius: 8px;
    box-shadow: 0 4px 24px rgba(0, 0, 0, 0.2);
  }

  #gbsfw
  {
    overflow: hidden;
  }

  #gbwa > div:nth-child(2) > ul
  {
    background: none;
  }

/** Search bar **/
  #sfdiv
  {
  /* Rounded search bar */
    position: relative;
    height: 46px;
    padding-top: 3px;
    border-radius: 23px;
    background: #FFF;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.15);
    box-sizing: border-box;
  }

  #sfdiv.sbhcn
  {
  /* Darker shadow when hovered */
    box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.15);
  }

  #sfdiv.sbfcn
  {
  /* Darker shadow with outline for visual aid when focused */
    box-shadow: 0 2px 8px 0 rgba(0, 0, 0, 0.25), inset 0 0 0 2px rgba(0, 145, 234, 0.6);
  }

  .sbib_a, .sbib_b, #sfdiv input
  {
  /* Remove unnecessary background */
    background: none;
  }

  .sbib_b
  {
  /* Align input box */
    padding: 3px 18px;
  }

  #gs_st0
  {
  /* Align voice search icon */
    transform: translate(3px, -2px);
  }

  .sbico-c
  {
  /* Aligh search icon */
    transform: translate(0, -1px);
  }

  .gsfi
  {
  /* Use Product Sans for search terms */
    font-family: 'Product Sans', Roboto, Arial, sans-serif;
    font-size: 20px;
    color: rgba(0, 0, 0, 0.78);
  }

/** Search buttons **/
  .jhp input[type="submit"], .gbqfba
  {
    height: 34px;
    font-family: 'Roboto';
    font-size: 15px !important;
    font-weight: normal !important;
    color: rgba(0, 0, 0, 0.6) !important;
    padding: 0 14px;
    border: 2px solid transparent !important;
    border-radius: 5px;
    margin: 16px 22px;
    background: #CCC;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
    transition: background 0.15s, box-shadow 0.2s, transform 0.1s;
    cursor: pointer;
  }

  .jhp input[type="submit"]:hover, .gbqfba
  {
    background: #BFBFBF !important;
    box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.15) !important;
    transform: translate(0, -2px);
  }

  .jhp input[type="submit"]:focus, .gbqfba:focus
  {
    outline: none;
    background: #BFBFBF;
    border-color: rgba(0, 0, 0, 0.15) !important;
    transform: translate(0, -1px);
  }

/** Suggestion List **/
  .sbdd_a
  {
  /* Seperate from search bar */
    top: 55px !important;
  }

  .sbdd_b
  {
  /* Prevent list from growing out of screen */
    max-height: 330px;
    padding: 6px 0;
    border-radius: 5px;
    box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.15);
    overflow-y: auto;
  }

  .sbsb_c
  {
  /* Suggested terms */
    font-size: 18px;
    padding: 5px 18px !important;
  }

  .sbsb_g, .sbfl_a
  {
  /* Remove unnecessary bottom elements */
    display: none;
  }

/* Content */
  .g
  {
  /* Normal search result card */
    position: relative;
    width: 100%;
    border-radius: 5px;
    padding: 12px 16px;
    margin-bottom: 12px !important;
    background: #FFFFFF;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,0.15);
  }

  .g .exp-outline
  {
    display: none;
  }

  .g .r
  {
 /* Result title */
    margin-bottom: 5px;
  }

  .g .r a
  {
 /* Result title link */
    color: #025D92 !important;
    font-family: 'Google Sans', Roboto, Arial, sans-serif;
  }

  .g cite
  {
 /* Result URL */
    color: #00796B;
  }

  .g .action-menu .mn-dwn-arw
  {
    margin-top: -3px;
    border-color: #00796B transparent;
  }

  .g .st
  {
    display: block;
    color: #6A6A6A;
    margin-top: 3px;
  }

  .g g-section-with-header
  {
    margin: 0;
  }

  .g g-scrolling-carousel
  {
    margin: -4px -16px -12px -16px;
  }

  .g g-scrolling-carousel > div
  {
    padding: 5px;
  }

  g-section-with-header
  {
    width: 100%;
    margin-left: 16px;
  }

  #center_col g-card
  {
    margin: 0;
  }

  .g-blk, .mod
  {
    padding: 0;
    margin-bottom: 25px !important;
    background: none;
    box-shadow: none;
  }

  .g-blk .mod
  {
    margin-bottom: 0 !important;
  }

  .kp-blk, .vk_c, .vk_cxp
  {
 /* Knowledge panel and special cards */
    border-radius: 5px;
    background: #FFFFFF;
    overflow: hidden;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,0.15);
  }

  .kp-blk, .vk_c, .vk_cxp
  {
    margin-left: 0 !important;
    margin-right: -32px !important;
  }

  .QunXQ
  {
    background: #ECEFF1;
  }

  .chip
  {
    padding: 6px 14px !important;
    border-radius: 20px !important;
    margin: 2px 8px 0 0 !important;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.2) !important;
  }

  .chip span
  {
    line-height: 20px !important;
  }

/* Footer */
  #fbar
  {
  /* Fit footer into general background */
    border: 0 !important;
    background: none !important;
  }

  .fbar
  {
  /* Lift footer away from the edge */
    position: relative;
    bottom: 5px;
  }
}
