css:
@-moz-document url-prefix("https://flow-e.com/workflow") {
/* hide inbox zero image and text */
.empty-feed-adviser-image, .empty-feed-adviser-text { display: none; }

/* hide card author and icons on workflow, always display actions on two lines */
div.card:not(.is-feed) div.card-author { display: none; }
div.card:not(.is-feed) div.card-left-part { display: none; }
div.card:not(.is-feed) div.card-right-part { margin-left: .5em; }
div.card-details { min-height: 60px; }
div.card:not(.is-feed) div.card-actions-list { width: 90px !important; }

/* lighter color for card body */ 
div.card:not(.is-feed) div.card-body { color: #ccc !important; }

/* always display incomplete tasks */
div[data-power-up-id="2"] { display: block !important; }
div.card:not(.is-fixed) li.is-completed { display: none !important; }
span.card-to-do-counter { display: none !important; }
div.card-power-ups-summary { border: none !important; }

/* move due date under card date */
div.card:not(.is-fixed) div.card-due-date { position: absolute; top: 32px; right: 5px; z-index: 0; background-color:white; }
div.card:not(.is-fixed) div.card-actions { z-index: 1; }

/* move other power-ups toggles up, make them smaller and hide them until hover */
div.card-power-ups-counters { display: none !important; position: absolute; top: 32px; background: white; zoom: 1;}
div.card:hover div.card-power-ups-counters { display: flex !important; flex-direction: row; }

/* color cards left border green if generated by mail, blue if generated locally
div.card.is-received:not(.is-fixed), div.card.is-sent:not(.is-fixed) { border-left: 5px solid #4caf50 !important; }
div.card.is-personal:not(.is-fixed) { border-left: 5px solid #2196f3 !important; }
div.card-details { border: 0 !important; } */

/* taller horizontal scrollbar */
div.workflows::-webkit-scrollbar { height: 12px; }
}
