css:
/* i really want this to be global */
html {
    background-color: #333 !important;
    opacity: 1.0;
}
/* i really want this to be global */
body,
#not-footer {
    background-color: #333;
}
/* i really want this to be global */
body {
    margin: 0;
    font-family: Cabin, "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 14px;
    line-height: 20px;
    color: #ffffff;
    background-color: #333;
}
/* i really want this to be global */
p {
    color: #eee;
    font-size: 16px;
    line-height: 1.3;
    font-family: 'Roboto', sans-serif;
}
/* i really want this to be global */
#kb-entry-header h1.kbentry-title {
    font-size: 28px;
    color: #eee;
    font-weight: 100;
    margin-bottom: 12px;
    font-family: 'Roboto Condensed', sans-serif;
    line-height: 1;
    max-width: 94%;
}

/* i really want this to be global */
#main-navbar.navbar .navbar-inner {
    min-height: 0;
    background: #333;
    border: 0 none;
    padding: 12px 0;
    -webkit-box-shadow: 0 1px 4px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0 1px 4px rgba(0, 0, 0, 0.4);
    box-shadow: 0 1px 4px rgba(0, 0, 0, 0.4);
}
/* i really want this to be global */
#main-navbar.navbar .navbar-inner .brand-sub-heading a {
    color: #eee;
}
/* i really want this to be global */
.sidebarTwo .widget .widget-header h3,
.sidebarOne .widget .widget-header h3 {
    color: #eee;
    font-weight: normal;
    font-size: 14px;
    font-weight: 600;
}
/* i really want this to be global */
.post-container .vote-widget .score.positive {
    color: #69b328;
}
/* i really want this to be global */
pre {
    display: block;
    padding: 9.5px;
    margin: 0 0 10px;
    font-size: 13px;
    line-height: 20px;
    word-break: break-all;
    word-wrap: break-word;
    white-space: pre;
    white-space: pre-wrap;
    background-color: #ccc;
    border: 1px solid #ccc;
    border: 1px solid rgba(0, 0, 0, 0.15);
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    -webkit-box-sizing: 0;
    -moz-box-sizing: 0;
    -o-box-sizing: 0;
    -ms-box-sizing: 0;
    box-sizing: 0;
}
/* i really want this to be global */
li.L1,
li.L3,
li.L5,
li.L7,
li.L9 {
    background: #ccc;
}
/* i really want this to be global */
.post-container .question h1 {
    font-size: 29px;
    color: #ccc;
    font-weight: 100;
}
/* i really want this to be global */
.post-container .post {
    min-height: 58px;
    background: #333;
    color: #eee;
}
/* i really want this to be global */
#middle .main-inner .container .mainContent .widget .widget-header + .widget-content {
    border: solid 1px #ccc;
    padding: 4px 3% 30px;
    background: #333;
    color: #eee;
}
/* i really want this to be global */
.widget-header {
    background: #333;
    color: #eee;
}
/* i really want this to be global */
.widget-header h3 {
    background: #333;
    color: #eee;
}
/* i really want this to be global */
widget .widget-content {
    padding: 15px 0 10px 0;
    clear: left;
}
/* i really want this to be global */
.widget.widget-nopad .widget-content {
    padding: 0;
}
