css:
@-moz-document domain("edmodo.com")
{
#topbar {
    background: #2B2B2B;
    padding: 0;
    box-shadow: 0 2px 4px rgba(0,0,0,.15);
    border-bottom: 1px solid beige;
    position: fixed;
    width: 100%;
    top: 0;
    z-index: 999996;
}
body {

    background-image: url(https://i0.wp.com/1.bp.blogspot.com/-QPNpmfiX8wI/UQweaLQs16I/AAAAAAAAAbc/T-NW2QNAcpI/s1600/Black+Wallpaper+pic+1024X768.jpg?resize=1600%2C1200)

  
}
.pb-content {
    
    background: #2B2B2B;
}

.avatar-container .profile-card {
    
    background: #2B2B2B;
}
.avatar-container .profile-card .avatar-section .user-info .text-15 {
    font-size: 20px;
    font-weight: bold;
    line-height: 22px;
    color: beige;
}

.avatar-container p.subtext {
    text-transform: capitalize;
    line-height: 15px;
    margin-top: 2px;
    font-size: 15px;
    font-weight: bold;
    color: beige;
}
.section {

    border-color: beige beige beige;
 
}


.section .section-header-bg-white {
    padding: 13px 10px 13px 15px;
    line-height: 22px;
    color: beige;
    background-color: #2B2B2B;
    font-size: 18px;
    font-weight: 300;
    border-radius: 2px 2px 0 0;
    overflow: visible;
    border-bottom: 1px solid #dfe4e8;
}

.avatar-container .profile-card .school-link-container a {
    display: block;
    position: relative;
    font-weight: bold;
    font-size: 15px;
    padding: 10px 45px;
    word-wrap: break-word;
    color: #546986;
    max-height: 33px;
    overflow: hidden;
}

.avatar-container .profile-card .district-link-container a, .avatar-container .profile-card .school-link-container a {
    display: block;
    position: relative;
    font-weight: bold;
    font-size: 15px;
    padding: 10px 45px;
    word-wrap: break-word;
    color: #546986;
    max-height: 33px;
    overflow: hidden;
}

}
