@-moz-document domain("hattrick.org") {

body{background: url(http://img12.imageshack.us/img12/7880/45982312.png)!important;}


/*      Top menu       */

#header{background:url(http://img410.imageshack.us/img410/1638/headertak.png) top left no-repeat !important;}
#logo{background:none !important;}

#menu{background: url(http://img106.imageshack.us/img106/653/menu.png) no-repeat !important;}
#menu a:hover{background: #e60000 !important;}
#menu a{color:#fff !important;}


/*       Links & headers   */

a {color: #cc0000 !important;}
h2 {color: #000 !important;}
h2 a{color: #fff !important;}
h3 {color: #000 !important;}


/*       Forum       */

.highlight{color: #00a398 !important;}
.folderName a{color: #000 !important;}
.forumTabs a{color: #000 !important;}
.cfUserInfo a{color: #000 !important;}
.cfHeader a {color: #ffffff !important;}


/*      Box backgrounds & text    */

.sidebarBox .boxHead h2,.subMenuBox .boxHead h2,.main .boxHead h2{color:#fff !important;}

.sidebarBox .boxHead, .subMenuBox .boxHead, .main .boxHead{background: url(http://img299.imageshack.us/img299/2456/topright.png) no-repeat top right!important;}

.subMenuBox .boxHead{	background: url(http://img299.imageshack.us/img299/2456/topright.png) no-repeat top right!important;}

.sidebarBox .boxHead h2, .subMenuBox .boxHead h2, .main .boxHead h2{	background: url(http://img301.imageshack.us/img301/7946/topleft.png) top left no-repeat!important;}

.subMenuBox .boxHead h2{background: url(http://img301.imageshack.us/img301/7946/topleft.png) no-repeat top left!important;}

}
