css:
@-moz-document domain("userstyles.org") {
*{
    color: #a7a7a7 !important;
    box-shadow: none !important;
    text-shadow: none !important;
    border-color: transparent !important;
    text-decoration: none !important;
}
/*HELLSTES DESIGN*/
#header, #send_feedback_button:hover, #overlay-info, .style_card, #notes_textarea, #Head, textarea, input , pre, #search_input, .errorExplanation, .advancedsettings_shown, .personal-page .light-blue-button, .navigation, .header-search .type_search, #style_installed_button, #footer-mobile, .PopList .Item, .FlyoutMenu, .InfoMessage, .AlertMessage
{
    background-color: #253447 !important;
    background: none
}
/*DUNKLES DESIGN*/
body, .white_button, #update_style_button, #footer, .css_button, #send_feedback_button, .see_more, .edit-setting, #install_style_button, .customize_button, select, .us-stylecard--long, .us-stylecard--short, #top_android_button
{
    background-color: #141d26 !important;
    background: none
}
/*HELLES DESIGN*/
#nav-view, #navigation, #nav-view-responsive, .navigation .links-box .navigation-link:hover, #top_android_button:hover, .Item.Read
{
    background-color: #1b2836 !important;
    background: none
}
/* BLUE TEXT */
a:link, h4 a:link, a:visited, h4 a:visited
{
    color: #1ea1f2 !important
}
/*GREY TEXT*/
#infomation_value_left
{
    color: #a7a7a7 !important
}
/* WHITE TEXT */
.style_card
{
    color: white !important
}
/* CORNERS */
#preview_image_div, #top_android_button
{
    border-radius: 10px
}
/* BUTTONS */
.Button
{
    border: 1px solid #1da1f2 !important;
    background: #141d26 !important;
    color: #1da1f2 !important;
    border-radius: 100px;
    padding: 4px 12px;
    
    -webkit-transition: background 200ms linear,border 200ms linear ;
    -ms-transition: background 200ms linear,border 200ms linear;
    transition: background 200ms linear,border 200ms linear ;
}
.Button:hover
{
    background: #253447 !important;
    border: 1px solid black !important;
    color: #1da1f2 !important;
    border-radius: 100px;
    padding: 4px 12px;
}
}

@-moz-document domain("userstyles.org") {
.overlay_background, .android_button_banner{
    display: none !important
}
td, th{
    border-color: grey !important
}
body > table > tbody > tr > td:nth-child(3),body > table > tbody > tr > td:nth-child(4),body > table > tbody > tr > td
{
    text-align: center
}
.navigation_link{
    background-color:  #253447;
    margin-top: 10px;
    padding-top: 7px;
    padding-bottom: 7px;
    border-radius: 100px;
}
.navigation_link:hover{
    background-color: #141d26
}
#navigation{
    text-align: center !important;
}
#install_style_button:hover{
    background: #253447 !important;
    -webkit-transition: background-color 200ms linear;
    -ms-transition: background-color 200ms linear;
    transition: background-color 200ms linear;
}
.author-styles *
{
    border: none;    
}
.author-styles td:nth-child(odd){
    border-left: 1px solid black !important;
    background: #253447;
    -webkit-transition: background 100ms linear;
    -ms-transition: background 100ms linear;
    transition: background 100ms linear;
}
.author-styles td:nth-child(even){
    border-left: 1px solid black !important;
    background: #1b2836;
    -webkit-transition: background 100ms linear;
    -ms-transition: background 100ms linear;
    transition: background 100ms linear;
}
.author-styles
{
    background: #252147 !important;
    border-right: 1px solid black !important;
    
}
.author-styles td:nth-child(odd):hover{
    background: #141d26;
}
.author-styles td:nth-child(even):hover{
    background: #141d26;
}
td{
    width: 1px;
    height: 15px !important;
    white-space: nowrap;    
    padding: 10px 5px 10px 5px !important;
}
body > table > tbody > tr:nth-child(n) > td > a:nth-child(2):hover{
    color: #f00 !important;
    -webkit-transition: color 500ms linear;
    -ms-transition: color 500ms linear;
    transition: color 500ms linear;
}
#update_style_button{
    -webkit-transition: background 200ms linear, color 200ms linear;
    -ms-transition: background 200ms linear, color 200ms linear;
    transition: background 200ms linear, color 200ms linear;
    color: black !important
}
#update_style_button:hover
{
    background-color: #2ecc71 !important;
}
#update_style_button:hover *{
    color: black !important;
}
#update_style_button > div:nth-child(1) > img
{
    position: relative; 
    padding: 25px 40px;     
    margin: -25px -40px;
    z-index: 2;    
}
#update_style_button > div:nth-child(2){
    padding-left: 15px
