css:
@-moz-document domain("wsgf.org") {
*{
    color: #a7a7a7;
    box-shadow: none !important;
    text-shadow: none !important;
    border-color: transparent !important
}


/*HELLSTES DESIGN*/
fieldset.collapsible:not(.collapsed), fieldset:not(.collapsible), #navigation, .block h3.title, .cse .gsc-control-cse, .gsc-control-cse, .game-grade div, .field-field-dr-thread .field-item, div.autoexpandall, #navigation ul ul li a:hover, #navigation ul ul li a:visited:hover, #page-header > div.navbar > div, #phpbbforum-page .navbar, #p163741 > div > table > tbody > tr.postheader > td, #phpbbforum-page tr.postheader, #phpbbforum-page dl.codebox, #phpbbforum-page .pagination span a, #phpbbforum-page .pagination span a:link, #phpbbforum-page .pagination span a:visited, #phpbbforum-page .pagination span a:active, .node-dr .meta h2, .node-teaser .meta, .with-photo .meta, #edit-field-news-tags-value, th, thead th, select, .gsc-tabHeader.gsc-tabhInactive, #gsc-i-id1, .gsc-input-box
{
    background-color: #253447 !important;
    background: none
}
/*DUNKLES DESIGN*/
body, #navigation .sfHover a, #navigation .sfHover a:hover, #navigation a:hover, #phpbbforum-page dl.codebox code, #content .node-teaser, .pager-inner, #navigation a:hover, #navigation a:visited:hover, #navigation .active-trail a.active:hover, #navigation .active-trail a:hover, #edit-submit-news-list, tr.even td, #page-body > div > div > ul.topiclist.forums > li > dl, #edit-submit-mgl, .gsc-modal-background-image, .gsc-tabHeader.gsc-tabhActive, #homepage > table *
{
    background-color: #141d26 !important;
    background: none
}
/*HELLES DESIGN*/
div.main-inner, .block .content, #phpbbforum-page div, #phpbbforum-page blockquote, #phpbbforum-page select, tr.odd td, .gsc-results-wrapper-overlay
{
    background-color: #1b2836 !important;
    background: none
}
/* BLUE TEXT */
.showuserlogin, .hideuserlogin, a, a:hover, a:visited
{
    color: #1ea1f2 !important
}
/*GREY TEXT*/
#phpbbforum-page .content, .gsc-preview-reviews, .gsc-control-cse .gs-snippet, .gsc-control-cse .gs-promotion em, .gsc-control-cse .gs-snippet, .gsc-control-cse .gs-promotion em
{
    color: #a7a7a7 !important
}
/* WHITE TEXT */
#navigation a, #navigation a:visited, .block h3.title, #phpbbforum-page blockquote cite, #phpbbforum-page .postprofile strong, #phpbbforum-page select, #edit-submit-news-list, th, thead th, #edit-submit-mgl, .gsc-tabHeader.gsc-tabhActive
{
    color: white !important
}
/* CORNERS */
#navigation a:hover, #navigation a, #navigation a:visited
{
    border-radius: 120px
}
}

@-moz-document domain("wsgf.org") {
#artcert > tbody > tr > td > div > div > img{
    border: 2px solid green !important;
    border-radius: 100px
}
#navigation ul ul{
    background: none;
    background-color: transparent !important
}
#phpbbforum-page blockquote{
    border: 2px dashed black !important
}
#phpbbforum-page .pagination span strong, #phpbbforum-page .pagination span a, #phpbbforum-page .pagination span a:link, #phpbbforum-page .pagination span a:visited, #phpbbforum-page .pagination span a:active{
    font-size: 25px;
}
#navigation ul ul li a:hover, #navigation ul ul li a:visited:hover{
    background: none !important;
}
#phpbbforum-page li.row{
    border-top: 1px solid black !important;
    border-bottom: 1px solid black !important
}
}
