css:
@-moz-document regexp("http(s)?://(www.)?php.net.*") {
#layout-content {
    background: rgba(0, 0, 0, 0.4) !important;
	color: #ccc !important;
}
blockquote.note {
    background-color: #0C0C0C99 !important;
    border: 1px solid #424242 !important;
}
.docs .classsynopsis, .refsect1 .fieldsynopsis, .refsect1 .dc-description, .docs .sect1 .dc-description, .refsect1 .example-contents > [class$="code"], .refsect1 .example-contents.screen,
#usernotes .note .text, .example-contents > [class$="code"], .example-contents.screen, .informalexample .literallayout{
    background: #141414 !important;
	border-color: #d9d9d9;
}
.phpcode span.html {
    color: #ccc !important;
}
.interfacename a, .fieldsynopsis .type, .methodsynopsis .type, .constructorsynopsis .type {
    color: #83e126 !important;
}
.methodname b, .methodname strong, .methodname a, .classsynopsis .classname, .parameter {
    color: #57a1ec !important;
}
a:hover, a:focus, #layout-content a:hover, #layout-content a:focus {
    color: #D858AB !important;
    border-color: #C958A1 !important;
}
#layout-content a:link, #layout-content a:visited {
    color: #7ca1c6 !important;
}
.title a, .title {
    color: #c54c9a !important;
}
.refsect1 blockquote.note {
    background: #182030 !important;
	color: #9daccc !important;
    border:1px solid #246 !important;
}
div.phpcode span.keyword {
    color: #90e33d !important;
}
div.phpcode span.default {
    color: #4589ce !important;
}
div.phpcode span.string {
    color: #b63d3d !important;
}
.phpcode span.comment {
    color: #808080 !important;
}
.headsup {
    border-bottom: 1px solid #041 !important;
    background-color: #15271580 !important;
}
div.caution {
    background: #8f8f1933 !important;
    border: 1px solid #596b00;
}
#usernotes .note .votes .tally {
    color: #308a1e;
}
a:hover, a:focus, #layout-content a:hover, #layout-content a:focus {
    color: #C958A1 !important;
}
h1, h2, h3, h4, h5, h6 {
    color: #b4b4b4 !important;
}
#flash-message .error, div.warning {
    background: #991B1B4D !important;
    border: 1px solid #750f0f !important;
}
.navbar {
    background: rgba(0, 0, 0, 0.4) !important;
}
.navbar .navbar-search .search-query {
    background: rgba(0, 0, 0, 0.37) none repeat scroll 0% 0% !important;
}
#layout-content:not(:only-child) {
    border-right: .25rem solid #0b070780 !important;
}
.tt-dropdown-menu {
    background: none repeat scroll 0 0 #0000004d !important;
    border-bottom: 1px solid #0000004D !important;
    box-shadow: 1px 0 1px -1px #0006 inset, -1px 0 1px -1px #C4C9DF inset, 0 0 1px #4F5B93 !important;
}
.tt-suggestion {
    background: rgba(0, 0, 0, 0.75) !important;
    border-bottom: 1px solid rgb(68, 68, 68) !important;
}
.tt-suggestions {
    color: #7babff  !important;
}
.tt-dropdown-menu .result-heading {
    border-bottom: 2px solid #43485F !important;
    text-shadow: 0 -1px 0 rgba(0,0,0,.25) !important;
    background-color: rgb(18, 19, 27) !important;
}
.tt-dropdown-menu .search {
    background: rgb(18, 19, 27);
}
.doctable thead tr, .segmentedlist thead tr {
    border-color: #323A60 !important;
    border-bottom-color: rgb(50, 58, 96) !important;
    border-bottom-color: #03050C !important;
    color: #b5c2d8 !important;
}
.doctable th, .segmentedlist th {
    background-color: #1735B733 !important;
}
.doctable tbody tr:nth-child(2n+1), .segmentedlist tbody tr:nth-child(2n+1) {
    background-color: #2b38961a !important;
}
.doctable tbody tr:nth-child(2n), .segmentedlist tbody tr:nth-child(2n) {
    background-color: #3E51A24D !important;
}
.doctable tr, .segmentedlist tr {
    border-color: #03010199 !important;
}
.para var, .simpara var {
    background-color: #1208084D !important;
    color: #c3c3c3 !important;
}
table.standard tr:nth-child(2n) td, table.standard tr:nth-child(2n) td.subr, table.standard tr:nth-child(2n) th.subr, table.standard tr td.subr, table.standard tr th.subr, table.standard tr:nth-child(2n) td.sub, table.standard tr:nth-child(2n) th.sub, table.standard tr td.sub, table.standard tr th.sub {
    background-color: #0006 !important;
}
.classsynopsisinfo .modifier, .fieldsynopsis .modifier, .methodsynopsis .modifier, .constructorsynopsis .modifier {
    color: #d94a92 !important;
}
#mirrors-container .download-file {
    background-color: #03020280 !important;
}
#mirrors-container .mirrors-list .mirror {
    border: 1px solid #141010 !important;
}
#mirrors-container .mirrors-list .mirror:hover {
    background-color: #10961033 !important;
    border: 1px solid #004e09 !important;

}


nav.navbar.navbar-fixed-top {
    background-color: #141414 !important;
}

.navbar .navbar-search .search-query {
    text-shadow: none;
}

a.brand img {
    visibility: hidden;
}

a.brand {
    background: url(https://cdn.iconscout.com/public/images/icon/free/png-128/php-logo-brand-development-tools-312a141d0b26c2fc-128x128.png) no-repeat;
    background-size: 70% 120%;
    background-position: center;
}

div.images {
    display: none;
}


/** operators */

[style="color: #007700"] {
    color: #90e33d !important;
}

/** reserved words **/

[style="color: #0000BB"] {
    color: #4589ce !important;
}

/** strings **/

[style="color: #DD0000"] {
    color: #b63d3d !important;
}

/** commments**/

[style="color: #FF8000"] {
    color: #808080 !important;
}

.navbar .navbar-search .search-query {
    color: #fff;
}
}
