.container > .row > a:not([class^="pull"]) {
	position: relative !important;
	float: left !important;
	left: 4px !important;
	top: 2px !important;
	margin: 0px 0px 0px 15px !important;
	padding: 3px 0px 0px 0px !important;
	width: 150px !important;
	color: #3ebcf8 !important;
	z-index: 9 !important;
}

