                Full card elements
   -------------------------------------------------------- */
.container > .row > table {
	position: relative !important;
	float: left !important;
	margin: 0px 0px 10px -146px !important;
	width: 150px !important;
	z-index: 0 !important;
	background: #0000005e !important;
}
.container > .row > table:nth-of-type(even),
.container > .row > table:nth-of-type(even) > tbody:nth-child(1) > tr:nth-child(1) > th:nth-child(1) > div:nth-child(1) > img:nth-child(1) {
	box-shadow: 0px 0px 6px 0px #0a75ff !important;
}
.container > .row > table:nth-of-type(odd),
.container > .row > table:nth-of-type(odd) > tbody:nth-child(1) > tr:nth-child(1) > th:nth-child(1) > div:nth-child(1) > img:nth-child(1) {
	box-shadow: 0px 0px 6px 0px #f00;
}
.container > .row > table > tbody {
	display: block !important;
	position: relative !important;
	height: 325px !important;
	width: 150px !important;
}
table[id^="song-"] > tbody:nth-child(1) > tr:nth-child(1),
table[id^="song-"] > tbody:nth-child(1) > tr:nth-child(1) > th,
table[id^="song-"] > tbody:nth-child(1) > tr:nth-child(1) > th > div {
