@-moz-document url-prefix("https://beatsaver.com"), url-prefix("https://www.beatsaver.com") {
/* --------------- BASE SECTION -------------------------------
   -------- Main content - includes navbar -------------------- */

@media (min-width: 1px), (max-width: 9999px) {
	body {
		background-color: #000 !important;
		color: #fff !important;
		font-size: 12px !important;
		line-height: 12px !important;
	}
	/* Adding background image - comment out the background-image element below for solid color */
	body::after {
		content: '';
		z-index: -1 !important;
		top: 0px !important;
		left: 0px !important;
		width: 100%;
		height: 100% !important;
		position: absolute !important;
		background-image: url("https://media.playstation.com/is/image/SCEA/beat-saber-normal-hero-background-01-ps4-us-09jun18?$native_nt$") !important;
		background-repeat: no-repeat;
		background-size: 100% 150% !important;
		-webkit-filter: blur(5px) brightness(30%);
		-moz-filter: blur(5px) brightness(30%);
		-o-filter: blur(5px) brightness(40%);
		-ms-filter: blur(5px) brightness(40%);
		filter: blur(5px) brightness(20%);
	}
	body > nav {
		display: block !important;
		position: relative !important;
	}
	/* Large elements */
	.container {
		display: block !important;
		position: relative !important;
		padding: 0px 0px 0px 0px !important;
		margin: 0px 0px 0px 0px !important;
		width: 100% !important;
		min-width: 100% !important;

		text-align: center !important;
		top: 0px !important;
	}
	.row {
		display: block !important;
		position: relative !important;
		margin: 0px 0px 5px 0px !important;
		padding: 0px 0px 0px 0px !important;
		width: 100% !important;
		top: 0px !important;
		left: 0px !important;
	}
	body > .container > .row::first-child {
/* 		position: absolute !important; */
		left: -50% !important;
		top: 10px !important;
	}

	.container > .row > .row {
		top: -10px !important;
