css:
@-moz-document url("https://www.edmodo.com/home#/"), url("https://www.edmodo.com/home") {

body {

	background-color:#060c18;

}

#topbar {

	background: #000;

}

.message-item, .template-message-item {

	background-color: #000;

}

.ns-stream-filter {

	background-color: #000;

	border-color: #000 #000408;

}

.ns-load-more {

	background-color: #000;

	border-color: #000 #000 #000;

}

.topbar {

	background: #060c18;

}

.green-btn, .green-btn:visited, a.green-btn, a.green-btn:visited {

	color: #fff;

	border-color: #00010c;

	background-color: #070f68;

}

.parent-container p.parent-code {

	background: #9fb4b6;

}

.ns-dropdown .ns-menu {

	background: #000;

}

.section {

	background-color: #fff;

	border-color: #000306 #000306 #000306;

}

.subtext, a.subtext, a .subtext {

	color: #fff;

}

.link-list-group-threaded > li > div > a {

	color: #fff;

}

.avatar-container .profile-card .district-link-container a, .avatar-container .profile-card .school-link-container a {

	color: #fff;

}

.avatar-container .profile-card .avatar-section .user-info .leftbar-profile-link {

	color: #fffdfd;

}

.section .section-header-bg-white {

	color: #fff;

	background-color: #060000;

}

.section {

	background-color: #000;

}

.link-list-group-options li a {

	color: #fff;

}

.pb-content {

	background: #000;

}

.placeholder-container, .request-resubmission-container {

	background: #fff;

}

.placeholder-container label {

	color: #000;

}

.blue-btn, .blue-btn:visited, .e-tour .introjs-nextbutton, .google-tour .introjs-nextbutton, a.blue-btn, a.blue-btn:visited {

	color: #fff;

}

.section .sub-section-header, .section .sub-section-header-big {

	color: #fff;

}

a {

	color: #fff;

	text-decoration: none;

}

.message-item .footer, .template-message-item .footer {

	background: #000205;

	border-top: 1px solid #000;

}

.message-item .footer .link, .message-item .footer .link a, .template-message-item .footer .link, .template-message-item .footer .link a {

	color: #fff;

}

.ns-post-reply {

	border-top: 1px solid #000;

}

.body, button, div, fieldset, form, h1, h2, h3, h4, h5, h6, html, input, label, legend, p, select, textarea {

	color: #fff;

}

.or-selector p {

	background-color: #090000;

}
}
