css:
@-moz-document url-prefix("https://app.spare5.com/"), url-prefix("https://app.spare5.com/v1") {
/*
Spare5 Navy Blue
Author: Manuel Uribe
Facebook URL: fb.com/mnluribe
Description: Navy blue theme for Spare5, follows design guidelines by Spare5 new interface.
Donation: https://paypal.me/mnluribef
Version: v1.2.1
*/
:root {
    --navy: #152439;
    --lnavy: #324156;
    --lnavyb: #4e5d74;
}
body {
    color: #dadada !important;
}
.container-fluid {
    padding-left: 0;
    padding-right: 0;
}
body,
.fivesweb-top-bar,
.five-profile,
.five-hover-profile,
.container-fluid,
.tasks-grid-container,
.job_box,
div.image-box,
.collapse.in,
.reward-body,
.support-dropdown-container,
.swell-modal,
.task-discussion-pane,
.payments-charities-container,
.ibox-content,
.fivesweb-referrals-container,
.fivesweb-skills-container,
.profile-dropdown-container,
.community-at-replies-dropdown-container,
.ibox .open > .dropdown-menu,
.community-modal-contents,
.instructions-panel .instructions-panel-content .instructions-panel-updated-date {
    background-color: var(--navy) !important;
}
footer,
.task-title,
.table-striped > tbody > tr:nth-of-type(odd),
.reward-box,
.clickable-box,
.fa-3x,
.login-buttons a,
.form-control,
.single-line,
.panel-body,
.problem-textarea,
.alert-warning,
.swell-overlay,
.community-facing-name,
.add-message-container .text-area,
.discussions-view .text-input,
.discussions-view .text-area,
.dropdown-menu > li > a:hover,
.dropdown-menu > li > a:focus,
.alert-info,
.btn-white,
#emailLogin {
    background-color: var(--lnavy) !important;
}
.input-group-addon,
.btn-white:hover,
.btn-white:focus {
    background-color: var(--lnavyb) !important;
}
.community-at-replies-dropdown-container,
.task-discussion-pane,
.swell-modal {
    border: 1px solid var(--lnavy) !important;
}
.form-control,
.single-line,
.input-group-addon,
.task-item,
.profile-logout-container .profile-logout-button,
.profile-dropdown-container,
.support-dropdown-container,
.discussions-view .text-input,
.discussions-view .text-area,
.problem-textarea,
.swell-modal {
    border: 1px solid var(--lnavyb) !important;
}

.task-title,
.community-modal-contents .modal-secondary-button,
.problem-header,
.icon-close-modal,
.problem-textarea,
.reward-header,
.shortcut-help-container .shortcut-help-row .shortcut-help-description,
.fivesweb-action-btn.secondary-btn,
.shortcut-help-container .shortcut-help-row .shortcut-help-key-container .shortcut-help-key,
.community-instructions-container .instructions-page-indicator-container .instructions-page-indicator,
.clickable-box p,
.reward-box h3, h4,
.shortcut-help-container .shortcut-help-row .shortcut-help-key-container .shortcut-help-key,
.add-message-container .text-area,
.discussions-view .text-input,
.discussions-view .text-area,
.reward-card .reward-chart-subtitle.min-pay-subtitle,
.alert-info,
.swell-modal,
.swell-modal-content,
div.status-message{
    color: #d0d0d0 !important;
}
.swell-theme.green-theme a,
.task-details-container .xp-description,
.small-five-profile .five-profile-name,
#emailLogin,
.alert-warning,
.task-state-header,
.five-profile .five-profile-generic-lockup,
.five-profile .five-profile-forum-posts,
.five-profile .five-profile-total-xp,
.five-profile .five-profile-pic-lockup,
.five-profile .skill-title,
div.swell-modal-title,
.forum-breadcrumb,
.forum-category .title,
.forum-discussion .title,
.input-group-addon,
.form-control,
.form-control input,
.form-control select,
.form-control textarea,
.login-buttons a{
    color: #e6e6e6 !important;
}
.profile-dropdown-container .profile-dropdown-row .profile-dropdown-details h3,
.support-dropdown-container li p,
.community-modal-contents,
.five-profile .skill-container .skill-level {
    color: var(--white) !important;
}
.ibox-content,
.alert-info,
.alert-warning,
.fivesweb-top-bar,
.btn-primary,
.btn-default{
    border-color: var(--lnavy) !important;
}

.form-control input,
.form-control select,
.form-control textarea {
    border: 1px solid #5f5f5f !important;
}
.btn-white {
    border-color: #e4e4e4 !important;
}
fieldset {
    color: #b3b3b3 !important;
}
.refer-title-subtext,
.forum-category .description,
.forum-discussion .first-message {
    color: #989898 !important;
}
.task-description, div.timestamp {
    color: #a5a7ab !important;
}
.task-title {
    border-bottom: 1px solid var(--lnavyb) !important;
}
.task-item {
    cursor: pointer !important;
    background-color: #2d3a4d !important;
}
.modal-header,
.fivesweb-action-btn.secondary-btn,
.community-modal-contents .modal-secondary-button {
    background-color: #4c4d4e !important;
}
.user-referrals .referral-row {
    border-bottom: 1px solid #303233 !important;
}
.contact .content,
.modal .search-actions,
.modal.main,
.modal.main > .search-form,
.modal.searchbox {
    background-color: #2d2d2d !important;
}
.add-message-container .text-area {
    border: 1px solid #403b3b !important;
}
.task-badge.master-task,
.task-master-task {
    background-color: #2395dd;
}
.task-badge.bonus-task {
    background-color: #1c6480 !important;
}
.profile-snippit-container .profile-snippit .profile-snippit-data {
    color: #1c84c6 !important;
}
.profile-logout-container .profile-logout-button {
    color: #fb1528 !important;
    background-color: var(--lnavy) !important;
}
.profile-snippit-container .profile-snippit .profile-snippit-data.profile-snippit-purse, .community-facing-name {
    color: #3ac459 !important;
}
.five-profile .skills-rule .skills-rule-text {
    color: #10bcff !important;
}
.ibox-title {
    background-color: #26374f !important;
    border-color: var(--lnavyb) !important;
}
.fivesweb-top-bar .fivesweb-top-bar-navbar-brand,
.btn-default,
.btn-primary{
    background: #08a42b !important;
}
hr,
.table > thead > tr > th {
    border-top: 1px solid #505050 !important;
}
.forum-category {
    border-bottom: 1px solid var(--lnavyb) !important;
}
.forum-discussion .poster,
.forum-discussion .count,
.forum-discussion .last-update,
.forum-discussion .pinned {
    color: #abb4b7 !important;
}
.forum-discussion .not-hot{
    color: #4CAF50 !important;
}
.forum-message {
    color: #adadad !important;
}
.forum-message .stroke {
    background-color: #636363 !important;
}
.community-page-indicator.active-page {
    color: var(--lnavyb) !important;
}
.swell-button:hover {
    background-color: #22e84f !important;
}
.community-instructions-container .instructions-page-indicator-container .instructions-page-indicator.active-page {
    background: #09da11 !important;
}
.donemodal {
    background: linear-gradient(to top, #131111, #4c4a4a) !important;
}
[type=radio].rateme:checked + label:before {
    background-color: #e6e6e6 !important;
    color: #08a42b !important;
}
.text-danger {
    color: #f72215 !important;
}
.text-success {
    color: #15a4ff !important;
}
}
