css:
@-moz-document url-prefix("https://heiligeexcept.io/BSTags/") {
html {
	background: #000;
	color: #fff;
}
body {
	position: absolute !important;
	display: block !important;
	background: #000;
	color: #fff;
}
.width {
	width: 100%;
	height: auto !important;
	margin: 0 0 0 0 !important;
}
header {
	position: relative !important;
	display: inline-block !important;
	background: #1f1f1fad;
	color: #fff;
	height: auto !important;
	min-height: 125px !important;
}
#headerLeft {
	position: absolute !important;
	display: block !important;
	height: auto !important;
	min-height: 125px !important;
	font-size: 11px !important;
	width: 240px;
}
#headerLeft > br {
	line-height: 5px;
}
#headerLeft > h1 {
	font-size: 18px !important;
	line-height: 14px;
}
#headerLeft * {
	font-size: 14px !important;
}
fieldset [class^="tag"] {
	padding: 2px 5px 2px 5px !important;
	width: auto !important;
	line-height: 11px !important;
}
#headerRight {
	display: block;
	width: calc(100% - 240px);
	margin: 0 0 0 auto !important;
	border: none !important;
	height: auto !important;
	min-height: 110px !important;
}
#searchTags {
	position: relative !important;
	display: block !important;
	height: 15px !important;
	font-size: 14px !important;
}
#headerTags {
	position: relative !important;
	display: block !important;
}
#headerTags * {
	line-height: 11px !important;
	font-size: 11px !important;
}
main {
	display: inline-block !important;
	position: relative !important;
	margin-top: auto !important;
}
main .song {
	height: 22px !important;
	border: 1px solid #47474770;
}
main .smallSong {
	height: 12px !important;
}
#searchResults {
	background: #242424e0;
}
#searchResults > a {
	width: 100%;
	display: inline-block;
	background: #0000;
	color: #fff;
	height: 22px !important;
}
#searchResults > a:nth-child(2n) {
	background: #00000029;
}

#searchResults > a .smalltext {
	overflow: visible;
	font-size: 12px !important;
	text-wrap: normal !important;
	vertical-align: middle !important;
}
main .song .text {
	vertical-align: middle !important;
}
.text {
	white-space: normal !important;
	margin: 0 !important;
	padding: 3px 5px 0 4px !important;
    width: calc(100% - 25px);
	font-size: 12px !important;
}
main > #searchResults > a > .song > .text {
    width: calc(100% - 25px);
	font-size: 12px !important;
	padding: 0px 5px 0 10px !important;
	line-height: 12px !important;
	vertical-align: middle !important;
}
span.smalltext { display: none !important}
.song > .text > h2 { 
	color: #09f;
	font-size: 12px !important;
	line-height: 20px !important;
	vertical-align: middle !important;
	max-height: 20px
}
main > #searchResults > a > .song > .text > h3 { 
	color: #b3b3b3;
	font-size: 12px !important;
	line-height: 20px !important;
	vertical-align: middle !important;
	margin-right: 20px !important;
	margin-left: auto !important;
	max-height: 20px;
	float:right;
}
main > #searchResults > .song.smallsong,.smallSong {
	background-color: #02a7ff59 !important;
	height: 12px !important;
}
main .smallSong .text {
    width: 100%;
    margin: 0px !important;
	vertical-align: middle !important;
	padding: 0 !important;
	line-height: 10px !important;
}
main .smallSong .text *{
    margin: 0px 15px !important;
	vertical-align: middle !important;
}
.song > img {
	height: 20px !important;
	width: auto !important;
	padding-left: 10px;
}
}
