css:
@-moz-document domain("slovnik.seznam.cz") {
body, #page, .hp #container .move {
    background: none;
    background-color: #000;
}
.hp #container .logo, .hp #container .logo.small, .logo_seznam.cz, #add-to-dictionary-btn a, #page.search #results .hgroup p.audio img, #menu {
    filter: invert(1) hue-rotate(170deg);
}
#page.search #results .morfologyTable .closeButton {
    filter: invert(0.93);
}
#searchForm input#q, #filter input#q, #searchForm #superSelCont select, #filter #superSelCont select, #searchForm #superSelCont3 select, #filter #superSelCont3 select, #searchForm input#searchButton, #filter input#searchButton {
    background: #222;
    color: #fff;
}
.morfLinks, #page.search #results .morfologyTable #morfSource table th.tableHeader, #page.search #results .morfologyTable .tabContainer table th.tableHeader, #morfSource {
    color: #fff;
}
h1, #page.search #results h3, #page.search #results ol li span, #page.search #results ul li span, #fulltext, #page.search #topSearch.searchForm #direct-switch .active a, #head #tabs .active a, #page.search #results .morfologyTable h2, #page.search #results .morfologyTable .tabContainer table td, #page.search #results .morfologyTable .morfTabs li.tab-selected a {
    color: #fff !important;
}
a, #head a, #foot a, #head #tabs a {
    color: #9BAED2;
}
#menu > ul > li > a {
    color: #2e487d;
}
#searchForm input#searchButton, #filter input#searchButton, #searchForm input.button, #filter input.button, #searchForm input.submit, #filter input.submit {
    text-shadow: none
}
#searchForm .suggest .content, #filter .suggest .content {
    background: #191919;
    color: #fff;
}
#searchForm input#q, #filter input#q, #searchForm .superSelect, #filter .superSelect, #searchForm input#searchButton, #filter input#searchButton, #searchForm input.button, #filter input.button, #searchForm input.submit, #filter input.submit, #searchForm .suggest .content, #filter .suggest .content {
    border: 1px solid rgba(0, 0, 0, 0.125);
}
#page.search #foot {
    border-top: 1px solid #1E1E1E;
}
#page.search #results .morfologyTable .morfologyTableHeader {
    background: none;
    background-color: #212121;
    border-bottom: 1px solid rgba(0, 0, 0, 0.125);
}
#page.search #results .morfologyTable {
    border: 1px solid rgba(0, 0, 0, 0.125);
    box-shadow: none;
}
#page.search #results .morfologyTable.showMorf {
    background: #272727;
}

#page.search #results #fastTrans span.w {
    color: #eee;
}

#searchForm #superSelCont select:hover, #filter #superSelCont select:hover, #searchForm #superSelCont3 select:hover, #filter #superSelCont3 select:hover {
    background: #222;
}
}
