css:
@-moz-document domain("s.to")
{
/*
S.TO Night TV Mode Modifications v2.0
coded by c0d3d.net
*/
.homeContentPromotionBox>.homeSliderView .col-md-3,body,html {
    background: #030508!important
}

.hosterSiteDirectNav {
    margin-bottom: 15px!important
}

.hosterSiteDirectNav ul li span strong {
    color: #000!important
}

.hosterSiteVideo ul li>div {
    background: 0 0!important;
    border-color: #585858!important
}

.hosterSiteVideo {
    padding: 5px 0!important;
    background: 0 0!important
}

.hosterSiteVideoButton {
    background: #243743!important
}

.hosterSiteTitle h2 {
    font-size: 30px!important;
    margin-top: 1px!important
}

.changeLanguageBox,.hosterSectionTitle {
    background: 0 0!important;
    color: #696969!important
}

.changeLanguageBox strong,.hosterSiteVideo h4,.shoutboxMessage strong {
    color: #dadada!important
}

.changeLanguageBox {
    box-shadow: none!important;
    margin-bottom: 8px!important;
    padding-bottom: 5px!important;
    padding-top: 5px!important;
    border: 0!important
}

.selectedLanguage {
    border: 0!important
}

.logo-wrapper,.main-header {
    background: #243743 0 0!important
}

.main-header {
    background-image: none!important
}

.inSiteWebStream {
    padding: 10px 0;
    background: 0 0!important
}

#footer,#news,#supportFAQ,.ContentContainerBox,.allSeriesBox,.col-lg-4,.col-md-8,.descriptionSpoilerLink,.descriptionSpoilerPlaceholder,.globalNewsMessage,.homepageInformationText,.hosterSectionTitle,.pageTitle,.paragraph-end.black,.shoutbox,.socialMediaBox,.socialshareboxseries,.ssto-share-buttons,.submitNewDescription,.topUserMonth {
    display: none!important
}
.homepageSupportFAQList.seriesNewsList, #seriesNewsFeed>.col-lg-12:first-child, #wrapper>.container:nth-child(3)>.row:nth-child(13)>.col-md-4:last-child {
    display: none!important
}
.marginBottom {
    margin-bottom: 0!important;
    padding-bottom: 0!important
}

.logo-wrapper {
    padding-top: 5px!important;
    padding-right: 5px!important;
    padding-left: 5px!important;
    height: 60px!important
}

.primary-navigation {
    padding-top: 5px!important;
    padding-bottom: 5px!important;
    padding-left: 15px!important
}

.logged-out,.main-header,.primary-navigation {
    height: 65px!important
}

.primary-navigation .signup-toggle {
    color: #fff!important;
    background: 0 0!important;
    border: 2px solid #0f1620!important;
    background-color: #0f1620!important;
    padding: 12px 25px!important;
    border-radius: 4px!important;
    margin-left: 25px!important
}

.primary-navigation .signup-toggle:hover {
    color: #eee!important;
    border: 2px solid #888!important;
    background: 0 0!important
}

.button.blue {
    background: #0f1620!important
}

.button.blue:hover {
    background: #888!important
}

.container {
    width: 90vw!important
}

.col-md-6-5 {
    width: 75%!important
}

.episodeGermanTitel {
    font-size: 20px!important
}

.episodeEnglishTitel {
    font-size: 15px!important
}

.breadCrumbMenu {
    margin: 5px 0!important
}

.markAsLastSeen {
    margin: 15px 20px 0!important
}

.coverListItem {
    margin-right: 2%!important;
    margin-left: 3%!important
}

.mediaBodyMessageBoxContent p {
    background: #0c1115!important;
    color: #fff!important;
    border-left: 2px solid #090e10!important
}

.mediaBodyMessageBoxContent,.messagesContainer ul li {
    background: #0c1115!important
}

.FAQTableItem time,.breadCrumbMenu li a,.container a,.genre ul li a,.homepageInformationText>.row h3,.mediaBodyMessage.right .mediaBodyMessageBoxContent p,.mediaBodyMessageNew textarea,.messageDetails time,.newsArticleReview a.headline h2,.newsArticleReview a.headline h4,.newsArticleReviewTeaser,.newsArticleReviewTime time,.pageTitle h1,.seriesListContainer>div>a>h3,.seriesListNavigation,.subMenuMainNavigation,.subMenuMainNavigationDropDownNavigation,.supportTicketMessages,p,strong {
    color: #fff!important
}

.mediaBodyMessageBox {
    box-shadow: 0 1px 3px #000
}

.container h1,.container h2,.container h3,.container h4,.container h6 {
    color: #f2f2f2!important
}

.paragraph-end {
    background-image: -webkit-gradient(linear,left top,right top,color-stop(0,rgba(254,254,254,0)),color-stop(0%,#00000000))!important;
    background-image: -webkit-linear-gradient(left,rgba(254,254,254,0),#00000000)!important;
    background: linear-gradient(to right,rgba(254,254,254,0),#121c2200)!important;
    bottom: 0!important;
    max-height: 100%!important;
    position: absolute!important;
    right: 0!important;
    height: 19px!important;
    width: 45px!important
}

.seriesListNavigation {
    background: #0c1217!important
}

.welcomePage img {
    box-shadow: 0 0 20px #040506!important
}

.welcomePage img:hover {
    box-shadow: 0 0 60px #040506!important
}

.searchContainerArea {
    background: #050708!important
}

#inputSearch {
    background: #243743!important
}

.shoutboxMessage p {
    color: #222!important
}

.homepageSupportFAQList,.searchResults li a,.settingsBox,.supportFAQArticle,.supportFAQArticleAnswer {
    background: #0c1115!important;
    box-shadow: 0 1px 2px #090c0f!important
}

.homepageSupportFAQListItem,.homepageSupportFAQListItem h5,.leaderboard small,.settingsBox small {
    color: #fff!important
}

.messagesContainer ul li {
    border-color: #243743!important
}

.messagesContainer {
    background: #0c1115!important;
    border: 1px dotted #243743!important;
    box-shadow: 0 1px 5px #000!important
}

.searchResults li a {
    box-shadow: 0 1px 2px #080b0e!important
}

.searchResults li a span {
    color: #44adf3!important
}

.searchResults li a:hover,.supportFAQAuthor {
    box-shadow: 0 1px 8px #44adf3!important
}

.table tr:nth-child(2n) {
    color: #fff!important;
    background: #0c1115!important
}

.faqList li,.leaderboard h5,.statisticUserOnlineList li a,.support-more>h3,.support-q-item>h5,.supportFAQArticleAnswerHeader,.table tr td,form fieldset label,form fieldset legend,pre,ul.listItem {
    color: #fff!important
}

.supportFAQAnswerForm,.table tr {
    color: #fff!important;
    background: #0f1621!important
}

.friendList>ul>li .friendshipRequestsItem,.mediaBodyMessageNew textarea,.subMenuMainNavigation,.subMenuMainNavigationDropDownNavigation,.supportTicketMessages,.tabs>div {
    background: #0c1115!important
}

.subMenuMainNavigation,.subMenuMainNavigationDropDownNavigation {
    box-shadow: 0 1px 2px #111!important
}

.friendList>ul>li:hover .friendshipRequestsItem {
    box-shadow: 0 1px 12px #44adf3!important
}

.supportTicketMessages .message {
    background: #0c1115!important;
    box-shadow: 0 1px 2px #0e151a!important
}

.authorDetails,.newsArticleReview,.randomButtons>ul li a,.randomFormContent {
    background: #252e35!important
}

.FAQTableItem {
    box-shadow: 0 0 7px #44adf3!important
}

.logList>div>div,.serienstreamTeamBox a>div {
    background: #0c1115!important;
    box-shadow: 0 1px 2px #070a0c!important
}

.FAQTableItem,.newsArticleReview:hover,.reviewList>li a:hover {
    background: #0c1115!important
}

.messagesContainer ul li:hover {
    background: #111!important
}

#morris_chart_1 {
    background: #a0a0a0!important
}

.seriesListHorizontalContent p {
    background: #44adf3!important
}

.leaderboard,.randomForm span,.randomFormContent,.randomFormContent small,.wishListSeriesTitle {
    color: #fff!important
}

.randomButtons>ul li.ui-tabs-active.ui-state-active a {
    border-color: #252e35!important;
    background: #0c1115!important
}

.randomButtons>ul li a {
    border-color: #0c1115!important
}

.add-series .add-series-button {
    background: #111c23
}
.changeLanguageBox strong {
    display: none;
}
.changeLanguageBox {
    zoom: 0.8;
}
div {
    zoom: 0.99!important;
}
}
