@-moz-document domain(calendar.google.com) {
	body[data-viewfamily="EVENT"] {
		box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
		height: calc(100vh - 25vh);
		margin: 25vh 10vw 0 10vw;
	}
	.tEhMVd {
		overflow: hidden;
		border-top-left-radius: 5px;
		border-top-right-radius: 5px;
		background: white;
	}
	#oauth2relay363847550 {
		opacity: 0;
	}
	#gb {
		border-bottom: 1px solid #e4e4e4;
		margin-bottom: -1px;
	}
	[aria-label="Side panel"] {
		right: 0;
	}
	.K2fuAf:before {
		content: '';
		position: fixed;
		display: block;
		height: 100vh;
		width: 100vw;
		top: 0;
		left: 0;
		z-index: -999;
		background-size: cover !important;
		background-position-x: center !important;
		background-position-y: 85% !important;
		pointer-events: none;
	}
	.K2fuAf[data-view-heading*="January"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/january.jpg');
	}
	.K2fuAf[data-view-heading*="February"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/february.jpg');
	}
	.K2fuAf[data-view-heading*="March"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/march.jpg');
	}
	.K2fuAf[data-view-heading*="April"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/april.jpg');
	}
	.K2fuAf[data-view-heading*="May"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/may.jpg');
	}
	.K2fuAf[data-view-heading*="June"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/june.jpg');
	}
	.K2fuAf[data-view-heading*="July"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/july.jpg');
	}
	.K2fuAf[data-view-heading*="August"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/august.jpg');
	}
	.K2fuAf[data-view-heading*="September"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/september.jpg');
	}
	.K2fuAf[data-view-heading*="October"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/october.jpg');
	}
	.K2fuAf[data-view-heading*="November"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/november.jpg');
	}
	.K2fuAf[data-view-heading*="December"]:before {
		background: url('https://github.com/patrickonparker/UserstyleStorage/raw/master/december.jpg');
	}
}
