css:
@-moz-document domain(riven.market) {
nav {
	background-color: white;
	height: 8vh;
	border-bottom: 1px solid #eee;
}

nav.scrolled { /* Box-shadow adjustment */
	-webkit-box-shadow: 0px 5px 20px 0px rgba(0, 0, 0, 0.1);
	-moz-box-shadow: 0px 5px 20px 0px rgba(0, 0, 0, 0.1);
	box-shadow: 0px 5px 20px 0px rgba(0, 0, 0, 0.1);
	background-color: white;
}

nav ul:nth-child(2) {
	margin-left: 11vw;
}

nav ul:nth-child(3) li:nth-child(2), nav ul:nth-child(3) li:nth-child(2) a {
	margin-right: 0;
}

nav ul li {
	margin-right: 15px;
}

nav ul li a, nav ul li a:after, nav ul li a:before, #session a div:after, #session a div:before { /* Removed ugly transition and borders from links when hovered */
	font-size: 20px;
	transition: none;
	top: 0;
	bottom: 0;
	box-shadow: none;
}

#slogan, #paypal, #patreon, body div:nth-child(3) div div iframe {
	display: none;
}

#logo { /* Took too much space -> Made smaller and moved inside the header */
	margin-top: 1.5vh;
	height: 5vh;
	width: 7vw;
}

#header { /* Adjusted heigth to fit logo */
	padding: 0;
	height: 12.5vh;
}

#wrapper { /* Correction needed as a result of removing the footer */
	padding-bottom: 2.5vh;
}

body, #background, #content {
	background: white;
	filter: none;
	box-shadow: none;
	border: 1px solid #eee;
	border-radius: 5px;
}

body {
	border: none;
}

div#logo.scrolled { /* Remove transformation of the logo when scrolled */
	transform: none;
	margin-top: 1.5vh;
}

.attribute.image { /* Borders on each riven row, dirty fix because of 'display: table-row' */
	border-top-left-radius: 3px;
	border-bottom-left-radius: 3px;
}

.attribute.action, .attribute.online {
	border-top-right-radius: 3px;
	border-bottom-right-radius: 3px;
}

select {
	border-radius: 2px;
}


/* Button restyling */
button {
	background: rgba(var(--color-accent-light1), 0.25); /* Ignore if error */
	border: none;
	border-radius: 3px;
}

button:hover {
	background: rgba(var(--color-accent-light1), 0.5); /* Ignore if error */
	border: none;
	color: inherit;
}

.grecaptcha-badge:hover {
	display: none;
}
}
