css:
@-moz-document domain(twitter.com) {

/* Twitter simple night mode v1.1.0 */

/* Left side containers */
.DashboardProfileCard,
.module .flex-module,
.Trends {
    border: none;
}

/* Items in left side containers */
.wtf-module .UserSmallListItem,
.wtf-module .import-prompt .cta {
    border: none;
}

/* Main container */
.top-timeline-tweetbox .timeline-tweet-box,
.stream-item:not(.no-header-background-module) {
    border: none;
}

/* Main search bar */
.global-nav .search-input {
    border: none;
}

/* New tweet header */
#global-tweet-dialog .modal-header, #Tweetstorm-dialog .modal-header {
    border: none;
}

/* New tweet textbox */
.RichEditor {
    border: none;
}
.RichEditor.is-fakeFocus {
    box-shadow: none;
}

/* Mentions and embedded content */
.wtf-module .import-prompt .service,
.PlayableMedia-container,
.TwitterCard .SummaryCard--small .SummaryCard-image {
    border: none;
}
.TwitterCard-container {
    border: none;
}
.TwitterCard[data-theme=night-mode] .SummaryCard-content {
    background-color: #ffffff08;
}

/* "Load new tweets" bar */
.new-tweets-bar,
.new-tweets-bar:hover {
    border: none;
}

/* Notifications page */
.content-header, .content-no-header,
.NotificationsHeadingContent,
.ActivityItem {
    border: none;
}

/* Inbox */
.DMActivity-header,
.DMInboxItem {
    border: none;
}

/* Message textbox */
.DMConversation-composer {
    border: none;
}
.DMComposer-container {
    border: none;
    background: none;
}

/* Tweet containers */
.tweet {
    border: none;
}
.QuoteTweet {
    border: none;
    background-color: #ffffff08;
}

/* Bottom of main container */
.stream-end-item, .stream-end, .stream-loading, .stream-placeholder,
.timeline-end .stream-loading {
    border: none;
}

/* Settings dropdown divider */
.dropdown-divider {
    border-color: transparent;
}

/* Settings list item */
.module .list-link,
.module li:first-child .list-link {
    border: none;
}

/* Settings main container */
.content-inner,
.content-header .header-inner,
.content-inner hr,
.content-inner input,
.content-inner .t1-select,
.content-inner.no-stream-end,
#notifications-optout-all-top,
.notifications-prompt-container,
.find-friends-list li,
.MutedKeywordsFooter,
.SessionsSettings--row {
    border: none;
}

/* Lists */
.ProfileHeading-content,
.ProfileListsTimeline .GridTimeline-end:not(.has-items),
.ListCreationModule {
    border: none;
}

}
