css:
@-moz-document url-prefix("https://www.hulu.com/") {
#content {
    background-color: #272727 !important;
}

.GlobalNav,
.BrowseMenu__menu,
.Nav__dropdown,
.GlobalNav__logout,
.Footer,
.SearchBarContainer,
.SearchBar > input[type=text],
.FullTextSearch__Entry {
 	background-color: #000 !important;
}

.EditorialCollection__slider-overflow-hider {
 	background-image: linear-gradient(to right,#531c1cf7 97%,#4f1b1bf7 100%);   
}

.Loader__bar {
    background-color: #fff0 !important;
}

.GlobalNav__browse-menu .Nav__link .fill {
 	fill: #fff;   
}

.GlobalNav__my-stuff svg g path:nth-child(1) {
    color: #fff;
    stroke-width: 2;
}

.GlobalNav__my-stuff svg g path:nth-child(2) {
    color: #fff;
    stroke-width: 1;
}

.Nav__logo a svg path{
    fill: #7fb845;
}    
    
.Nav__label,
.Footer__logo,
.Tile__title,
.Tile__title--clickable,
.RecentSearch__Item,
.RecentSearch__Clear,
.SearchBar__ClearButton,
.ListItem__Content__HighlightText,
.ListItem__Content__HighlightText--Normal,
.Item__Title--Normal,
.FullTextSearch__Entry mark {
    color: #7fb845 !important;
}
    
.HoverPopout {
    background-color: #7fb845 !important;
}

.MyStuffAction__button {
    box-shadow: inset 0 0 0 2px #444;
}

.MyStuffAction__line-1,
.MyStuffAction__line-2 {
    background-color: #444;
}    
    
.StopSuggesting__btn {
    box-shadow: inset 0 0 0 2px #fff;
}

.StopSuggesting__line-1,
.StopSuggesting__btn--light .StopSuggesting__line-1,
.StopSuggesting__btn--light:hover .StopSuggesting__line-1 {
    background-color: #fff;
}

.BrowseMenu__genres {
 	border-left: 1px solid #fff !important; 
}

.Modal__backdrop {
    background: #000 !important;
}

.Modal__dialog {
    background: #272727 !important;
}

.Error,
.MyStuffBanner__title,
.MyStuffBanner__background,
.MyStuffBanner__description,
.BrowseMenu__items,
.BrowseMenu__item {
    color: #fff !important;
    background-color: #444 !important;
}

.ViewAllLink__right-arrow::after {
    content: "";
    display: inline-block;
    width: 9px;
    height: 9px;
    -webkit-transform: rotate(-45deg);
    -ms-transform: rotate(-45deg);
    transform: rotate(-45deg);
    border: 2px solid #7fb845;
    border-width: 0 2px 2px 0;
    margin: 0 5px;
}

.MyStuffBanner__description svg g path:nth-child(n) {
    stroke: #fff !important;
    stroke-width: 2.5;
}

.GenericTileContent__title,
.ViewAllLink__right-arrow {
    color: #7fb845 !important;
    background-color: #272727;
}

.LevelOne,
.PortraitCollection__title,
.SimpleCollection__title,
.OffsiteView .Item {
    color: #fff !important;
    background-color: #272727 !important;
}

.GenericTileContent__description,
.Tile__description,
.Tile__action,
.Tile__metadata,
.DetailsAbout__meta-segment {
    color: #fff !important;
}

.ProfileSelectorModal__line {
    border: 1px solid #fff;
}

.ProfileSelectorModal__title--who,
.ProfileSelectorModal__subtitle,
.Hub__title,
.GenericTileContent__action,
.GlobalNav__profile-first-item,
.GlobalNav__profile-first-item--profile-name,
.GlobalNav__add-account,
.GlobalNav__my-stuff,
.Nav__link,
.Subnav__item,
.Subnav__item.active,
.Select-value,
.Select-option,
.Select-option.is-focused,
.DetailsAbout__name,
.Tile__action,
.MyStuff__collection-title,
.SearchResultContainer__Title,
.UpsellActionTextView,
.OffsiteView h3,
.ListItem__Type,
.Item__Type {
 	color: #fff !important;

}

.SimpleCollection__description,
.Footer__site-link,
.Footer__legal-link {
    color: #bbb;
}

.Footer__site-link:hover,
.Footer__legal-link:hover {
    color: #fff;
}

.DetailsMasthead__container .Tile__title {
    color: #fffc !important;
}

.SearchBar > input[type=text],
.FullTextSearch__Entry,
.ListItem__HighlightText--Highlighted {
 	color: #fff !important;   
}

.BrowseMenu__item--active {
    color: #bbb !important;
	-webkit-text-stroke-color: #b5b5b5 !important;;
    border-bottom-color: #aaa !important;;   
}

.Nav__dropdown > .Nav__container:hover,
.GlobalNav__add-account:hover,
.GlobalNav__logout:hover,
.ListItem--Selected {
 	background-color: #3b3e4680 !important;   
}

.GlobalNav__profile-first-item:hover .GlobalNav__letter {
	background-color: #7fb845 !important;
}

.Subnav__accent-bar {
    background-color: #fff !important;
}

.Select-value,
.Select-multi-value-wrapper {
    color: #fff !important;
    background-color: #7fb845 !important;
}


.LevelTwo__container {
	color: #fff !important;
    background-color: #272727 !important;
}
    
.DetailsBody__about,
.DetailsDropdown__single,
.Select-menu,
.Select-option.is-focused {
	color: #fff !important;
    background-color: #7fb845 !important;
}

.SingleNetworkMasthead__arrow--left svg g path,
.SingleNetworkMasthead__arrow--right svg g path,
.Slider__button--previous svg g path,
.Slider__button--next svg g path {
    stroke: #fff;
}
    
.SingleNetworkMasthead__arrow--right circle,
.SingleNetworkMasthead__arrow--left circle,
.Slider__button--next circle,
.Slider__button--previous circle {
 	fill: #7fb845c0 !important;
}

.Slider__button--next,
.Slider__button--previous {
	background: #fff0 !important;
}

.Select-arrow {
    background: none;
    position: absolute;
    top: 24px;
    right: 32px;
    border: solid #fff;
    border-width: 0 2px 2px 0;
    display: inline-block;
    padding: 5px;
    -webkit-transform: rotate(45deg);
    -ms-transform: rotate(45deg);
    transform: rotate(45deg);
    -webkit-transition: -webkit-transform 0.3s ease;
    -webkit-transition: transform 0.3s ease;
    transition: transform 0.3s ease;
    -webkit-transform-origin: 65% 65%;
    -ms-transform-origin: 65% 65%;
    transform-origin: 65% 65%;
}

.DetailsCollection__items--full-width .CollapsibleDescription__trigger {
    top: 45px;
    background: none;
    border: solid #fff;
    border-width: 0 2px 2px 0;
    display: inline-block;
    padding: 5px;
    -webkit-transform: rotate(45deg);
    -ms-transform: rotate(45deg);
    transform: rotate(45deg);
    -webkit-transition: -webkit-transform 0.3s ease;
    -webkit-transition: transform 0.3s ease;
    transition: transform 0.3s ease;
    -webkit-transform-origin: 65% 65%;
    -ms-transform-origin: 65% 65%;
    transform-origin: 65% 65%;
}
    
.Select-value {
 	color: #fff !important;
    border-bottom: 1px solid #fff !important;   
}

.Hub__collections .SimpleCollection,
.ListItem {
 	border-bottom: 1px solid #c6c6c6 !important;   
}

.Nav.GlobalNav {
 	box-shadow: 0 1px 0 0 #8d95a999;
}

.Nav__dropdown {
 	border: 1px solid #8d95a999 !important;   
}

.GlobalNav__profile-first-item,
.GlobalNav__add-account,
.SingleNetwork__subnav {
    border-bottom: 1px solid #8d95a999 !important;
}
}

@-moz-document url-prefix("https://auth.hulu.com/") {
.auth-login .login-header {
    background-image: none !important;
}

.auth-login .login-dialog {
    background-color: #eee !important;
}

.login-header svg path {
    fill: #7fb845 !important;
}

.login-message,
.auth-login {
    color: #7fb845 !important;
    background-color: #272727;
}
}
