css:
@-moz-document domain('dou.ua') {
    body {
        background-color: #222;
    }

    .g-page {
        background-color: #333;
    }

    .b-typo {
        color: #eee;
    }

    .b-content-menu {
        background: transparent;
    }

    .page-articles,
    .b-content-menu li a,
    .b-adv-events .link,
    .b-footer a,
    .b-rss a,
    .b-articles .b-articles-switch li.current a,
    .announce,
    .page-index .b-block .title h3 a,
    h1,
    h2,
    h3,
    h4
    {
        color: #eee !important;
    }

    a {
        color: #007ACC;
    }

    /* Common - Page */

    .pageviews {
        filter: invert(100%);
    }

    .b-similar,
    .b-dou-vacancies {
        background: #222;
    }

    .b-dou-vacancies {
        border-color: #222;
    }

    .cell.b-right-side {
        background: #222;
        padding: 15px;
    }

    .g-right-shadowed,
    .g-left-shadowed {
        background: transparent;
    }

    .b-adv-events .link .img:after, .b-adv-events .link .img:before {
        background: transparent;
    }

    /* Common - Comment */
    .b-index-discussions .comment::after {
        display: none;
    }

    /* Common - Article author & comments */
    .b-index-columnisty .author a,
    .b-index-discussions .meta .userlink,
    .b-index-discussions .meta,
    .b-index-discussions .meta a,
    .b-adv-events .date,
    .b-footer {
        color: #ccc;
    }
  
  .b-typo blockquote {
      background: #222;
  }

    .b-index-discussions .comment {
        background: #222;
        border: none;
        color: #ccc;
    }

    .b-index-discussions .comment:hover {
        background: #444;
    }

    /* Home - Sponsors */
    .page-index .b-sponsors .items a {
        background: #fff;
        opacity: 0.3;
        transition: opacity 0.3s ease 0s;
    }

    .page-index .b-sponsors .items a:hover {
        opacity: 1;
    }

    .page-index .b-sponsors .items .row {
        border-bottom: 10px solid #333;
    }

    .page-index .b-sponsors .img {
        margin-bottom: 0;
        height: auto;
        position: static;
    }

    /* Home - Interesting */
    .b-most-interesting .b-switcher:after {
        display: none;
    }

    .b-most-interesting .b-switcher .bg-l,
    .b-most-interesting .b-switcher .bg-r {
        display: none;
    }

    .b-most-interesting .b-switcher li {
        background: #000;
        padding-bottom: 10px;
    }

    .b-most-interesting .b-switcher li .link img {
        margin-bottom: 10px;
    }

    .b-balls .black-ball {
        background: #ccc;
    }

    .b-balls .bg-b {
        background: transparent;
    }

    .b-balls ul li {
       background: #222;
        border-radius: 50%;
        z-index: -1;
        height: 11px;
        width: 11px;
        margin: 0 1px;
    }

    /* Article - Code */
    pre.hljs {
        filter: invert(100%);
    }

    /* Article - Images */
    .b-typo.b-typo_post img {
        opacity: 0.4;
        transition: opacity 0.3s ease 0s;
    }

    .b-typo.b-typo_post img:hover {
        opacity: 1;
    }

    /* Article - Also */
    .b-articles.b-articles__also {
        background: #222;
    }

    .b-articles .b-articles-switch li.current select {
        color: #ccc;
    }

    .b-post-info .date,
    .b-post-info .name,
    .b-post-info .author .name a {
        color: #ccc;
    }

    /* Article - Interesting */
    .b-sticky-right-side .l-sticky-block a.link {
        color: #ccc;
    }

    /* Article - Comments */
    .b-comments .comment .sup-users a {
        color: #ccc;
    }
    .g-approved-user .b-post-author .avatar,
    .comments-head .navigation-comment {
        color: #ccc;
    }

    .comments-head .comment-subscribe>span {
        color: #666;
    }

    .b-comments .comment .support {
        filter: invert(100%);
    }

    .b-typo code {
        background: #444;
    }

    .comments-head.__fixed .fixed-menu {
        background: #444;
    }

    /* Article - Footer */
    .b-footer-slider {
        background: none;
    }

    .b-footer-slider .item {
        color: #ccc;
    }

    /* Article - vacancies */
    .b-dou-vacancies .vacancies-list li, 
    .b-dou-vacancies .vacancies-list li .company,
    .b-dou-vacancies .vacancies-list li span {
        color: #ccc !important;
    }
}
