	0% { box-shadow: 0 0 0 0 var(--accent_main) !important; }
	70% { box-shadow: 0 0 5px 10px rgba(255, 255, 255, 0) !important; }
	100% { box-shadow: 0 0 0 0 rgba(255, 255, 255, 0) !important; }
}

@keyframes pulse-shadow--onboarding {
	0% { box-shadow: 0 0 0 0 var(--accent_main) !important; }
	30% { box-shadow: 0 0 0 0 var(--accent_main) !important; }
	80% { box-shadow: 0 0 5px 10px rgba(255, 255, 255, 0) !important; }
	100% { box-shadow: 0 0 0 0 rgba(255, 255, 255, 0) !important; }
}
}
