css:
@-moz-document domain("lwn.net") {
body {
    line-height: 1.4;
    color: #87928A;
    background-color: #171C19;
}
a:link {
    color: #478C90;
}
a:visited {
    color: #B16139;
}
a:hover,
a:active {
    color: #179AA0;
    background-color: transparent;
}
#menu {
    background-color: #232A25;
}
.navmenu ul {
    background-color: #232A25;
}
H2.SummaryHL A:link {
    color: #78877D;
}
h1 {
    color: #78877D;
}
DIV.topnav-container {
    margin-left: 35em;
    margin-right: 40em;
    background: #171C19;
    color: #87928A;
    padding: 1;
}
div.maincolumn {
    padding-left: 20em;
}
DIV.ArticleText {
    margin: 0 10em 0 10em;
}
DIV.FeatureByline {
    background-color: #232A25;
    border-style: solid;
    border-width: 2px;
    margin: 5px 10px 2px 0px;
}
DIV.GAByline {
    background-color: #232A25;
    border-style: solid;
    border-width: 2px;
}
DIV.BigQuote {
    color: #87928A;
    background-color: #232A25;
    border-style: solid;
    border-width: 0px;
    font-style: italic;
    padding: 5px;
    margin: 1em 8em 1em 5em;
}
SPAN {
    color: #87928A;
    background-color: #232A25;
}

P.Cat1HL,
P.Cat2HL,
P.Cat3HL {
    color: #78877D;
    background-color: transparent;
}
TR.Even {
    background-color: #232A25;
}
table.OddEven tr:nth-child(2n+1) {
    background-color: #232A25;
}
table.OddEven tr:nth-child(2n) {
    background-color: transparent;
}

P.CommentTitle {
    color: #78877D;
}
DIV.CommentBox P.CommentTitle {
    background-color: #232A25;
}
DIV.CommentBox {
    border-color: #232A25;
}
DIV.Headline {
    background-color: #232A25;
}
.Form {
    background-color: #232A25;
    border-style: solid;
    border-radius: 8px;
    border-color: #87928A;
    border-width: 1px;
}
FONT.QuotedText {
    color: #87928A;
    background-color: #232A25;
}
button, html input[type="button"], input[type="reset"], input[type="submit"] {
    color: #87928A;
    background-color: #232A25;
    border-radius: 8px;
    border-color: #87928A;
    border-style: solid;
    border-width: 1px;
}
input[type=text], input[type=password] {
    background-color: #232A25;
    border-radius: 8px;
    border-color: #87928A;
    border-style: solid;
    border-width: 1px;
}
}
